INSERT INTO Author_Name (id, name, des, language_id) VALUES (1, "Weöres Sándor", "<p>(Szombathely, 1913. június 22. – Budapest, 1989. január 22.) Kossuth- és Baumgarten-díjas magyar költő, író, műfordító, irodalomtudós.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (2, "Márai Sándor", "<p><strong>Grosschmid Sándor Károly Henrik </strong>(Kassa, 1900. április 11. – San Diego, Kalifornia, 1989. február 21.) magyar író, költő, újságíró.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (3, "Arisztotelész", "<p>(Sztageira, Kr. e. 384 – Khalkisz, Kr. e. 322. március 7.) görög tudós és filozófus, a modern európai tudomány atyja és előfutára. Mesterével, Platónnal együtt a nyugati kultúra legnagyobb hatású gondolkodói közé tartozik.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (4, "George Orwell", "<p><strong>Eric Arthur Blair</strong> (Motihari, Bihár, India, 1903. június 25. – London, 1950. január 21.) angol író, kritikus, újságíró.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (5, "Zig Ziglar", "<p><strong>Hilary Hinton Ziglar </strong>(November 6, 1926 – November 28, 2012) American author, salesman, and motivational speaker. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (6, "William Arthur Ward", "<p>(December 17, 1921 – March 30, 1994) American motivational writer.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (7, "Henry Kissinger", "<p><strong>Henry Alfred Kissinger</strong>, születési nevén <strong>Heinz Alfred Kissinger</strong> (Fürth, 1923. május 27. – ) Nobel-békedíjas németországi születésű amerikai diplomata, politikus, politológus, író és történész. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (8, "Bill Gates", "<p><strong>William Henry Gates</strong> (Seattle, 1955. október 28. –) amerikai üzletember, szoftverfejlesztő, feltaláló, filantróp.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (9, "Yuval Noah Harari", "<p>(Kiryat Ata, Izrael, 1976. február 24. – ) izraeli történész, író, a Jeruzsálemi Héber Egyetem történelem tanszékének professzora.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (10, "Bruce Lee", "<p><strong>Lee Jun-fan</strong> (San Francisco, Kalifornia, 1940. november 27. – Hongkong, 1973. július 20.) hongkongi származású kínai harcművész, színész, rendező, producer és forgatókönyvíró; a jeet kune do harcművészeti iskola létrehozója.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (11, "Jordan Peterson", "<p><strong>Jordan Bernt Peterson</strong>, <strong>Jordan B. Peterson</strong> (Fairview, 1962. június 12. –) kanadai politológus és klinikai pszichológus, a Torontói Egyetem pszichológia tanszékének professzora. Főként szociál, és személyiség pszichológiával foglalkozik, kiemelt tekintettel a vallások és ideológiák pszichológiájára, valamint a személyiség és a teljesítmény felmérésére és fejlesztésére. Nézeteit egzisztencialista filozófusok (Kierkegaard, Sartre, Camus) alaptételeire és Jung illetve Freud analitikus pszichológiájára alapozza, emellett felhasználja Joseph Campbell munkáit is.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (12, "Arnold Bennett", "<p><b>Enoch Arnold Bennett </b>(1867. május 27. - 1931. március 27.) angol író.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (13, "Deepak Chopra", "<p>(Újdelhi, Brit India, 1946. október 22.) indiai-amerikai szerző. A New Age mozgalom jelentős alakja, aki az alternatív gyógyászat egyik legismertebb és leggazdagabb alakjának számít. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (14, "Paulo Coelho", "<p><strong>Paulo Coelho de Souza </strong>(Rio de Janeiro, 1947. augusztus 24. – ) brazil író, az ENSZ békenagykövete.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (15, "John Lennon", "<p><strong>John Ono Lennon</strong>, születési nevén <strong>John Winston Lennon</strong> (Liverpool, 1940. október 9. – New York, 1980. december 8.) a 20. század egyik legnagyobb hatású angol énekese, zeneszerzője, költője és a The Beatles gitárosa. Az 1960-as évek folyamán Paul McCartney-val való együttműködése komoly hatást gyakorolt a beatzene és a rock fejlődésére, a komolyabb és politikai üzenetet hordozó irányba terelve azt.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (16, "Brian Weiss", "<p><strong>Brian Leslie Weiss </strong>(1944. november 6. - ) amerikai pszichiáter, hipnoterapeuta és szerző, aki a múltbéli regresszióra specializálódott. Írásai a reinkarnációval, a múltbéli regresszióval, a jövőbeli életek előrehaladásával és a lélek halál utáni túlélésével foglalkoznak.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (17, "Carl Gustav Jung", "<p>(Kesswil, Svájc, 1875. július 26. – Küsnacht, Svájc, 1961. június 6.) svájci pszichiáter, pszichológus, analitikus.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (18, "Clive Staples Lewis", "<p>(Belfast, Észak-Írország, 1898. november 29. – Oxford, Anglia, 1963. november 22.) ír író, irodalomtörténész, gondolkodó, teológus, a modern kori gyermekfantasy megteremtője, közeli barátjához, J. R. R. Tolkienhoz hasonlóan a 20. századi világirodalom egyik jelentős képviselője. Leghíresebb műve a Narnia krónikái című fantasyszeptológia.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (19, "Gabriel García Márquez", "<p><strong>Gabriel José de la Concordia García Márquez </strong>(Aracataca, 1927. március 6. – Mexikóváros, 2014. április 17.) Nobel-díjas kolumbiai író, újságíró, kiadó és politikai aktivista. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (20, "Amit Ray", "<p>(1960. augusztus 12 - ) indiai író és 'spirituális mester'. A meditációról, a jógáról, a békéről és az együttérzésről szóló tanításairól ismert.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (21, "Pierre Teilhard de Chardin", "<p>(Orcines, Puy-de-Dôme, 1881. május 1. – New York, 1955. április 10.) a tudományos gondolkodás és a vallásos lelkiség egymásrautaltságát hirdető francia jezsuita teológus, filozófus és paleontológus. Panteista teológiát fejlesztett ki, a tudomány és a kereszténység elemeit társította.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (22, "Kalkuttai Szent Teréz", "<p><strong>Teréz anya</strong>, születési neve albánul: <strong>Agnes Gonxha Bojaxhiu</strong> (Üsküb, 1910. augusztus 26. – Kolkata, Nyugat-Bengál, 1997. szeptember 5.) albán származású római katolikus apáca, a Szeretet Misszionáriusai szerzetesrend alapítója, a Nobel-békedíj (1979) és számos magas kitüntetés tulajdonosa. Az indiai Kolkata szegénynegyedeiben végzett áldozatos munkájával az egész emberiség elismerését kivívta.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (23, "Ramana Maharshi", "<p>(India, Tirucsuli, 1879. december 30. – India, Tiruvannámalai, 1950. április 14.), születési nevén Venkataráman Ijer, az egyik legismertebb modern kori indiai bölcs, advaita mester, szatguru.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (24, "William Shakespeare", "<p>(26 aprilie 1564, Stratford-upon-Avon, Regatul Angliei – 23 aprilie 1616, Stratford-upon-Avon, Regatul Angliei) dramaturg și poet englez, considerat cel mai mare scriitor al literaturii de limba engleză și supranumit „Poetul din Avon” (în engleză 'Bard of Avon”) sau „Lebăda de pe Avon” ('The Swan of Avon'). Din vasta sa operă - parte realizată în colaborare - s-au păstrat aproape 38 de piese de teatru, 154 de sonete, 2 lungi poeme narative, precum și alte multe poezii traduse în aproape fiecare limbă vorbită.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (25, "Brené Brown", "<p><strong>Casandra Brené Brown </strong>(1965. november 18. - ) amerikai professzor, előadó, író és podcast műsorvezető.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (26, "Richard Rohr", "<p>amerikai ferences lelkész, prédikátor és spirituális könyvek szerzője.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (27, "Joseph Campbell", "<p>(White Plains, New York, 1904. március 26. – Honolulu, 1987. október 30.) amerikai író, esszéista, vallástörténész.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (28, "Marcus Aurelius", "<p><strong>Imperator Caesar Marcus Aurelius Verus Augustus</strong>, általánosan elterjedt néven <strong>Marcus Aurelius császár</strong>, született <strong>Marcus Annius Catillius Severus</strong> (Róma, 121. április 26. – Vindobona vagy Sirmium, 180. március 17.) – a Római Birodalom császára (161. március 7. – 180. március 17.)</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (29, "Denis Waitley", "<p><strong>Denis E. Waitley</strong> (született 1933-ban) amerikai motivációs előadó, író és tanácsadó.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (30, "Eckhart Tolle", "<p><strong>Ulrich Leonard Tölle </strong>(Lünen, 1948. február 16. – ) német származású kanadai író, előadó, spirituális tanító.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (31, "Walt Whitman", "<p>(31 mai 1819, West Hills⁠, Suffolk, New York, SUA – 26 martie 1892, Camden⁠, New Jersey, SUA) poet, eseist, jurnalist și umanist american. Considerat „cel mai mare poet american” de către mulți pe când trecuseră doar patru ani de la moartea sa, Whitman este văzut ca primul poet urban. Este unul dintre reprezentanții tranziției de la transcendentalism la realism, opera sa arătându-se influențată de ambele curente. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (32, "Alan Watts", "<p><strong>Alan Wilson Watts</strong> (1915. január 15. – 1973. november 16.) angol származású filozófus, író, a keleti filozófiák és életfelfogás Nyugaton való terjesztéséről vált híressé.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (33, "Lao-ce", "<p><strong>Lao-ce</strong> (kínaiul:<strong>老子</strong>, pinjin: <strong>Lǎozǐ</strong>) feltehetőleg az i. e. 6. században élt kínai filozófus, akiről azonban nem tudni biztosan, hogy valóban létező történelmi személy volt-e, vagy csupán legenda. Lao-ce magyarul „<strong>öreg gyermek</strong>et” vagy „<strong>öreg mester</strong>t” jelent, ugyanis a gyermek szó a taoizmusban a tapasztalt mesterek gyermeki tisztaságát, egyszerűségét jelenti.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (34, "Lev Tolsztoj", "<p><strong>Lev Nyikolajevics Tolsztoj</strong> (Jasznaja Poljana, 1828. szeptember 9. – Asztapovo, 1910. november 20.) orosz író, filozófus, az orosz irodalom és a világirodalom egyik legnagyobb szerzője, a realista orosz próza mestere. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (35, "Mark Twain", "<p><strong>Samuel Langhorne Clemens</strong> (Florida, Monroe megye, Missouri, 1835. november 30. – Redding, Connecticut, 1910. április 21.) amerikai író, újságíró, humorista.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (36, "Dante Alighieri", "<p>eredetileg <strong>Durante Alighieri</strong>, gyakran csak <strong>Dante </strong>(Firenze, 1265. május második fele – Ravenna, 1321. szeptember 14.) itáliai költő, filozófus, az olasz és a világirodalom nagy klasszikusa.  </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (37, "Wass Albert", "<p>Gróf szentegyedi és cegei <strong>Wass Albert</strong> (Válaszút, 1908. január 8. – Astor, Florida, 1998. február 17.) erdélyi magyar író és költő. A magyar irodalom egyik legellentmondásosabb alakja; a hivatalos irodalomtörténet mintha tudomást se venne róla, miközben mások a legnagyobbak között emlegetik.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (38, "Hamvas Béla", "<p>(Eperjes, 1897. március 23. – Budapest, 1968. november 7.) Kossuth-díjas magyar író, filozófus, esztéta és könyvtáros, Hamvas József evangélikus lelkész, tanár, író és hírlapszerkesztő fia.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (39, "Popper Péter", "<p>(Budapest, 1933. november 19. – Budapest, 2010. április 16.) magyar pszichológus, klinikai gyermek-szakpszichológus, pszichoterapeuta, egyetemi tanár, író.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (40, "Dzsalál ad-Dín Rúmí", "<p><strong>Rumi</strong>, <strong>Dzsalál ad-Dín Muhammad Rúmí</strong> (Gúrida Birodalom, Balh, 1207. szeptember 30. – Ikóniumi (Rumi) Szultánság, Konya, 1273. december 17.) a legnagyobb perzsa szúfi misztikus filozófus és költő.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (41, "Giovanni Boccaccio", "<p>(Certaldo, 1313. június 16. – 1375. december 21.) olasz író, költő, humanista.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (42, "Geoffrey Chaucer", "<p>(London, 1343 – London, 1400. október 25.) az első angol nyelven alkotó költő, filozófus, bürokrata, diplomata.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (43, "Francesco Petrarca", "<p>(Arezzo, 1304. július 20. – Arquà, 1374. július 19.) az egyik legnevesebb itáliai prehumanista költő. Életműve évszázadok óta befolyást gyakorol az olasz és az európai költészetre, hatása érződik Magyarországon is Balassi Bálinttól a kortárs Rózsássy Barbaráig.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (44, "Ludovico Ariosto", "<p>(8 septembrie 1474, Reggio nell'Emilia, Italia – 6 iulie 1533, Ferrara, Statele Papale) poet italian din epoca Renașterii, cunoscut mai ales ca autor al poemului eroic 'Orlando furioso' (1516).</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (45, "Giordano Bruno", "<p>(Nola, 1548 – Róma, 1600. február 17.) itáliai domonkos szerzetes, filozófus, csillagász, matematikus, költő és hermetikus okkultista; a késő reneszánsz egyik kiemelkedő alakja.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (46, "Leonardo da Vinci", "<p><strong>Leonardo di ser Piero da Vinci </strong>(1452. április 15. – 1519. május 2.) itáliai polihisztor. Leonardo festő, tudós, matematikus, hadmérnök, feltaláló, anatómus, szobrász, építész, zeneszerző, költő és író egy személyben.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (47, "Rotterdami Erasmus", "<p>(Rotterdam vagy Gouda, 1466. október 27. (?) – Bázel, 1536. július 12.) nagy hatású németalföldi humanista tudós, Ágoston-rendi szerzetes, filozófus és teológus. A reformáció szellemi előfutára. Nevének régi magyar alakja: Rézmán.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (48, "Apáczai Csere János", "<p><strong>Apáczai Csere János</strong>, születési nevén <strong>Csere János</strong>, 1656-tól többnyire <strong>Apáczai Cseri János</strong> (Apáca, 1625. június 10.? – Kolozsvár, 1659. december 31.) erdélyi magyar pedagógus, filozófus, kálvinista teológus, a magyar nevelésügy és az anyanyelvi tudományos ismeretterjesztés előfutára, az erdélyi puritánus mozgalom kiemelkedő alakja, az első magyar enciklopédista, a karteziánus ismeretelmélet korai magyar követőinek egyike.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (49, "Aquinói Szent Tamás", "<p><strong>Aquinói Tamás </strong>(Roccasecca, 1225. január 28. – Fossanova, 1274. március 7.) latin nyelven író középkori olasz teológus, skolasztikus filozófus, Domonkos-rendi szerzetes, a keresztény misztika egyik képviselője.<strong> Angyali Doktor</strong> (latinul: <strong>Doctor Angelicus</strong>) néven is ismert. A római katolikus egyház legnevezetesebb hittudósai, az úgynevezett egyháztanítók között tartja számon.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (50, "Brassai Sámuel", "<p>(Torockószentgyörgy, 1797. június 15. – Kolozsvár, 1897. június 24.) nyelvész, filozófus, természettudós, az „utolsó erdélyi polihisztor”, az MTA tagja. Foglalkozott földrajzzal, történelemmel, statisztikával és közgazdaságtannal, a zene elméleti kérdéseivel, esztétikával, valamint műkritikával is. Az oktatásügyben számos reform elindítója és előmozdítója volt; hozzájárult a korabeli tudományos eredmények népszerűsítéséhez.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (51, "Domanovszky Endre", "<p>(Tótkomlós, 1817. november 17. – Budapest, 1895. április 18.) magyar filozófus, filozófiai író, jogakadémiai tanár, a Magyar Tudományos Akadémia levelező tagja.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (52, "Hankiss Elemér", "<p>(Debrecen, 1928. május 4. – Budapest, 2015. január 10.) Széchenyi-díjas magyar szociológus, filozófus, értékkutató, irodalomtörténész.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (53, "Hermann István", "<p>(Budapest, 1925. október 10. – Budapest, 1986. szeptember 12.) magyar filozófus, esztéta, kritikus, pedagógus, egyetemi tanár, a Magyar Tudományos Akadémia tagja (levelező: 1976, rendes: 1985).</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (54, "Hevesi Simon", "<p>születési és 1905-ig használt nevén <strong>Handler Simon</strong> (Aszód, 1868. március 22. – Budapest, Terézváros, 1943. február 1.) magyar–zsidó hittudós teológus és filozófus, rabbi, egyetemi tanár.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (55, "Jász Géza", "<p>(Jobbágyi, 1863. február 12. – Budapest, 1937. február 11.) magyar sporttisztviselő, a Magyar Labdarúgó-szövetség (MLSZ) első elnöke, filozófiai író.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (56, "Kalocsai Dezső", "<p>(1924. szeptember 28., Bánréve, Gömör és Kishont vármegye - 2006. január 15., Budapest) filozófus.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (57, "Kampis György", "<p>(Budapest, 1958. június 2. –) tudományfilozófus, elméleti biológus és evolúciókutató. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (58, "Joseph Kentenich", "<p>(1885. november 16. - 1968. szeptember 15.) pallottinus pap, a Schönstatti Apostoli Mozgalom alapítója. Teológus, pedagógus és a modern kor számos kérdésére adott katolikus válasz úttörője volt, akinek tanításai számos kihívásnak voltak kitéve a politikai és egyházi hatalom részéről.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (59, "Arthur Koestler", "<p><strong>Kestler Artur</strong>, <strong>Kösztler Artúr</strong> (Budapest, Terézváros, 1905. szeptember 5. – London, 1983. március 1.) magyar-brit regény- és újságíró, társadalomfilozófus.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (60, "Georg Kühlewind", "<p><strong>Székely György</strong> (1924. március 6. - 2006. január 15.) magyar filozófus, író, előadó és meditációs tanító volt, aki Rudolf Steiner szellemtudományának hagyományából dolgozott.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (61, "Murányi Mihály", "<p>(Bp., 1933. – Bp., 1978): pszichológus, filozófus.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (62, "Polányi Károly", "<p>(született Pollacsek) (Bécs, 1886. október 25. – Pickering, Ontario, 1964. április 23.) magyar gazdaságtörténész, szociálfilozófus, társadalompolitikus.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (63, "Polányi Mihály", "<p>(szül. Pollacsek) (Budapest, 1891. március 11. – Northampton, 1976. február 22.) magyar–brit tudós, akinek a munkássága a fiziko-kémiától a közgazdaságtanon keresztül a filozófiáig terjedt.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (64, "Schreiner Dénes", "<p>(Budapest, 1969) filozófus, esztéta. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (65, "Sós Vilmos", "<p>(1933-2019)</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (66, "Augustin de Hipona", "<p><strong>Augustinus de Thagaste</strong>, <strong>Augustin</strong> sau <strong>Aurelius Augustinus</strong> (<strong>Sfântul Augustin</strong> la catolici, lat. <strong>Sanctus Augustinus</strong>, <strong>Fericitul Augustin</strong> la ortodocși, (13 noiembrie 354, Thagaste, Numidia, azi Souk Ahras, în Algeria - 28 august 430, Hippo Regius, Numidien, azi Annaba, Algeria) Învățător al Bisericii (doctores ecclesiae), episcop, filozof, teolog. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (67, "Alexandriai Szent Atanáz", "<p><strong>Alexandriai Szent Atanáz </strong>vagy <strong>Nagy Szent Atanáz</strong>, a Kopt Egyházban <strong>Apostoli Szent Atanáz</strong>, görögösen <strong>Athanasziosz</strong> (Alexandria, 298 körül – Alexandria, 373. május 2.) Alexandria törvényes püspöke 328-tól haláláig, egyháztanító, az arianizmus harcos ellenfele. Még életében elnyerte az Egyház oszlopa és az Ortodoxia atyja kitüntető elnevezéseket. Atanáz a migrénes fejfájásban szenvedők védőszentje.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (68, "Antiochiai Szent Ignác", "<p>(35 körül – 107. [vagy 117] október 17.) Antiochia püspöke 69-től haláláig, apostoli atya.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (69, "Avilai Szent Teréz", "<p>(Ávila, Kasztília, Spanyolország, 1515. március 28. – Alba de Tormes, Salamanca (tartomány), 1582. október 4.) apáca, a római katolikus egyház szentje.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (70, "Bosco Szent János", "<p><strong>Giovanni Melchiorre Bosco Ochienna</strong>, <strong>Don Bosco</strong> (Castelnuovo d'Asti, 1815. augusztus 16. – Torino, 1888. január 31.) olasz katolikus pap, a Szalézi Társaság férfi szerzetesrend megalapítója.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (71, "Canterburyi Szent Anzelm", "<p>(Aosta, 1033. – Canterbury, 1109. április 21.) nagy hatású teológus, filozófus, a szellemtörténet a „skolasztika atyjaként” is tiszteli.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (72, "Chantal Szent Johanna Franciska", "<p><strong>Jeanne-Françoise Frémiot, Baronne de Chantal</strong> (Dijon, 1572. január 28. – Moulins, 1641. december 13.) római katolikus szent.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (73, "Claret Szent Antal Mária", "<p>(Sallent, Katalónia, 1807. december 23. – Fontfroide, 1870. október 24.) spanyol püspök.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (74, "Csíkszentmihályi Mihály", "<p>(Fiume, Olaszország, 1934. szeptember 29. – Claremont, Kalifornia, USA, 2021. október 20.) Széchenyi-díjas magyar-amerikai pszichológus. A flow-élmény, vagyis egy nagyon összpontosított elmeállapot pszichológia koncepciójának megalkotója. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (75, "Facundói Szent János", "<p>(1430 körül - 1479. június 11.) spanyol augustinus szerzetes és pap.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (76, "Goretti Szent Mária", "<p><b>Santa Maria Goretti</b> (Corinaldo 1890. október 16. – Nettuno 1902. július 6.) olasz vértanú, 1950. június 24-én avatta szentté XII. Piusz pápa, melynek során Mária édesanyja is jelen volt.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (77, "I. Szent László", "<p>(Lengyel Királyság, 1040 körül – Nyitra, 1095. július 29.) 1077-től magyar király, 1091-től haláláig pedig horvát király. I. Béla király és Richeza királyné másodszülött fia. Nevéhez fűződik a magántulajdon védelmének megszilárdítása, Horvátország elfoglalása (1091) és az első magyar szentek avatása. Őt magát is szentté avatták 1192-ben, és kivételes fizikuma miatt nevezik az „Isten atlétájának” is.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (78, "Pennaforti Szent Rajmund", "<p>(1175/1180 körül – 1275. január 6.) középkori hispániai pap, domonkos szerzetes, jogtudós.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (79, "Lothar Bickel", "<p><strong>Eliezer Bickel</strong> (1902. május 8. Kysseliw, Bukovina, Ausztria-Magyarország - 1951. április 23. Toronto, Kanada ) osztrák, 1919-től román, 1950-től kanadai orvos, filozófiai írások szerzője.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (80, "Emil Cioran", "<p><strong>Émile Michel Cioran</strong> (Resinár, 1911. április 8. – Párizs, 1995. június 20.) román-francia filozófus és író.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (81, "Camil Petrescu", "<p>(Bukarest, 1894. április 22. – Bukarest, 1957. május 14.) román író, drámaíró, költő, esztéta és filozófus. Az ő fellépésének köszönhetően ért véget a román irodalomban a klasszikus regények időszaka és ő lett a modern regényírás romániai megalapítója.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (82, "Andrei Plesu", "<p><strong>Andrei Gabriel Pleșu </strong>(Bukarest, 1948. augusztus 23. - ) román filozófus, művészettörténész, publicista és politikus.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (83, "Jack Kornfield", "<p>Amerikai író és tanár az amerikai théraváda buddhizmus Vipassana mozgalmában.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (84, "Elisabeth Kübler-Ross", "<p>(Zürich, 1926. július 8. – Scottsdale, 2004. augusztus 24.) svájci származású pszichiáter. Ő az On Death and Dying (A halál és a hozzá vezető út) című nagy horderejű könyv szerzője, amelyben ismertette a Kübler-Ross modellként ismert elméletét.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (85, "Terence McKenna", "<p><strong>Terence Kemp McKenna </strong>(November 16, 1946 – April 3, 2000) American ethnobotanist and mystic who advocated the responsible use of naturally occurring psychedelic plants.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (86, "Tendzin Gyaco", "<p><strong>Dzsepcun Dzsampal Ngagvang Loszang Jese Tendzin Gyaco</strong> (olykor <strong>Gjaco </strong>átírással is), született <strong>Lhamo Donrup</strong> (Takcer, 1935. július 6. – ) a tizennegyedik dalai láma, a tibeti emigráns kormány egykori vezetője</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (87, "Amy Cuddy", "<p><strong>Amy Joy Casselberry Cuddy</strong> (1972. július 23 - ) amerikai szociálpszichológus, író és előadó. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (88, "Rick Warren", "<p>amerikai evangélikus lelkész és író.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (89, "Billy Graham", "<p><strong>William Franklin 'Billy' Graham</strong> (Charlotte, Észak-Karolina, 1918. november 7. – Montreat, Észak-Karolina, 2018. február 21.) amerikai evangélista.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (90, "Hermann Hesse", "<p><strong>Hermann Karl Hesse</strong>, írói álneve: <strong>Emil Sinclair</strong> (Calw, 1877. július 2. – Svájc, Montagnola, 1962. augusztus 9.) irodalmi Nobel-díjas német-svájci író, költő és festő, az újromantika kimagasló képviselője.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (91, "Abraham Joshua Heschel", "<p>(1907. január 11. - 1972. december 23.) lengyel-amerikai rabbi, a 20. század egyik vezető zsidó teológusa és zsidó filozófusa.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (92, "Amy Grant", "<p><strong>Amy Lee Grant </strong>(Augusta, Georgia, 1960. november 25. –) Grammy-díjas amerikai keresztény-pop énekesnő, alkalmanként színésznő.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (93, "Joel Osteen", "<p><strong>Joel Scott Osteen</strong> (1963. március 5. - ) amerikai világi prédikátor, televangelista, üzletember és író a texasi Houstonban. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (94, "Jane Goodall", "<p><b>Dame Jane Goodall</b> (London, 1934. április 3. – ) angol etológus, antropológus és primatológus, a csimpánzok szociális és családi életének legismertebb kutatója, a Jane Goodall Intézet és a tanzániai Gombe Nemzeti Park igazgatója. Zoológiai szakmunkákban nevének rövidítése: <b>„Goodall”</b>.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (95, "Alan Alexander Milne", "<p>(Kilburn, London, 1882. január 18. – Hartfield, Sussex, 1956. január 31.) angol író, a Micimackó című gyermekkönyv szerzője.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (96, "Ruth Bader Ginsburg", "<p>(Brooklyn, 1933. március 15. – Washington, 2020. szeptember 18.) amerikai jogász, az Amerikai Egyesült Államok Legfelsőbb Bíróságának bírája 1993-tól 2020-ban bekövetkezett haláláig.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (97, "Diána walesi hercegné", "<p><strong>Diana Frances Spencer</strong> (Sandringham, Anglia, 1961. július 1. – Párizs, 1997. augusztus 31.) walesi hercegné 1981-től 1996-ig, III. Károly brit király első felesége.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (98, "Biasz", "<p>Kr. e. 590 körül született a jón Prienében, és Kr. e. 530 körül halt meg. Az ókori görög filozófiai hagyományban számon tartott, úgynevezett hét bölcs egyike. Bírói tisztet viselt. Az ókori hagyomány szerint éles eszű, igazságos és békeszerető ember volt. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (99, "Hésziodosz", "<p>(i. e. 8. század első fele) görög epikus költő.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (100, "Homérosz", "<p>(i. e. 8. század) görög költő.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (101, "Thalész", "<p><strong>Milétoszi Thalész</strong> (Milétosz, Kr. e. 624 körül – Kr. e. 546 körül). A hét bölcs egyike, a matematika és filozófia atyja, a materialista milétoszi filozófiai iskola első képviselője, a legkorábbi görög természetfilozófus. Ő az első olyan görög matematikus, akinek neve fennmaradt.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (102, "Aiszkhülosz", "<p>(i. e. 525 – Gela, Szicília, i. e. 456) görög tragédiaköltő, a „tragédia atyja”.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (103, "Hérakleitosz", "<p><strong>Epheszoszi Hérakleitosz</strong> (kb. Kr. e. 535 – Kr. e. 475), az Égei-tenger partján található Epheszoszban született preszókratikus filozófus. Karl Jaspers szerint egyike volt az ún. tengelykor (i. e. 800 – 200) meghatározó gondolkodóinak. Az emberi tudattól független anyagi valóság létezésének hangsúlyozása, az istenimádás elutasítása miatt a materializmus, az ellentétek szerepével kapcsolatos kutatásai miatt pedig a dialektika előfutárának is tekintik.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (104, "Püthagorasz", "<p>(Kr. e. 570 körül – Kr. e. 495) ión származású, preszókratikus filozófus és matematikus, a püthagoreus filozófiai iskola megalapítója. Nevét ma a matematikában a Pitagorasz-tétel viseli (amit a maga általánosságában valószínűleg ő bizonyított először). Tanítványaival máig ható eredményeket ért el a csillagászatban, a matematikában és a zeneelméletben.</p>
INSERT INTO Author_Name (id, name, des, language_id) VALUES (105, "Antiszthenész", "<p>(i. e. 446 – i. e. 368) görög filozófus, a cinikus iskola megalapítója. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (106, "Arisztophanész", "<p>(Kr. e. 446 k. – Kr. e. 386) athéni komédiaköltő, az attikai ókomédia legkiemelkedőbb alkotója.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (107, "Démokritosz", "<p>(Abdéra, I. e. 470 és I. e. 460 között – Abdéra, I. e. 370 körül), ókori görög atomista filozófus, az antik materializmus kiemelkedő képviselője.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (108, "Hérodotosz", "<p><strong>Halikarnasszoszi Hérodotosz</strong> (Halikarnasszosz, Kis-Ázsia, ma Bodrum, Törökország, i. e. 484 körül – Thurioi, i. e. 425 körül) görög történetíró.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (109, "Hippokratész", "<p><strong>Kószi Hippokratész </strong>(Kosz, Kr. e. 460 – Larissza, Kr. e. 377 körül) ókori görög orvos, a kószi orvosi iskola vezetője, természettudományos író, antropológus. Az orvostudomány megalapítójának tartják, az ókorban egyenesen Aszklépiosztól, a gyógyítás istenétől származtatták. Névrokona a matematikus khioszi Hippokratész és Hippokratész, Géla türannosza is. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (110, "Iszokratész", "<p>(Athén, i. e. 436 – Athén, i. e. 338 augusztusa) ókori görög szónok.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (111, "Stefan Zweig", "<p>(Bécs, 1881. november 28. – Petrópolis, 1942. február 22.) osztrák író, költő, műfordító.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (112, "Émile Zola", "<p><strong>Émile Édouard Charles Antoine Zola</strong> (Párizs, 1840. április 2. – Párizs, 1902. szeptember 29.) francia regényíró és művészeti kritikus, a világirodalom jelentős alkotója, a naturalista irányzat megteremtője. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (113, "Oscar Wilde", "<p><strong>Oscar Fingal O'Flaherty Wills Wilde</strong> (Dublin, 1854. október 16. – Párizs, 1900. november 30.) ír költő, író, drámaíró.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (114, "Quentin Tarantino", "<p>(Knoxville, Tennessee, 1963. március 27. –) olasz származású amerikai rendező, színész, forgatókönyvíró, producer, az amerikai független film egyik legnagyobb hatású alakja.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (115, "Woody Allen", "<p><strong>Allen Stewart Konigsberg</strong> (New York, 1935. november 30. –) amerikai író, színész, humorista, Oscar-díjas filmrendező és filmproducer.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (116, "Ivan Szergejevics Turgenyev", "<p>(Orjol, Oroszország, 1818. november 9. – Párizs, Franciaország, 1883. szeptember 3.) orosz író, drámaíró, költő, műfordító. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (117, "Fjodor Ivanovics Tyutcsev", "<p>(Ovsztug, 1803. december 5. – Carszkoje Szelo, 1873. július 27.) orosz költő, diplomata.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (118, "Henry David Thoreau", "<p>(July 12, 1817 – May 6, 1862) American naturalist, essayist, poet, and philosopher.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (119, "Rabindranath Tagore", "<p>(Kalkutta, 1861. május 7. – Kalkutta, 1941. augusztus 7.) indiai költő, író, zeneszerző, festő, polihisztor, aki életművével átformálta az indiai régió irodalmát és zenéjét. Prózájában és verseiben, túllépve a klasszikus szanszkrit hagyományokon, a köznapi nyelv használatát vezette be a bengáli irodalomba. Kiemelkedő alkotóművésze a modern Indiának, aki egyúttal közvetlen kapcsolatot teremtett a nyugati és a legszínvonalasabb indiai művészetek között.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (120, "Robert Louis Stevenson", "<p>(Edinburgh, 1850. november 13. – Vailima, Apia közelében, Szamoa, 1894. december 3.) skót regényíró, az ifjúsági irodalom kimagasló alakja.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (121, "Herbert Spencer", "<p>(Derby, 1820. április 27. – Brighton, 1903. december 8.) angol filozófus, biológus, antropológus és szociológus.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (122, "Samuel Smiles", "<p>(Haddington, 1812. december 23. – Kensington, 1904. április 16.) skót író, aki elsősorban a neveléssel kapcsolatos műveket adott ki.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (123, "Henryk Sienkiewicz", "<p><strong>Henryk Adam Aleksander Pius Sienkiewicz </strong>(Wola Okrzejska, Kongresszusi Lengyelország, 1846. május 5. – Vevey, 1916. november 15.) irodalmi Nobel-díjas lengyel író.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (124, "George Bernard Shaw", "<p><strong>G. B. Shaw</strong> (Dublin, 1856. július 26. – Ayot St Lawrence, Hertfordshire, 1950. november 2.) Nobel-díjas ír drámaíró. William Shakespeare után az ő műveit játsszák leggyakrabban az angol nyelvű színházakban.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (125, "Albert Schweitzer", "<p>alias <strong>Oganga</strong>, avagy <strong>Nagy Fehér Varázsló</strong> (Kaysersberg, Németország (ma Franciaország) 1875. január 14. – Lambaréné, Gabon, 1965. szeptember 4.) elzászi német teológus, lelkész, filozófus, orgonaművész, tanár, orvos, Nobel-békedíjas (1952).</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (126, "George Sand", "<p><strong>Amandine Aurore Lucile Dupin</strong>, <strong>Baronne Dudevant</strong> (Párizs, 1804. július 1. – Nohant-Vic, 1876. június 8.) francia írónő, regényíró, dramaturg, újságíró, irodalomkritikus.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (127, "Antoine de Saint-Exupéry", "<p>(Lyon, 1900. június 29. – Marseille, 1944. július 31.) francia író és pilóta.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (128, "Bertrand Russell", "<p>(Ravenscroft (Monmouthshire), Wales, 1872. május 18. – Penrhyndeudraeth, Wales, 1970. február 2.) irodalmi Nobel-díjas angol matematikus, logikatudós, filozófus és szociológus, Kingston III. grófja, közéleti személyiség. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (129, "John Ruskin", "<p>(London, 1819. február 8. – Coniston, Cumbria, Anglia, 1900. január 20.) angol művészeti író, festő és esztéta. A modern kortárs művészek pártfogója.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (130, "Eleanor Roosevelt", "<p><strong>Anna Eleanor Roosevelt </strong>(New York, 1884. október 11. – New York, 1962. november 7.) az Egyesült Államok first ladyje 1933-tól 1945-ig. Támogatta férje, <strong>Franklin D. Roosevelt</strong> kormányzati politikáját, a New Dealt, és az emberi jogok szószólója lett. Miután férje 1945-ben meghalt, Eleanor megmaradt szónoknak és politikusnak, és továbbra is támogatta a <strong>New Deal-koalíció</strong>t. Dolgozott a nők egyenjogúságáért.</p>
INSERT INTO Author_Name (id, name, des, language_id) VALUES (131, "Romain Rolland", "<p>(Clamecy, 1866. január 29. – Vézelay, Burgundia, 1944. december 30.) Nobel-díjas francia író.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (132, "Erich Maria Remarque", "<p>született <strong>Erich Paul Remark</strong> (Osnabrück, Németország, 1898. június 22. – Locarno, Svájc, 1970. szeptember 25.) német pacifista és antifasiszta író.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (133, "Alekszandr Szergejevics Puskin", "<p>(Moszkva, 1799. június 6. – Szentpétervár, 1837. február 10.) orosz költő, író, drámaíró, az orosz irodalom fejlődésének meghatározó alakja, aki mindhárom műnemben maradandó alkotásokat hozott létre. A modern orosz irodalmi nyelv megteremtője.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (134, "Abraham Lincoln", "<p>(Hodgenville mellett, Kentucky, 1809. február 12. – Washington, 1865. április 15.) 1861-től 1865-ig az Amerikai Egyesült Államok 16. elnöke, az első republikánus elnök.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (135, "Stanisław Lem", "<p>(Lwów, Lengyelország, 1921. szeptember 12. – Krakkó, 2006. március 27.) lengyel science fiction-író. Munkáiban gyakran bocsátkozik filozófiai spekulációkba a technológiáról, az intelligencia természetéről, a kétoldalú kommunikáció és a kölcsönös megértés nehézségeiről vagy az emberi korlátokról és az emberiség világegyetemben elfoglalt helyéről. Témáit néha fikcióként mutatja be, elkerülve ezzel mind a tudományos élet buktatóit, mind a tudományos stílus korlátait, máskor azonban az esszé vagy filozófiai könyv műfaját választotta.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (136, "Vasilii Kliucevski", "<p><strong>Vasilii Osipovici Kliucevski </strong>(28 ianuarie 1841 - 25 mai 1911) un important istoric imperial rus al perioadei imperiale târzii. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (137, "Franz Kafka", "<p>(Prága, 1883. július 3. – Kierling, ma Klosterneuburg része, 1924. június 3.) német nyelven alkotó csehországi születésű, zsidó író, a 20. századi irodalom egyik legkiemelkedőbb alakja. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (138, "Victor Hugo", "<p><strong>Victor-Marie Hugo </strong>(Besançon, 1802. február 26. – Párizs, 1885. május 22.) francia romantikus költő, regény- és drámaíró, politikus és akadémikus.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (139, "Alekszandr Ivanovics Herzen", "<p>(Moszkva, 1812. április 6. – Párizs, 1870. január 21.) az orosz forradalmi demokraták első nemzedékének tagja, materialista gondolkodó, és az orosz szocializmusnak, a narodnyikságnak a megteremtője volt.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (140, "Ernest Hemingway", "<p><strong>Ernest Miller Hemingway </strong>(Oak Park, Illinois, 1899. július 21. – Ketchum, Idaho, 1961. július 2.) amerikai, irodalmi Nobel-díjas regényíró, novellista, újságíró, az elveszett nemzedék jeles tagja.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (141, "Nyikolaj Vasziljevics Gogol", "<p>(Szorocsinci, 1809. április 1. – Moszkva, 1852. március 4.) ukrán származású orosz író, az orosz irodalom klasszikusa, az orosz széppróza és drámairodalom egyik legnagyobb hatású alakja.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (142, "Mahatma Gandhi", "<p><strong>Mohandász Karamcsand Gandhi</strong>, híveitől kapott tiszteletnevén <strong>Mahátma Gandhi</strong> (Porbandar, Gudzsarát, 1869. október 2. – Újdelhi, 1948. január 30.) indiai jogász, politikai és spirituális vezető, az indiai függetlenségi mozgalom vezéralakja.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (143, "John Galsworthy", "<p>(Kingston upon Thames, 1867. augusztus 14. – London, 1933. január 31.) angol író és drámaíró, irodalmi Nobel-díjas. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (144, "Erich Fromm", "<p><strong>Erich Seligmann Fromm </strong>(Frankfurt am Main, Németország, 1900. március 23. – Muralto, Ticino kanton, Svájc, 1980. március 18.) német filozófus, szociálpszichológus, humanista.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (145, "Anatole France", "<p><strong>Jacques Anatole François Thibault</strong> (Párizs, 1844. április 16. – Saint-Cyr-sur-Loire, 1924. október 12.) francia író, költő, kritikus, akadémikus. Franciaországban úgy tekintenek rá, mint a „harmadik köztársaság” egyik nagy irodalmi kritikusára, kora élő lelkiismeretére, aki a 20. század elején mélyen elkötelezte magát szociális és politikai ügyek mellett. 1921-ben munkásságát irodalmi Nobel-díjjal ismerték el.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (146, "Gustave Flaubert", "<p>(Rouen, 1821. december 12. – Croisset – ma Canteleu része, 1880. május 8.) francia író, a lélektani regény egyik legnagyobb mestere.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (147, "Ralph Waldo Emerson", "<p>(Boston, Massachusetts, 1803. május 25. – Concord, Massachusetts, 1882. április 27.) amerikai esszéíró, költő, unitárius lelkész és a transzcendentalista mozgalom vezetője a 19. század elején.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (148, "Albert Einstein", "<p>(Ulm, 1879. március 14. – Princeton, 1955. április 18.) zsidó származású német Nobel-díjas elméleti fizikus; egyes tudományos és laikus körökben a legnagyobb 20. századi tudósnak tartják. Ő dolgozta ki a relativitáselméletet és nagymértékben hozzájárult a kvantummechanika, a statisztikus mechanika és a kozmológia fejlődéséhez. 1921-ben fizikai Nobel-díjjal jutalmazták „az elméleti fizika területén szerzett eredményeiért, különös tekintettel a fényelektromos jelenség törvényszerűségeinek felismeréséért”. Zsenialitására jellemző, hogy előre tudott jelezni olyan dolgokat, amelyeket 100 évvel később a gyakorlatban is bizonyítottak. 2016. február 11-én amerikai kutatók bejelentették, hogy gravitációs hullámokkal kapcsolatos elmélete igazolódott, ezzel a felfedezéssel egy új tudományág született, a gravitációshullám-csillagászat.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (149, "Alexandre Dumas", "<p><strong>Id. Alexandre Dumas</strong>, <strong>Alexandre Dumas père</strong>, teljes nevén <strong>Dumas Davy de la Pailleterie</strong> (Villers-Cotterêts, 1802. július 24. – Puys, Dieppe mellett, 1870. december 5.) francia író, legismertebb művei kalandos történelmi regények, amelyek a világ legolvasottabb francia írójává tették őt. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (150, "Alexandre Dumas (ifj.)", "<p><strong>Ifj. Alexandre Dumas</strong> (<strong>Dumas fils</strong>) (Párizs, 1824. július 27. – Marly-le-Roi, Yvelines, 1895. november 27.) francia író, drámaíró, novellista, id. Alexandre Dumas fia.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (151, "Fjodor Mihajlovics Dosztojevszkij", "<p>(Moszkva, 1821. november 11. – Szentpétervár, 1881. február 9.) orosz író. Az orosz irodalom és az egész világirodalom mindmáig legnevesebb képviselőinek egyike, a 19. századi orosz irodalom felemelkedésének kulcsfigurája, az ún. filozófiai-ideológiai regény műfajának megalkotója, a lélektani regény mestere.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (152, "Benjamin Disraeli", "<p>Beaconsfield grófja (London, 1804. december 21. – London, 1881. április 19.) brit konzervatív politikus, író, miniszterelnök. Központi szerepet játszott a modern konzervatív párt létrehozásában. Az egyetlen zsidó származású miniszterelnök.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (153, "Charles Dickens", "<p><strong>Charles John Huffam Dickens </strong>(Portsmouth, 1812. február 7. – Higham, 1870. június 9.) angol író. Egyesek szerint a viktoriánus kor legnagyobb írója és az angol nyelv legkiválóbb művelője. Írásainak népszerűségét az is mutatja, hogy napjainkig minden műve folyamatosan kapható anyanyelvén. Írói álneveː <strong>Boz</strong> (magyarul <strong>Boz Dickens Károly</strong>, <strong>Boz Károly</strong>)</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (154, "Charles Darwin", "<p><strong>Charles Robert Darwin</strong> (Shrewsbury, Shropshire, 1809. február 12. – Downe, 1882. április 19.) angol természettudós, a biológiai evolúció egyik felismerője, egyúttal névadója (darwinizmus). </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (155, "Stephen King", "<p><strong>Stephen Edwin King</strong> (<strong>Richard Bachman</strong> néven is publikál) (Portland, Maine, 1947. szeptember 21. – ) amerikai író, a jelenkor egyik legolvasottabb szerzője.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (156, "Anton Pavlovics Csehov", "<p>(Taganrog, 1860. január 29. – Badenweiler, 1904. július 15.) orosz író, drámaíró, orvos. A 19. századi orosz irodalom legnagyobbjai közé tartozik, de a századforduló drámairodalmának egyik legnagyobb hatású alakja is egyben, az úgynevezett „drámaiatlan” dráma megteremtője, a novella műfajának megújítója.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (157, "G. K. Chesterton ", "<p><strong>Gilbert Keith Chesterton </strong>(1874. május 29. – 1936. június 14.) angol író, filozófus, drámaíró, újságíró, szónok, teológus, életrajzíró és kritikus. Gyakran nevezték az ellentmondások hercegének. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (158, "Henry Thomas Buckle", "<p>(1821. november 24. - 1862. május 29.) angol történész, a befejezetlen History of Civilization (A civilizáció története) című mű szerzője, és kiváló amatőr sakkozó. Néha 'a tudományos történelem atyjának' nevezik.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (159, "Bertolt Brecht", "<p>(Augsburg, 1898. február 10. – Berlin, 1956. augusztus 14.) német drámaíró, költő, rendező, a 20. századi színház egyik megújítója.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (160, "Alekszandr Blok", "<p><strong>Alekszandr Alekszandrovics Blok</strong> (Szentpétervár, 1880. november 28. – Petrográd, 1921. augusztus 7.) orosz költő és drámaíró, az orosz szimbolizmus egyik meghatározó alakja.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (161, "Honoré de Balzac", "<p>(Tours, 1799. május 20. – Párizs, 1850. augusztus 18.) francia regényíró, a francia realista regény megteremtőinek egyike, neve a világirodalom legjelentősebb alakjai között szerepel.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (162, "Dzsaváharlál Néhrú", "<p>a perzsa <strong>Javâher-e La'al</strong>-ból, amelynek jelentése <strong>Vörös Ékszer</strong> (Alláhábád, 1889. november 14. – Új-Delhi, 1964. május 27.) a független India első miniszterelnöke (hivatalban: 1947. augusztus 15. – 1964. május 27.), az indiai függetlenségi mozgalom, az Indiai Nemzeti Kongresszus vezetője.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (163, "André Maurois", "<p>született <strong>Émile Salomon Wilhelm Herzog </strong>(1885. július 26. – 1967. október 9.) francia író.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (164, "Guy de Maupassant", "<p><strong>Henri René Albert Guy de Maupassant </strong>(Tourville-sur-Arques (Miromesnil kastély), 1850. augusztus 5. – Passy, 1893. július 6.) népszerű 19. századi francia író, a modern novella egyik atyjaként, a tömör és a lényegre törő stílus mestereként tartják számon.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (165, "Thomas Mann", "<p><strong>Paul Thomas Mann </strong>(Lübeck, 1875. június 6. – Zürich, 1955. augusztus 12.) német író, a 20. századi német nyelvű irodalom egyik legjelentősebb alakja.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (166, "Henry Wadsworth Longfellow", "<p>(Portland, Maine, 1807. február 27. – Cambridge, Massachusetts, 1882. március 24.) amerikai költő, egyetemi tanár.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (167, "Ludwig Feuerbach", "<p><strong>Ludwig Andreas Feuerbach </strong>(28 iulie 1804, Landshut, Bavaria, Germania – 13 septembrie 1872, Nürnberg, Bavaria, Imperiul German) filosof german.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (168, "George Washington", "<p>(Popes Creek, Virginia, 1732. február 22. – Mount Vernon, Virginia, 1799. december 14.) amerikai politikus és katona, a függetlenségi háború főparancsnoka és az Egyesült Államok első elnöke 1789-től 1797-ig. Egyike az amerikai 'alapító atyáknak', az USA egyik nemzeti hőse. Róla nevezték el az ország fővárosát és egyik tagállamát.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (169, "Stendhal", "<p><strong>Marie-Henri Beyle</strong> (Grenoble, 1783. január 23. – Párizs, 1842. március 23.) 19. századi francia író, egyike a realizmus első képviselőinek.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (170, "Adam Smith", "<p>(Kirkcaldy, 1723. június 5. – Edinburgh, 1790. július 17.) skót klasszikus közgazdász és filozófus. Általában őt tartják a modern közgazdaság-tudomány atyjának.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (171, "Arthur Schopenhauer", "<p>(Danzig, 1788. február 22. – Frankfurt am Main, 1860. szeptember 21.) német metafizikus, aki ismertségét főként <em>A világ mint akarat és képzet</em> (<em>Die Welt als Wille und Vorstellung</em>) című fő művének és maró hangulatú esszéinek köszönhette. A köztudatba „a pesszimizmus filozófusaként” vonult be, mivel a világunkat, s abban életünket egy rossz tréfának tartotta.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (172, "Friedrich Schiller", "<p><strong>Johann Christoph Friedrich Schiller</strong>, 1802-től <strong>von Schiller</strong> (Marbach am Neckar, 1759. november 10. – Weimar, 1805. május 9.) német költő, drámaíró, filozófus és történész. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (173, "Jean-Jacques Rousseau", "<p>(Genf, Genfi Köztársaság, 1712. június 28. – Ermenonville, Francia Királyság, 1778. július 2.), genfi felvilágosodás kori filozófus, író, zeneszerző.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (174, "Jean-Paul Sartre", "<p><strong>Jean-Paul Charles Aymard Sartre</strong> (Párizs, 1905. június 21. – Párizs, 1980. április 15.) irodalmi Nobel-díjas francia dráma- és regényíró, irodalomkritikus, politikai aktivista, valamint az ateista egzisztencializmus képviselőjeként a 20. századi francia filozófia vezéralakja.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (175, "Jean Paul Richter", "<p><strong>Johann Paul Friedrich Richter </strong>(Wunsiedel, 1763. március 21. – Bayreuth, 1825. november 14.) német író.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (176, "Georg Christoph Lichtenberg", "<p>(Ober-Ramstadt (Darmstadt közelében), 1742. július 1. – Göttingen, 1799. február 24.) német író, matematikus, a kísérleti fizika első német professzora, az első német nyelvű aforizmaszerző.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (177, "Gotthold Ephraim Lessing", "<p>(Kamenz, 1729. január 22. – Braunschweig, 1781. február 15.) német drámaíró, kritikus, esztéta, dramaturg, a felvilágosodás szellemi életének kiemelkedő alakja, a modern színházi kritika megteremtője.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (178, "Samuel Johnson", "<p>(Lichfield, Staffordshire, 1709. szeptember 18. – London, 1784. december 13.) angol tudós, költő, irodalomkritikus, lexikográfus.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (179, "Immanuel Kant", "<p>(Königsberg, 1724. április 22. – Königsberg, 1804. február 12.) német filozófus, a német idealizmus megteremtője, a königsbergi egyetem professzora. Az ő nevéhez fűződik továbbá a kategorikus imperatívusz megalkotása, a filozófiai „kopernikuszi fordulat”, a transzcendentális idealizmus.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (180, "David Hume", "<p>(Edinburgh, 1711. április 26. – Edinburgh, 1776. augusztus 25.) skót empirista filozófus, történész, közgazdász, esszé-író; a skót felvilágosodás egyik legfontosabb alakja.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (181, "Alexander von Humboldt", "<p>Báró <strong>Alexander von Humboldt</strong>, teljes nevén <strong>Friedrich Wilhelm Heinrich Alexander Freiherr von Humboldt</strong> (Berlin, 1769. szeptember 14. – Berlin, 1859. május 6.) német természettudós és utazó, Wilhelm von Humboldt öccse. Botanikai szakmunkákban nevének rövidítése: „Humb.”.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (182, "Paul Henri Thiry D'Holbach", "<p>născut <strong>Paul Heinrich Dietrich von Holbach</strong> (8 decembrie 1723 - 21 ianuarie 1789) savant, filozof materialist, enciclopedist de origine germană, dar de limbă franceză, cunoscut mai ales ca fiind primul ateu autodeclarat din Europa.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (183, "Johann Gottfried von Herder", "<p>(Mohrungen, 1744. augusztus 25. – Weimar, 1803. december 18.) német költő, műfordító, teológus, filozófus.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (184, "Claude Adrien Helvétius", "<p>(Párizs, 1715. február 26. – Párizs, 1771. december 26.) francia materialista filozófus.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (185, "Heinrich Heine", "<p><strong>Christian Johann Heinrich Heine </strong>(numele la naștere Harry Heine), (13 decembrie 1797, Düsseldorf, Sfântul Imperiu Roman – 17 februarie 1856, Paris, Al Doilea Imperiu Francez) poet și prozator german.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (186, "Georg Wilhelm Friedrich Hegel", "<p>(Stuttgart, 1770. augusztus 27. – Berlin, 1831. november 14.) német filozófus, egyetemi tanár; a klasszikus német filozófia és a német idealizmus legnagyobb és legjelentősebb képviselője.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (187, "Johann Wolfgang von Goethe", "<p>(Frankfurt am Main, 1749. augusztus 28. – Weimar, 1832. március 22.) német író, költő, grafikus, művészetteoretikus, jogász és politikus. A német irodalom egyik klasszikusa, a világirodalom egyik legnagyobb költője. A líra, a dráma és az epika műnemében egyaránt remekművek sorát alkotta.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (188, "Benjamin Franklin", "<p>(Boston, Massachusetts, 1706. január 17. – Philadelphia, Pennsylvania, 1790. április 17.) amerikai polihisztor, államférfi, diplomata, feltaláló, író, természettudós, politikai filozófus, nyomdász. Az USA „alapító atyáinak” egyikeként segített az Egyesült Államok Függetlenségi Nyilatkozatának megszövegezésében, és annak egyik aláírója volt.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (189, "Henry Fielding", "<p>(Sharpham, Somerset, 1707. április 22. – Lisszabon, 1754. október 8.) angol regényíró, jogász, újságíró.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (190, "Johann Gottlieb Fichte", "<p>(Rammenau, 1762. május 19. – Berlin, 1814. január 29.) német filozófus. A német idealizmus (metafizikai idealizmus) megalapítója és Schelling, illetve Hegel mellett legfontosabb képviselője.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (191, "Nicolas Chamfort", "<p><strong>Sébastien-Roch Nicolas de Chamfort</strong> (6 aprilie 1740 - 13 aprilie 1794) scriitor francez din perioada Revoluției franceze, căreia i-a fost susținător.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (192, "Thomas Carlyle", "<p>(4 decembrie 1795 - 5 februarie 1881) eseist, autor satiric și istoric scoțian, a cărui operă a fost foarte apreciată în epoca victoriană. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (193, "George Byron", "<p><strong>George Gordon Noel Byron</strong>, <strong>Lord of Newstead</strong> (London, 1788. január 22. – Meszolóngi, Görögország, 1824. április 19.) Percy Bysshe Shelley és John Keats mellett az angol romantikus költészet egyik legismertebb képviselője.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (194, "Robert Burns", "<p>(1759. január 25. – Dumfries, 1796. július 21.) skót költő, dalszerző. Skócia nemzeti költőjének tartják.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (195, "Bonaparte Napoleon", "<p>(Ajaccio, Korzika, Francia Királyság, 1769. augusztus 15. – Szent Ilona-sziget, Nagy-Britannia, 1821. május 5.) francia tábornok, hadvezér, politikus, az Első Francia Köztársaság konzulja 1799 és 1804 között, majd a Francia Birodalom császára <strong>I. Napóleon</strong> néven először 1804 és 1814, majd száz nap erejéig 1815 márciusa és júniusa között. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (196, "Pierre-Augustin Caron de Beaumarchais", "<p>(Párizs, 1732. január 24. – Párizs, 1799. május 18.) francia író, színműíró.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (197, "Voltaire", "<p><strong>François-Marie Arouet</strong> (Párizs, 1694. november 21. – Párizs, 1778. május 30.) a francia felvilágosodás egyik legnagyobb alakja, író, költő és filozófus. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (198, "Jonathan Swift", "<p>(Dublin, 1667. november 30. – Dublin, 1745. október 19.) angol születésű ír szatirikus író, esszé- és politikai pamfletíró, az irodalmi Scriblerus Klub tagja, anglikán lelkész.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (199, "Baruch Spinoza", "<p><strong>Benedictus (Baruch) Spinoza</strong> (Amszterdam, 1632. november 24. – Hága, 1677. február 21.) a felvilágosodás korának racionalista filozófusa, a panteizmus, illetve a korai materializmus képviselőjének is tekinthetjük.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (200, "François de La Rochefoucauld", "<p>(Párizs, 1613. szeptember 15. – Párizs, 1680. március 17.) kalandos életű francia herceg, író és moralista, akinek neve mindenekelőtt Maximes (Maximák) című aforizmakötetének köszönhetően maradt fenn. <strong>Descartes</strong>, <strong>Pascal </strong>és <strong>La Bruyère</strong> mellett ő volt a 17. század negyedik nagy francia gondolkodója.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (201, "Jean Racine", "<p>(La Ferté-Milon, 1639. december 22. – Párizs, 1699. április 21.) francia író, drámaíró.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (202, "Alexander Pope", "<p>(London, 1688. május 21. – Twickenham, 1744. május 30.) angol költőt általánosan a 18. század első felének legnagyobb költőjeként tartják számon.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (203, "Blaise Pascal", "<p>(Clermont-Ferrand, 1623. június 19. – Párizs, 1662. augusztus 19.) francia matematikus, fizikus, vallásfilozófus, teológus, moralista és vitatkozó. Jelentőset alkotott a fizika, a matematika, a teológia, a filozófia és az irodalom témakörében is. Hozzájárult a természettudományok fejlődéséhez, mechanikus számológépet szerkesztett, megalapozta a projektív geometriát, kidolgozta másokkal közösen a valószínűség matematikai elméletét. Tanulmányozta a folyadékokat és tisztázta a vákuum és a nyomás fogalmait. A nyomás mértékegysége az ő munkásságának tiszteletére lett pascal. A gondolkodásnak és a gyakorlati kísérletek tényadatainak tulajdonított döntő szerepet tudományos munkájában. A poitou-i mocsarak lecsapolásában szakértőként vett részt.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (204, "Isaac Newton", "<p><strong>Sir Isaac Newton </strong>(Woolsthorpe Manor, 1642. december 25. – Kensington, 1727. március 20.) angol fizikus, matematikus, csillagász, filozófus és alkimista; az újkori történelem egyik kiemelkedő tudósa.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (205, "John Milton", "<p>(London, 1608. december 9. – Chalfont, 1674. november 8.) angol költő, politikus, a barokk irodalom egyik legnagyobb alakja. Erőteljes szónoki prózája és költészetének választékossága hatalmas hatást gyakorolt a 18–19. század irodalmára. Költeményei mellett Milton több röpiratot publikált az emberi jogok és a szabad vallásgyakorlás védelmében, és Oliver Cromwell titkáraként aktív részt vállalt az angol polgári forradalomban.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (206, "John Locke", "<p>(Wrington, Somerset, 1632. augusztus 29. – Oates, Essex, 1704. október 28.) angol filozófus, orvos és politikus. Az angol empirizmus, illetve a korai materializmus egyik fő képviselője, egyike azon gondolkodóknak, akik a tapasztalatot teszik meg a filozófia alapelvévé: minden tudás a tapasztalattól függ és annak ellenőrzése alatt áll. Az államról, a vallási toleranciáról és a pedagógiáról vallott nézetei nagy befolyással voltak a felvilágosodásra és a politikai liberalizmusra. Ötvenes éveitől lett híres ember, de hírnevét végül filozófusként érdemelte ki, műveinek, elsősorban az 1689-ben megjelent Értekezés az emberi értelemről című munkájának a publikálása révén.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (207, "Gottfried Wilhelm Leibniz", "<p>(Lipcse, 1646. július 1. – Hannover, 1716. november 14.) német polihisztor: jogász, diplomata, történész, matematikus, fizikus és filozófus. Nagy Frigyes azt mondta róla: „önmagában egy akadémia”.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (208, "Jean de La Fontaine", "<p>(Château-Thierry, 1621. július 8. – Párizs, 1695. április 13.) francia író és költő.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (209, "Daniel Defoe", "<p><strong>Daniel Foe</strong> (London, 1660. szeptember 13. – London, 1731. április 24.) angol író, újságíró. Az angol felvilágosodás egyik nagy alakja. Az első angol regényíró. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (210, "Pierre Corneille", "<p>(Rouen, 1606. június 6. – Párizs, 1684. október 1.) francia klasszicista drámaíró, a francia klasszicizmus első jelentős tragédia írója. A komédia műfajában is örökérvényűt alkotott.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (211, "Jean de La Bruyére", "<p>(Párizs, 1645. augusztus 16. – Versailles, 1696. május 10.), francia író, esszéíró, moralista.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (212, "Baltasar Gracián", "<p><strong>Baltasar Gracián y Morales </strong>(1601. január 8. – 1658. december 6.) spanyol barokk prózaíró.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (213, "Joseph Addison", "<p>(Milston, Wiltshire, 1672. május 1. – Kensington, 1719. június 17.) brit esszéista, költő, drámaíró, újságíró, politikus.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (214, "Ben Jonson", "<p><strong>Benjamin Jonson</strong> (Westminster, London, 1572. (kb.) június 11. – London, 1637. augusztus 6.) angol drámaíró, költő és színész volt, <strong>William Shakespeare</strong> kortársa.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (215, "Galileo Galilei", "<p>(Pisa, 1564. február 15. – Arcetri, 1642. január 8.) itáliai fizikus, csillagász, matematikus, természettudós. A fizikában az elsők között honosította meg a kísérleteket és méréseket, új módszereket adva ezzel a fizikának (és a többi természettudománynak). A csillagászatban ugyancsak az elsők között használt távcsöveket csillagászati jelenségek és égi objektumok megfigyelésére. Eredményei ellentmondtak az uralkodó ptolemaioszi, geocentrikus világképnek, ezért összeütközésbe került a katolikus egyházzal. Az inkvizíció 1633-ban a könyveit betiltotta, Galileit tanainak megtagadására kényszerítette, és házi őrizetben kellett élnie 1642-ben bekövetkezett haláláig. A katolikus egyház csak 1992-ben érvénytelenítette az ítéletet.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (216, "René Descartes", "<p>(La Haye-en-Touraine (ma Descartes (Indre-et-Loire)), Franciaország, 1596. március 31. – Stockholm, 1650. február 11.) francia filozófus, természetkutató és matematikus. Vele kezdődik az újkori filozófia antropológiai fordulata, valamint ő volt az analitikus geometria egyik megalapítója. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (217, "Lope De Vega", "<p><strong>Félix Lope de Vega y Carpio</strong> (25 noiembrie 1562, Madrid, Province of Madrid, Spania – 27 august 1635, Madrid, Province of Madrid, Spania) scriitor spaniol, considerat unul din cei mai importanți poeți și dramaturgi ai „Secolului de aur” al literaturii spaniole.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (218, "Johannes Amos Comenius", "<p>születési cseh nyelvén <strong>Jan Amos Komenský</strong> (Nivnice, 1592. március 28. – Amszterdam, 1670. november 15.) cseh pedagógus és író, „a nemzetek tanítója”. Ő tekinthető az első modern, haladó szellemiségű európai pedagógusnak. A cseh-morva testvérek püspöki tisztét is betöltötte.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (219, "Sebastian Brant", "<p>(1457/1458 – 1521. május 10.) német humanista és szatirikus.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (220, "Pierre de Ronsard", "<p>(Couture-sur-Loir, 1524. szeptember 11. – La Riche, 1585. december 27.) francia költő, a Pléiade nevű irodalmi csoport vezéralakja, a reneszánsz legnagyobb francia költője.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (221, "Epikurosz", "<p>(Számosz sziget, Kr. e. 341 decembere – Athén, Kr. e. 270), görög atomista filozófus, Démokritosz természetfilozófiájának követője. Megvetette korának összes gondolkodóját, kivételt csupán Anaxagorásszal és Démokritosszal tett, ugyanis fontosnak tartotta, hogy autodidaktának tekintsék és nem ismert el semmilyen kapcsolatot saját bölcselete és mások gondolatai között.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (222, "Menandru", "<p>(aprox. 342 – 291 î.Hr.) poet dramatic grec și principalul reprezentant al comediei noi (a cărei perioadă cuprinde aproximativ intervalul 336 - 250 î.Hr.). Aduce importante inovații comediei clasice, renunțând la cor și mărind numărul personajelor. A compus peste o sută de comedii, prima realizând-o la vârsta de 22 de ani.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (223, "Caecilius Statius", "<p>(Kr. e. 220 körül - Kr. e. 166 körül) római komikus költő.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (224, "Publius Terentius Afer", "<p>(Karthago, Kr. e. 185 körül – ?, Kr. e. 159) római költő, vígjátékíró.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (225, "Marcus Tullius Cicero", "<p>(3 January 106 BC – 7 December 43 BC) Roman statesman, lawyer, scholar, philosopher, and academic skeptic, who tried to uphold optimate principles during the political crises that led to the establishment of the Roman Empire.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (226, "Quintus Horatius Flaccus", "<p>(Venusia, ma Venosa, Olaszország, Kr. e. 65. december 8. – Róma, Kr. e. 8. november 27.) római költő.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (227, "Titus Livius", "<p>(59 î.Hr., Padova, Italia – 17 d.Hr., Padova, Italia) istoric roman, autor al unei monumentale istorii a Romei, Ab urbe condita (De la fondarea Romei).</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (228, "Publius Ovidius Naso", "<p>(Sulmo, ma Sulmona, Olaszország, i. e. 43. március 20. – Tomis [avagy Tomi], ma Konstanca, Románia, i. sz. 17 vége vagy 18) a római aranykor költője. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (229, "Lucius Annaeus Seneca", "<p>Édesapjától megkülönböztetendő, <strong>ifjabb Senecá</strong>nak (<strong>Seneca Minor</strong>) is nevezik. (Córdoba, Kr. e. 4 körül – Róma, Kr. u. 65. április 12.) római sztoikus filozófus, drámaíró és államférfi.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (230, "Vergiliu (Publius Vergilius Maro)", "<p>(15 octombrie 70 î.Hr., Andes/azi Virgilio – 19 î.Hr., Brundisium/azi Brindisi) poet latin, autor al epopeii în versuri Aeneis („Eneida”), considerată epopeea națională a romanilor. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (231, "Epiktétosz", "<p>(Hierapolisz, i. sz. 55 körül – Nikopolisz, i. sz. 120 körül) görög sztoikus filozófus.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (232, "Juvenalis", "<p><strong>Decimus Iunius Iuvenalis</strong>, <strong>Iuvenalis </strong>(Aquinum, 50/60 körül – 130 körül) római költő, a római irodalom három nagy szatíraírójának (Horatius, Persius, Iuvenalis) egyike. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (233, "Arbiter Petronius", "<p>(27, – Cumae, 66) ókori római író.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (234, "Plutarkhosz", "<p>(Khairóneia, 46/48 – Khairóneia, 125/127) görög közép-platonista krónikás, életrajzíró, akit, miután római polgár lett, Lucius Mestrius Plutarchus (görögül: Μέστριος Πλούταρχος) névvel illették. Az ókori görög és latin nyelvű nagy jelentőségű történetírók közé tartozik. Ismertségét fő műve, a „Párhuzamos életrajzok” (gör. Bioi paralelloi) hozta meg számára, melyben a mitológiai időktől kezdődően egészen koráig fontos közszereplők, törvényhozók, államférfiak, hadvezérek életét írja le huszonkét kötetben.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (235, "Publius Cornelius Tacitus", "<p>(Kr. u. 55 vagy 56 – valószínűleg 117 és 120 között) római történetíró.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (236, "Lucius Apuleius Platonicus", "<p><b>Lucius Apuleius Platonicus</b> (123/125 d. Hr. - 180 d. Hr.) scriitor latin. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (237, "Damaszkuszi Szent János", "<p>(Damaszkusz, 645 körül – Mar Saba-kolostor, 749. december 4.) szír keresztény teológus, szerzetes, a katolikus egyház utolsó egyházatyája, XIII. Leó pápa 1890-ben emelte az egyháztanítók sorába.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (238, "Pierre Abélard", "<p>(Le Pallet, 1079. – Chalon-sur-Saône, 1142. április 21.) francia skolasztikus filozófus, teológus és himnuszköltő. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (239, "Roger Bacon", "<p>(1219 - 1292), supranumit „Doctor mirabilis”, a fost unul dintre cei mai mari filosofi ai Evului Mediu.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (240, "Gárdonyi Géza", "<p>született <strong>Ziegler Géza</strong> (Agárdpuszta, 1863. augusztus 3. – Eger, 1922. október 30.) magyar író, költő, drámaíró, újságíró, pedagógus, a Magyar Tudományos Akadémia tiszteleti tagja. A 19–20. századforduló magyar irodalmának népszerűségében máig kiemelkedő alakja. Korának sajátos figurája, egyik irodalmi körhöz sem sorolható tagja volt. Életműve átmenetet képez a 19. századi romantikus, anekdotikus történetmesélés és a 20. századdal születő Nyugat-nemzedék szecessziós, naturalista-szimbolista stíluseszménye között.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (241, "Fekete István", "<p>(Gölle, 1900. január 25. – Budapest, 1970. június 23.) József Attila-díjas magyar író, számos regény, ifjúsági könyv és állattörténet írója. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (242, "Rejtő Jenő", "<p>született <strong>Reich</strong>, írói álnevei: <strong>P. Howard</strong>, <strong>Gibson Lavery</strong> (Budapest, Erzsébetváros, 1905. március 29. – Jevdakovo, Kamenkai járás, Voronyezsi terület, oroszországi megszállt övezet, Szovjetunió, 1943. január 1.) magyar író, kabaré- és színpadi szerző, filmíró, a magyar szórakoztató irodalom népszerű képviselője és megújítója.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (243, "Hofi Géza", "<p>született <strong>Hoffmann </strong>(Budapest, 1936. július 2. – Budapest, 2002. április 10.) Kossuth- és kétszeres Jászai Mari-díjas magyar humorista, előadóművész, színművész, érdemes és kiváló művész, a Halhatatlanok Társulatának örökös tagja, a magyar kabaré történetének egyik leghíresebb és legtöbbet emlegetett alakja. A magyar kabaréműfaj külön úton járó, kiemelkedő népszerűségű mestere, aki önálló produkcióival egyéni stílust teremtett.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (244, "Platón", "<p>(régiesen Plátó, eredetileg: Arisztoklész; sz.: Kr. e. 427 körül, Athén vagy Aigina – † Kr. e. 347, Athén), ókori görög filozófus, iskolaalapító. Hatása jelentős volt az ókori és a középkori filozófiára, művei manapság is viták és filozófiai vizsgálódások tárgyát képezik. Munkásságának nemcsak társadalomtudományi, hanem irodalmi értéke is nagy jelentőséggel bír.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (245, "Publilius Syrus", "<p>(Kr. e. 85 körül – Kr. e. 43 után), római mimusszerző és mimusszínész.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (246, "Albius Tibullus", "<p><strong>Tibullus </strong>(Gabii, Kr. e. 54 körül – Róma, Kr. e. 19) ókori római elégiaköltő.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (247, "Müller Péter", "<p>(Budapest, 1936. december 1. –) Kossuth- és József Attila-díjas magyar író, dramaturg, forgatókönyvíró, előadó, a hazai spirituális irodalom egyik legismertebb alakja.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (248, "Giuseppe Mazzini", "<p>(22 June 1805 – 10 March 1872) Italian politician, journalist, and activist for the unification of Italy (Risorgimento) and spearhead of the Italian revolutionary movement.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (249, "Lao Tzu", "<p>semilegendary ancient Chinese Taoist philosopher. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (250, "William Ellery Channing", "<p>(April 7, 1780 – October 2, 1842) the foremost Unitarian preacher in the United States in the early nineteenth century and, along with <strong>Andrews Norton</strong> (1786–1853), one of Unitarianism's leading theologians.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (251, "Confucius", "<p>Chinese: <strong>孔夫子</strong>; pinyin: <strong>Kǒng Fūzǐ</strong>, '<strong>Master Kǒng</strong>'; or commonly <strong>孔子</strong>; <strong>Kǒngzǐ</strong>; (c. 551 – c. 479 BCE) Chinese philosopher and politician of the Spring and Autumn period who is traditionally considered the paragon of Chinese sages.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (252, "Lucy A. Mallory", "<p>née, <strong>Rose </strong>(c. 1843 or 1856—September 4, 1920) American writer, publisher, editor, and spiritualist.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (253, "Henri-Frédéric Amiel", "<p>(27 September 1821 – 11 May 1881) Swiss moral philosopher, poet, and critic.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (254, "Solon", "<p>(c. 630 – c. 560 BC) Athenian statesman, constitutional lawmaker and poet.  </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (255, "Oprah Winfrey", "<p><strong>Oprah Gail Winfrey</strong> (January 29, 1954), an American talk show host, television producer, actress, author, and philanthropist.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (256, "Ludwig Wittgenstein", "<p><strong>Ludwig Josef Johann Wittgenstein </strong>(26 April 1889 – 29 April 1951) Austrian philosopher who worked primarily in logic, the philosophy of mathematics, the philosophy of mind, and the philosophy of language. He is considered by some to be the greatest philosopher of the 20th century.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (257, "Black Elk", "<p><strong>Heȟáka Sápa</strong> (December 1, 1863 – August 19, 1950), a wičháša wakȟáŋ ('medicine man, holy man') and heyoka of the Oglala Lakota people.  </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (258, "James Redfield", "<p>American author, lecturer, screenwriter, and film producer.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (259, "Abraham Hicks", "<p><strong>Esther Hicks</strong> (née <strong>Weaver</strong>, born March 5, 1948) is an American inspirational speaker, channeler, and author.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (260, "Becca Lee", "<p>Contemporary and paranormal romance author.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (261, "Sadhguru", "<p><strong>Jagadish Vasudev</strong> (3 September 1957) founder and head of the Isha Foundation, based in Coimbatore, India.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (262, "Esop", "<p>(n. 620 î.Hr. - d. 560 î.Hr.) scriitor antic grec, cunoscut pentru fabulele lui. Se crede că a fost sclav, mai târziu eliberat de stăpânul său. Fabulist și filosof, atunci când îl sfătuia corect pe stăpânul său, era eliberat pe o perioadă scurtă de vreme. Multe fabule ale sale s-au păstrat până în prezent.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (263, "Anakharszisz", "<p>szkíta költő és író, hagyomány szerint a hét bölcs egyike – az i. e. 6. században élt.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (264, "Lao Zi", "<p>chineză: <strong>老子</strong>, pinyin: <strong>Lǎozǐ</strong>; alte variante de transcriere <strong>Laozi</strong>, <strong>Lao Tse</strong>, <strong>Laotze </strong>sau <strong>Lao Tzu</strong>, numele se traduce prin <strong>Bătrânul Maestru</strong> - filozof chinez, a cărui naștere este cel mai probabil databilă în jurul veacului VI înainte de Hristos. Este figura fondatoare a taoismului și autorul cărții de bază a acestuia, Tao Te Ching - Cartea Căii și a Virtuții (Tao Te King, Dàodéjīng, chineză tradițională: 道德經 ). Textul însă a fost stabilit, se pare, abia în secolul IV î.Hr.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (265, "Teognis din Megara", "<p>poet liric grec activ în aproximativ secolul al VI-lea î.Hr. Lucrarea care i se atribuie constă în poezie gnomică destul de tipică vremii, prezentând maxime etice și sfaturi practice despre viață.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (266, "Aristip", "<p>Aristip din Cirene (cca. 435-350 î.d.Hr.) unul din fondatorii școlii filozofice cirenaice.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (267, "Sofocle", "<p>(cca. 496 î.Hr. - d. 406 î.Hr.) poet tragic grec.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (268, "Iulius Cezar", "<p><strong>Gaius Iulius Caesar</strong> (12 iulie 100 î.Hr., Roma, Republica Romană – 15 martie 44 î.Hr., Teatrul lui Pompeius, Republica Romană) conducător politic și general roman și una dintre cele mai influente și mai controversate personalități din istorie.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (269, "Seneca cel Bătrân", "<p><strong>Lucius Annaeus Seneca cel Bătrân</strong> (c. 54 î.Hr. - c. 39 d.Hr.), cunoscut și sub numele de <strong>Seneca Retoricianul</strong>, scriitor roman, născut dintr-o bogată familie de cavaleri din Corduba, Hispania.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (270, "Phaedrus", "<p>(Pieria, Makedónia, Kr. e. 15 körül – Kr. u. 55 körül) trák származású ókori római meseköltő.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (271, "Marcus Annaeus Lucanus", "<p>(3 noiembrie 39 d.Hr - 30 aprilie 65 d.Hr.) poet roman, născut în Corduba (azi Cordoba), în Hispania Baetica.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (272, "Petronius Arbiter", "<p>(27, – Cumae, 66) ókori római író. Nero korában élt.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (273, "Pliniu Cel Bătrân (Gaius Plinius Secundus)", "<p><strong>Gaius Plinius Secundus</strong>, sau <strong>Plinius Maior</strong> (23, Novum Comum (Como, Italia) - 24 august 79, Stabies) un important și renumit erudit al Imperiului Roman.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (274, "Pliniu Cel Tânăr", "<p>(n. 61, Como, Italia - d. 113, Bitinia, Roma Antică), scriitor și administrator roman.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (275, "Quintilian", "<p><strong>Marcus Fabius Quintilianus </strong>(35 - 100) retor și pedagog roman, fiind, alături de Cicero, unul dintre renumiții oratori și scriitori latini. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (276, "Quintus Curtius Rufus", "<p>Istoric roman, probabil din secolul I, autor al singurei sale lucrări cunoscute și singura care a supraviețuit, Historiae Alexandri Magni</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (277, "Ioan Gură de Aur", "<p>cunoscut și ca <strong>Ioan Hrisostom</strong> (circa 347, Antiohia – 14 septembrie 407), arhiepiscop de Constantinopol, una din cele mai importante figuri ale patrologiei creștine, considerat sfânt deopotrivă în Biserica Răsăriteană și în Biserica Apuseană, care îl venerează cu titlul de doctor al Bisericii.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (278, "Vasile cel Mare", "<p>(329/330 d. Hr., Kayseri, Imperiul Roman - 379 d. Hr., Kayseri, Imperiul Roman) arhiepiscop din secolul al IV-lea, considerat doctor al Bisericii și unul din cei trei arhiepiscopi de seamă, răsăriteni, ai Bisericii Ortodoxe, alături de Grigore de Nazianz („Teologul”) și Ioan Gură de Aur.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (279, "Iustinian I (cel Mare)", "<p>(11 mai 482 d.Hr., Tauresium⁠, Imperiul Roman de Răsărit – 14 noiembrie 565 d.Hr., Constantinopol, Imperiul Roman de Răsărit) împărat roman în perioada 527-565.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (280, "Vishachadatta", "<p>poet și dramaturg indian sanscrit.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (281, "Rūdakī", "<p><strong>Abu Abd Allah Ja'far ibn Muhammad ibn Hakim ibn Abd al-Rahman ibn Adam al-Rudhaki al-Sha'ir al-Samarqandi.</strong> (858 - 940/41) poet, cântăreț și muzician persan.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (282, "Vasile I Macedoneanul", "<p>(c.811 - 29 august, 886) împărat bizantin între 867 și 886, întemeietorul dinastiei macedonene și inițiatorul renașterii artei bizantine.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (283, "Al-Ma'arri", "<p><b>Abu al-'Alā Ahmad ibn 'Abd Allāh ibn Sulaimān al-Tanūkhī al-Ma'arri </b>(973 d.Hr., Maarrat al-Numan, Guvernoratul Idlib, Siria – 1057 d. Hr., Maarrat al-Numan, Guvernoratul Idlib, Siria) poet și erudit arab din Siria, personalitate importantă a literaturii în limba arabă.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (284, "Avicenna", "<p> (Abu Ali Al-Husayn ibn 'Abd Allah ibn Sina )</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (285, "Firdoùsi", "<p><strong>Hakīm Abu l-Qāsim Firdawsī Tūsī</strong> (935 - 1025) poet persan care s-a impus în literatura țării sale și în cea universală prin epopeea Șah-name - Cartea regilor, alcătuită din cca. 50.000 de distihuri, veritabilă panoramă a trecutului, real și imaginar, al popopurului persan, începând dintr-un trecut cețos, legendar și ajungând până la cucerirea arabă.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (286, "Murasaki Shikibu", "<p>(n. 970 d.Hr., Kyoto, Japonia – d. 1016, Kyoto, Japonia) scriitoare japoneză, autoarea romanului medieval Genji Monogatari scris în epoca Heian, în jurul anului 1000 d.H.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (287, "Vladimir al II-lea Monomahul", "<p><strong>Vladimir Vsevolodovici Monomah</strong>, nume creștin: <strong>Vasili </strong>sau <strong>Basileios</strong>; (1053, Pereiaslav, Marele Ducat al Lituaniei, Polonia-Lituania – 19 mai 1125, Kiev, Rusia Kieveană) a domnit ca mare principe a Rusiei Kieveane din 1113 până în 1125. Este considerat un sfânt în Biserica Ortodoxă și este sărbătorit pe 6 mai.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (288, "Omar Khayam", "<p><strong>Ghiās od-Dīn Abul-Fatah Omār ibn Ibrāhīm Haiām Nișābūrī</strong> (18 mai 1048, Nișapur, Persia - 4 decembrie 1131) poet, matematician, filosof și astronom persan.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (289, "Shams al-Din al-Samarqandi", "<p><strong>Shams al-Dīn Muḥammad ibn Ashraf al-Ḥusaynī al-Samarqandī</strong> (c. 1250 - c 1310) astronom și matematician din secolul al XIII-lea din Samarkand, în prezent în Uzbekistan.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (290, "Anvari", "<p>Awhad ad-Din 'Ali ibn Mohammad Khavarani (1126–1189) poet persan, cunoscut în Europa sub numele latinizat de Anvari.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (291, "Șota Rustaveli", "<p>(cca. 1160-1166 Rustavi, Meskheti, Regatul Georgiei - 1216 Ierusalim)  poet georgian din secolele XII-XIII.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (292, "Hafez", "<p>Khwāja Šamsu d-Dīn Muḥammad Hāfez-e Šīrāzī</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (293, "Djami", "<p><strong>Maulānā Nuro'd-Dīn Abdo'r Rahmān Djāmi </strong>(18 august 1414 - 19 noiembrie 1492) poet și erudit persan, adept al sufismului. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (294, "Michelangelo Buonarroti", "<p>(6 martie 1475, Caprese Michelangelo, Republica Florentină – 18 februarie 1564, Roma, Statele Papale) alături de Leonardo da Vinci, cel mai important artist în perioada de vârf a Renașterii Italiene. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (295, "Jean de la Fontaine", "<p>(8 iulie 1621, Château-Thierry, Franța – 13 aprilie 1695, Paris) poet, dramaturg și prozator francez.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (296, "Petru I al Rusiei", "<p><strong>Petru cel Mare</strong> (9 iunie 1672, Moscova, Țaratul Rusiei – 8 februarie 1725, Sankt Petersburg, Imperiul Rus) a condus Rusia din 7 mai (27 aprilie) 1682 până la moartea sa.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (297, "Piotr Ceaadaev", "<p><strong>Piotr Iakovlevici Ceaadaev</strong> (7 iunie 1794, Moscova, Imperiul Rus – 26 aprilie 1856, Moscova, Imperiul Rus) filozof și literat în egală măsură.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (298, "Denis Diderot", "<p>(5 octombrie 1713, Langres – 31 iulie 1784, Paris) filosof și scriitor francez.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (299, "William Hazlitt", "<p>(10 aprilie 1778 - 18 septembrie 1830) eseist, critic literar și de teatru, pictor, comentator social și filosof englez.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (300, "Nikolai Ivanovici Novikov", "<p>(8 mai 1744, Avdot'ino - 12 august 1818, Moscova) jurnalist rus, om de litere și ezoterist.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (301, "Alekszandr Nyikolajevics Ragyiscsev", "<p>(Moszkva vagy Verhnyeje Obljazovo, 1749. augusztus 20. – Szentpétervár, 1802. szeptember 12.) orosz író, a 18. század végének kiemelkedő alkotója.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (302, "Richard Brinsley Sheridan", "<p><strong>Richard Brinsley Butler Sheridan</strong> (30 octombrie 1751 - 7 iulie 1816) satirist, politician, dramaturg, poet irlandez și proprietar pe termen lung al Teatrului Regal din Londra, Drury Lane.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (303, "Grigori Savvici Skovoroda", "<p>(3 decembrie 1722 - 9 noiembrie 1794) poet, filozof, compozitor ucrainean.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (304, "Alekszandr Vasziljevics Szuvorov", "<p>(Moszkva, 1729. vagy 1730. november 24. – Szentpétervár, 1800. május 18.) orosz hadvezér, tábornok, generalisszimusz, Itália hercege, az újkor egyik legnagyobb stratégája. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (305, "Konstantin Dmitrievici Balmont", "<p>(15 iunie 1867 - 23 decembrie 1942) poet rus, stabilit din 1921 în Franța.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (306, "Jack London", "<p><strong>John Griffith Chaney </strong>(12 ianuarie 1876, California, SUA – 22 noiembrie 1916, Glen Ellen, Comitatul Sonoma, California, SUA), scriitor și jurnalist american.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (307, "Karl Marx", "<p>(5 mai 1818, Trier, Regatul Prusiei – 14 martie 1883, Londra, Regatul Unit al Marii Britanii și Irlandei) filozof german, istoric, economist, sociolog și jurnalist, întemeietor împreună cu Friedrich Engels al teoriei socialismului științific, teoretician și lider al mișcării muncitorești.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (308, "Vissarion Grigorievici Belinski", "<p>(11 iunie 1811 - 7 iunie 1848) filozof, critic literar rus, fondator al programului estetic al realismului în literatura rusă 'Școala naturală'.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (309, "Nikolai Berdeaev", "<p>(n. 6/18 martie 1874, Kiev, Imperiul Rus – d. 24 martie 1948, Clamart, Île-de-France, Franța) filosof rus care s-a ocupat, între altele, de filosofia religiei. Este un reprezentant al existențialismului.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (310, "Booker T. Washington ", "<p><strong>Booker Taliaferro Washington</strong> (5 aprilie 1856, Hale's Ford, Comitatul Franklin, Virginia, Virginia, SUA – 14 noiembrie 1915, Tuskegee⁠, Alabama, SUA) educator american, autor, orator și consilier al mai multor președinți ai Statelor Unite.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (311, "Valeri Briusov", "<p><strong>Valeri Iakovlevici Briusov</strong> (1 decembrie 1873 - 9 octombrie 1924) poet, prozator, dramaturg, translator, critic și istoric, unul din principalii reprezentanți ai curentului simbolist din literatura rusă.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (312, "Nikolai Cernîșevski", "<p><strong>Nikolai Gavrilovici Cernîșevski </strong>(n. 12 iulie 1828 - d. 17 octombrie 1889) luptător social, filozof materialist, scriitor, jurnalist și critic literar rus.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (313, "Agatha Christie", "<p><strong>Agatha Mary Clarissa</strong>, <strong>Lady Mallowan</strong>, <strong>DBE</strong>, <b>Mary Westmacott, </b>născută <strong>Miller </strong>(15 septembrie 1890, Ashfield, Anglia, Regatul Unit al Marii Britanii și Irlandei - 12 ianuarie 1976, Winterbrook, Cholsey, Anglia, Regatul Unit) scriitoare engleză de romane, povestiri scurte și piese de teatru polițiste.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (314, "Pavel Aleksandrovici Florenski", "<p>(21 ianuarie 1882 - 8 decembrie 1937)  teolog ortodox rus, preot, filozof, matematician, fizician, inginer electrician, inventator, polihistor.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (315, "Alekszandr Grin", "<p>(Szlobodszkoj, Vjatkai kormányzóság, Oroszország, 1880. augusztus 23. – Sztarij Krim, Krím, 1932. július 8.) orosz író, a mágikus realizmus egyik első képviselője.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (316, "Stanisław Jerzy Lec", "<p>Baronul <strong>Stanisław Jerzy de Tusch-Letz</strong> (6 martie 1909 - 7 mai 1966), aforist și poet polonez.  </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (317, "Konstantin Gheorghevici Paustovski", "<p>(31 mai 1892, Moscova, Imperiul Rus - 14 iulie 1968, Moscova, URSS) scriitor rus și sovietic.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (318, "Vasili Rozanov", "<p><strong>Vasili Vasilievici Rozanov</strong> (2 mai 1856 - 5 februarie 1919), unul dintre cei mai contoversați scriitori și filosofi ai perioadei antebelice din Rusia.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (319, "Lev Șestov", "<p>(31 ianuarie 1866 - 19 noiembrie 1938), născut Yehuda Leib Shvartsman, existențialist și filozof religios rus. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (320, "Vladimir Vernadski", "<p><strong>Vladimir Ivanovici Vernadski</strong> (12 martie 1863, Sankt Petersburg, Imperiul Rus – 6 ianuarie 1945, Moscova, RSFS Rusă, URSS) geochimist și mineralogist sovietic, considerat unul dintre fondatorii geochimiei, a biogeochimiei și a radiogeologiei.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (321, "Alain", "<p><strong>Émile-Auguste Chartier </strong>(3 martie 1868, Mortagne-au-Perche, Normandia de Jos, Franța - 2 iunie 1951, Le Vésinet⁠, Île-de-France, Franța) filozof, jurnalist și pacifist francez. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (322, "Nicolae Bagdasar", "<p>(5 februarie 1896, Roșiești, Vaslui – 21 aprilie 1971, București) filozof român, membru corespondent al Academiei Române din 1943.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (323, "Aurel Baranga", "<p><strong>Aurel Leibovici</strong> (20 iunie 1913, București – 10 iunie 1979, București) poet și dramaturg român de origine evreiască.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (324, "Gustavo Adolfo Bécquer", "<p>(17 februarie 1836, Sevilia, Spania - 22 decembrie 1870, Madrid, Spania) poet și scriitor romantic spaniol (mai ales de povestiri scurte), de asemenea dramaturg, cronicar literar și talentat la desen. Este considerat una dintre cele mai importante figuri ale literaturii spaniole.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (325, "Ludwig van Beethoven", "<p>(16 decembrie 1770, Bonn, Sfântul Imperiu Roman – 26 martie 1827, Viena, Imperiul Austriac) compozitor german, recunoscut ca unul din cei mai mari compozitori din istoria muzicii.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (326, "William Blake", "<p>(28 noiembrie 1757, Londra, Regatul Marii Britanii – 12 august 1827, Londra, Regatul Unit al Marii Britanii și Irlandei) poet, vizionar, pictor și gravor englez. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (327, "Johan Bojer", "<p>(6 martie 1872 - 3 iulie 1959) scriitor norvegian.  </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (328, "Victor Cousin", "<p>(28 noiembrie 1792, Paris, Prima Republică Franceză – 14 ianuarie 1867, Cannes, Alpes-Maritimes, Franța) filozof francez, profesor la Universitatea Sorbona. A scris pe larg despre istoria filozofiei. A fost membru al Academiei Franceze din 1830.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (329, "Joseph Dietzgen", "<p>(9 decembrie 1828 - 15 aprilie 1888) filozof socialist, marxist și jurnalist german.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (330, "Halíl Dzsibrán", "<p><strong>Dzsibrán Halíl Dzsibrán bin Miháíl bin Szaad </strong>(Bisárrí, Oszmán Birodalom (ma: Libanon), 1883. január 6. – New York, 1931. április 10.) libanoni származású amerikai maronita keresztény költő, filozófus, képzőművész.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (331, "Llyod C. Douglass", "<p>(27 august 1877 - 13 februarie 1951) pastor și autor american.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (332, "William Faulkner", "<p><strong>William Cuthbert Faulkner</strong>, născut <strong>Falkner </strong>(25 septembrie 1897 – 6 iulie 1962) prozator american, laureat al Premiului Nobel pentru Literatură în anul 1949, unul dintre scriitorii reprezentativi ai literaturii americane din secolul XX. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (333, "André Gide", "<p>(Párizs, 1869. november 22. – Párizs, 1951. február 19.) francia író, esszéista.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (334, "Maxim Gorki", "<p>Alexei Maximovici Peșkov (28 martie 1868, Nijni Novgorod, Imperiul Rus – 18 iunie 1936, Gorki-10⁠, Q4414690⁠, RSFS Rusă, URSS), scriitor rus/sovietic, fondator al realismului socialist în literatură și activist politic.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (335, "Martin Heidegger", "<p>(26 septembrie 1889, Meßkirch, Baden – 26 mai 1976, Freiburg im Breisgau) unul din cei mai importanți filozofi germani din secolul al XX-lea.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (336, "Aldous Huxley", "<p>(Godalming, 1894. július 26. – Los Angeles, 1963. november 22.) brit író, költő és filozófus.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (337, "Jens Peter Jacobsen", "<p>(7 aprilie 1847 - 30 aprilie 1885) scriitor danez, inițiator al naturalismului în literatura daneză și precursor al decadentismului prin preocuparea pentru desăvârșirea formei artistice.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (338, "William James", "<p>(11 ianuarie 1842, New York – 26 august 1910, Tamworth, New Hampshire) psiholog și filozof american, considerat părintele psihologiei americane.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (339, "Søren Kierkegaard", "<p>(5 mai 1813, Copenhaga, Danemarca-Norvegia – 11 noiembrie 1855, Copenhaga, Danemarca) filozof, scriitor și teolog danez din secolul al XIX-lea.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (340, "Katherine Mansfield", "<p>Kathleen Mansfield Murry (născută Beauchamp la 14 octombrie 1888 la Wellington, Noua Zeelandă – d. 9 ianuarie 1923 la Fontainebleau, Franța) nuvelistă și poetă neozeelandeză de limbă engleză.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (341, "Jean-François Marmontel", "<p>(11 iulie 1723 - 31 decembrie 1799) istoric și scriitor francez.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (342, "Prosper Mérimée", "<p>(28 septembrie 1803, Paris, Franța - 23 septembrie 1870, Cannes) dramaturg, povestitor, istoric, arheolog francez, considerat creatorul nuvelei realiste franceze.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (343, "Michel de Montaigne", "<p>Michel Eyquem de Montaigne-Delecroix (28 februarie 1533 - 13 septembrie 1592) unul din cei mai importanți filosofi francezi ai Renașterii. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (344, "Robert Musil", "<p><strong>Robert Mathias Edler von Musil </strong>(6 noiembrie 1880, Klagenfurt am Wörthersee, Austria – 15 aprilie 1942, Geneva, Geneva, Elveția) scriitor austriac. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (345, "Petre P. Negulescu", "<p><strong>Petre Paul Negulescu</strong>, filosof și om politic român, membru titular al Academiei Române (1936).</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (346, "Nezami Ganjavi", "<p>(c. 1141-1209), Nizami Ganje'i,  Nizami,  sau Nezāmi, al cărui nume formal era Jamal ad-Dīn Abū Muḥammad Ilyās ibn-Yūsuf ibn-Zakkī, persan din secolul al XII-lea, poet sunnit musulman. Considerat cel mai mare poet epic romantic din literatura persană, care a adus un stil colocvial și realist epopeii persane.  Moștenirea sa este larg apreciată și împărtășită de Afganistan, Azerbaidjan, Iran, regiunea Kurdistan și Tadjikistan.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (347, "Friedrich Nietzsche", "<p><strong>Friedrich Wilhelm Nietzsche </strong>(15 octombrie 1844, Röcken - 25 august 1900, Weimar) unul dintre cei mai importanți filosofi europeni al secolului al XIX-lea, care a exercitat o influență remarcabilă, adesea controversată, asupra gândirii filosofice a generațiilor ce i-au urmat.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (348, "Novalis", "<p><strong>Friedrich Leopold von Hardenberg</strong> (2 mai 1772, Wiederstedt, Sfântul Imperiu Roman – 25 martie 1801, Weißenfels, Principatul Saxoniei, Sfântul Imperiu Roman) unul dintre cei mai mari poeți și prozatori germani, considerat cel mai de seamă reprezentant al Cercului romanticilor de la Jena. Influențat de Fichte, căruia i-a fost discipol, cultivă idealismul magic și extazul mistic.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (349, "Seán O'Casey", "<p><strong>John Casey</strong> (30 martie 1880 Dublin, Irlanda - 18 septembrie 1964 Torquay, England) dramaturg irlandez.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (350, "Emil George Papahagi", "<p>(5/18 septembrie 1907, Constantinopol - 11 aprilie f. a., București, România) eseist.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (351, "Perpessicius", "<p><strong>Dumitru S. Panaitescu </strong>(21 octombrie 1891, Brăila, România – 29 martie 1971, București, România) istoric și critic literar, folclorist, eseist și poet român, cercetător și editor al operei eminesciene, membru titular al Academiei Române.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (352, "Pindar", "<p>Pindarus (522 î.Hr. Cynoscephalae, Boeotia - 443 î.Hr. Argos) poet antic.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (353, "Marcel Proust", "<p>Valentin Louis Georges Eugène Marcel Proust (10 iulie 1871, Paris, A Treia Republică Franceză – 18 noiembrie 1922, Paris, A Treia Republică Franceză) romancier, eseist și critic francez.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (354, "Jules Renard", "<p>(22 februarie 1864, Châlons-du-Maine – 22 mai 1910, Paris) romancier și dramaturg francez.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (355, "Maximilien Marie de Robespierre", "<p>(6 mai 1758 – 28 iulie 1794) avocat și om de stat francez care a devenit una dintre cele mai cunoscute, influente și controversate figuri ale Revoluției franceze.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (356, "Saadi", "<p><strong>Abū-Muhammad Muslih al-Dīn bin Abdallāh Shīrāzī</strong> (1210 Șiraz, Persia – 1292) unul dintre principalii poeți persani.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (357, "Sextus Empiricus", "<p>filozof, astronom și medic grec, reprezentant al scepticismului târziu, care a trăit în jurul anului 200 d.Hr.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (358, "Symmachus", "<p>(342 körül – 402/403) római szenátor és rétor. A római szenátus konzervatív, pogány irányzatának vezető alakja, Szent Ambrus unokatestvére.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (359, "Konstantinos Tsatsos", "<p>(1 iulie 1899 - 8 octombrie 1987) diplomat, profesor de drept, savant și om politic grec.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (360, "Alexandru Vlahuță", "<p>(5 septembrie 1858, Pleșești, azi Alexandru Vlahuță, județul Vaslui — 19 noiembrie 1919, București) scriitor român, una dintre cele mai cunoscute cărți ale sale fiind „România Pitorească”.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (361, "Francis Bacon", "<p>(n. 22 ianuarie 1561, Londra - d. 9 aprilie 1626, Londra) filosof, om de știință, jurist, orator, autor englez. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (362, "Ambrose Bierce", "<p>(Meigs megye, Ohio, 1842. június 24. – Mexikó, 1914?) amerikai író, újságíró.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (363, "Adolphe de Chesnel", "<p>(24 septembrie 1791 - 3 octombrie 1862) este un poligraf, istoric și enciclopedist francez.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (364, "Oliver Goldsmith", "<p>(10 noiembrie 1728 - 4 aprilie 1774, Londra, Anglia) scriitor și medic irlandez. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (365, "Manuel González Prada", "<p>Jose Manuel de los Reyes González de Prada y Ulloa (Lima, 5 ianuarie 1844 - Lima, 22 iulie 1918) politician și anarhist peruan, critic literar și director al Bibliotecii Naționale din Peru.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (366, "Jean Auguste Dominique Ingres", "<p>(29 august 1780, Montauban, Midi-Pirinei, Franța – 14 ianuarie 1867, Paris, Franța) pictor francez neoclasic. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (367, "Paolo Mantegazza", "<p>(31 octombrie 1831 - 28 august 1910) neurolog, fiziolog și antropolog italian, cunoscut pentru cercetările sale experimentale asupra efectelor frunzelor de coca asupra psihicului uman. A fost, de asemenea, un autor de ficțiune.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (368, "Simion Mehedinți", "<p>(16 octombrie 1868, Soveja, Vrancea, România – 14 decembrie 1962, București, România) academician, geograf și geopolitician român.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (369, "Cornelius Nepos", "<p>(110 î.Hr. - c. 25 î.Hr.) biograf roman.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (370, "Marin Preda", "<p>(5 august 1922, Siliștea Gumești, Teleorman, România – 16 mai 1980, Mogoșoaia, Ilfov, România) unul dintre cei mai importanți romancieri români din perioada postbelică, membru al Partidului Comunist Român.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (371, "Properțiu", "<p><strong>Propertius</strong> (<strong>Sextus Aurelius Propertius Carus</strong>) (c. 47 î.e.n. Umbria -  c. 15 î.e.n.) poet elegiac latin.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (372, "Auguste Rodin", "<p>(Párizs, 1840. november 12. – Meudon, Île-de-France, 1917. november 17.) francia szobrászművész.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (373, "Madame de Staël", "<p>Anne Louise Germaine de Staël-Holstein (22 aprilie 1766, Paris, Regatul Franței – 14 iulie 1817, Paris, Restaurația franceză), scriitoare elvețiană de limbă franceză.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (374, "Ludwig Tieck", "<p>(31 mai 1773, Berlin - 28 aprilie 1853, Berlin) poet, dramaturg și scriitor romantic german. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (375, "Thornton Wilder", "<p><strong>Thornton Niven Wilder</strong> (17 aprilie 1897, Madison, Wisconsin, SUA – 7 decembrie 1975, New Haven, Connecticut, Connecticut, SUA) dramaturg și romancier american. A câștigat de trei ori Premiul Pulitzer — pentru romanul The Bridge of San Luis Rey și pentru două piese de teatru Our Town și The Skin of Our Teeth și National Book Award (SUA) pentru romanul The Eighth Day.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (376, "Vicente Blasco Ibañez", "<p>(29 ianuarie 1867, Valencia, Comunitatea Valenciană, Spania – 28 ianuarie 1928, Menton, Alpes-Maritimes, Franța) scriitor spaniol și ocazional regizor de film.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (377, "Constantin Brâncuși", "<p>(19 februarie 1876, Hobița, Peștișani, Gorj, România – 16 martie 1957, Paris, Franța) sculptor român cu contribuții covârșitoare la înnoirea limbajului și viziunii plastice în sculptura contemporană.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (378, "Remy de Gourmont", "<p><strong>Rémy de Gourmont </strong>(4 aprilie 1858, Bazoches-au-Houlme, Normandia de Jos, Franța – 27 septembrie 1915, Paris, Île-de-France, Franța) scriitor și critic literar francez. A fost cel mai important critic al mișcării simboliste.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (379, "John Keats", "<p>(31 octombrie 1795, Moorgate⁠, Anglia, Regatul Unit – 23 februarie 1821, Roma, Statele Papale) poet englez, unul din cei mai importanți reprezentanți ai romantismului englez.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (380, "Ivan Andreevici Krîlov", "<p>(13 februarie 1769, Moscova — 21 noiembrie 1844, Sankt Petersburg) este cel mai cunoscut fabulist rus.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (381, "Georges André Malraux", "<p> (Párizs, 1901. november 3. – Créteil, 1976. november 23.) francia regényíró, művészetteoretikus és kulturális miniszter.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (382, "Adam Mickiewicz", "<p>(24 decembrie 1798, Zavossie, Lithuania Governorate, Imperiul Rus - 26 noiembrie 1855, Constantinopol, Imperiul Otoman) poet și scriitor polonez și luptător pentru independența și reîntregirea patriei natale. Este considerat unul dintre cei mai buni poeți romantici polonezi din secolul al XIX-lea. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (383, "Paul Valéry", "<p><strong>Paul Ambroise Valéry</strong> (30 octombrie 1871, Sète – 20 iulie 1945, Paris) scriitor francez, autor de poeme și eseuri, reprezentant al simbolismului tardiv în literatura franceză.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (384, "Hannes Alfvén", "<p>(30 mai 1908, Norrköping⁠, Östergötlands län, Suedia – 2 aprilie 1995, parohia Danderyd⁠, Stockholm län, Suedia) fizician suedez, câștigător al Premiul Nobel pentru Fizică în anul 1970 pentru dezvoltarea teoriei magnetohidrodinamicii. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (385, "Fernand Baldensperger", "<p>(4 mai 1871 - 24 februarie 1958) critic și istoric literar francez, teoretician al literaturii comparate.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (386, "Tristan Bernard", "<p><strong>Paul Bernard</strong> (7 septembrie 1866, Besançon, Franța – 7 decembrie 1947, Paris, Île-de-France, Franța) romancier și dramaturg francez.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (387, "Jacques-Bénigne Bossuet", "<p>(27 septembrie 1627 — 12 aprilie 1704) episcop francez, teolog, orator renumit și predicator la curtea regelui Ludovic al XIV-lea. Bossuet era un mare apărător a teoriei politicii absolutiste; susținea că guvernarea unei țări era divină iar regii primeau puterea de la Dumnezeu.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (388, "Melvin Calvin", "<p>(8 aprilie 1911, Saint Paul, Minnesota, SUA – 8 ianuarie 1997, Berkeley, California, SUA) chimist evreu-american, laureat al Premiului Nobel pentru chimie (1961) pentru elucidarea mecanismului fotosintezei.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (389, "Ion Luca Caragiale", "<p>(n. 13 februarie 1852 Haimanale, județul Prahova, Țara Românească, astăzi I. L. Caragiale, județul Dâmbovița, România – 9 iunie 1912, Berlin, Imperiul German) dramaturg, nuvelist, pamfletar, poet, scriitor, director de teatru, comentator politic și ziarist român.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (390, "Pavel Alexeevici Cerenkov", "<p>(28 iulie 1904, Novaya Chigla, Bobrovsky Uyezd⁠, Gubernia Voronej⁠, Imperiul Rus – 6 ianuarie 1990, Moscova, RSFS Rusă, URSS) fizician sovietic de mare reputație, laureat al Premiului Nobel pentru Fizică în 1958 pentru descoperirea fenomenului denumit în onoarea sa efect Cerenkov.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (391, "Max Delbrück", "<p>(4 septembrie 1906, Berlin, Imperiul German – 9 martie 1981, Pasadena, California, SUA) biofizician și genetician german american, considerat unul dintre fondatorii biologiei moleculare.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (392, "Vladan Desnica", "<p>(17 septembrie 1905 - 4 martie 1967) scriitor și traducător iugoslav.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (393, "Jovan Dučić", "<p>(17 februarie 1871, Trebinje, Republika Srpska, Bosnia și Herțegovina - 7 aprilie 1943, Gary, Indiana, SUA) scriitor sârb, de origine macedo-vlahă, poet și diplomat.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (394, "Victor Eftimiu", "<p>(24 ianuarie 1889, Boboshticë, Korçë, Albania – 27 noiembrie 1972, București) dramaturg, eseist, povestitor, scriitor și traducător român de origine macedoromână, respectiv membru al Academiei Române și francmason român din perioada interbelică.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (395, "Pierre Emmanuel", "<p><strong>Jean-Noël Mathieu</strong> (3 mai 1916 - 24 septembrie 1984) poet francez de inspirație creștină. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (396, "Ulf von Euler", "<p>(7 februarie 1905 - 9 martie 1983) fiziolog și farmacolog suedez. A împărțit Premiul Nobel pentru Fiziologie și Medicină în 1970 pentru activitatea sa în domeniul neurotransmițătorilor.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (397, "Edward Gibbon", "<p>(27 aprilie 1737, Wandsworth, Regatul Unit - 16 ianuarie 1794, Londra, Regatul Marii Britanii) istoric englez și membru al parlamentului britanic. Cea mai faimoasă lucrare a sa este „Istoria declinului și a prăbușirii Imperiului Roman”, publicată în șase volume între anii 1776 și 1788. Operele sale sunt renumite pentru calitate, ironia prozei, folosirea surselor primare și denigrarea deschisă a religiei instituționale.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (398, "Ragnar Granit", "<p><strong>Ragnar Arthur Granit</strong> (30 octombrie 1900 - 12 martie 1991) om de știință finlandezo-suedez care a primit Premiul Nobel pentru Fiziologie sau Medicină în 1967 împreună cu Haldan Keffer Hartline și George Wald 'pentru descoperirile lor privind procesele fiziologice și chimice primare ale vederii în ochi'.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (399, "Jules Lemaitre", "<p>(27 aprilie 1853 - 4 august 1914) critic literar și dramaturg francez.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (400, "Willard Libby", "<p><strong>Willard Frank Libby</strong> (17 decembrie 1908, Grand Valley, Colorado, SUA – 8 septembrie 1980, Los Angeles, California, SUA) chimist american, unul din oamenii Proiectului Manhattan, laureat al Premiului Nobel pentru chimie (1960).</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (401, "André Michel Lwoff", "<p>(Ainay-le-Château, 1902. május 8. – Párizs, 1994. szeptember 30.) francia mikrobiológus. 1965-ben François Jacobbal és Jacques Monod-val megosztva elnyerte az orvostudományi Nobel-díjat 'az enzim- és vírustermelés genetikai szabályozásának felfedezéséért'. A Magyar Tudományos Akadémia tiszteleti tagja.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (402, "Alexandr Mihailovici Prohorov", "<p>(n. 11 iulie 1916, Atherton⁠(d), Queensland, Australia – d. 8 ianuarie 2002, Moscova, Rusia) fizician rus, membru de onoare al Academiei Române, laureat al Premiului Nobel pentru Fizică în 1964, pentru munca de pionierat depusă în domeniul laserilor și maserilor. Doctor Honoris Causa al Universităților din București și Cluj-Napoca.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (403, "Marguerite Yourcenar", "<p>Marguerite Cleenewerck de Crayencour (8 iunie 1903, Bruxelles - 17 decembrie 1987, SUA) eseistă, poetă, profesor universitar, romancieră, traducătoare și scriitoare de limbă franceză de origine belgiană, care a trăit marea majoritate a vieții sale în Franța și Statele Unite ale Americii.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (404, "Dimitrie Anghel", "<p>(16 iulie 1872, Cornești, Miroslava, Iași, România – 13 noiembrie 1914, Iași, România) poet, prozator, reprezentant al simbolismului român.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (405, "Vasile Conta", "<p>(15 noiembrie 1845, com. Ghindăoani, județul Neamț – 21 aprilie 1882, Iași) filozof, scriitor și ministru român.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (406, "Alphonse Marie Louis de Prat de Lamartine", "<p>(Mâcon, 1790. október 21. – Párizs, 1869. február 28.) francia költő, regény- és drámaíró, politikus, kitűnő szónok, aki részt vett a Második Köztársaság kikiáltásában és kormányában. A francia romantika egyik kiemelkedő alakja.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (407, "Theodor Pallady", "<p>(11 aprilie 1871, Iași, România – 16 august 1956, București, România) pictor român, membru al avangardei artistice românești.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (408, "Peter Paul Rubens", "<p>(28 iunie 1577, Siegen/Westfalia – 30 mai 1640, Antwerpen) cel mai renumit pictor flamand.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (409, "Tennessee Williams", "<p><strong>Thomas Lanier Williams</strong> (26 martie 1911, Columbus⁠, Mississippi, SUA – 25 februarie 1983, New York City, New York, SUA) dramaturg, poet și romancier american, laureat al premiului Pulitzer pentru dramă pentru piesa de teatru <em>A Streetcar Named Desire</em> (Un tramvai numit dorință) în 1948 și ulterior pentru piesa <em>Cat On a Hot Tin Roof</em> (Pisica pe acoperișul fierbinte) în 1955, ambele beneficiind de timpurii și mai contemporane ecranizări remarcabile.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (410, "Ion Agârbiceanu", "<p>(12 septembrie 1882, Cenade, Comitatul Alba de Jos, Austro-Ungaria – 28 mai 1963, Cluj, România) scriitor, ziarist și prozator român, adept al sămănătorismului, parlamentar, academician, canonic și protopop unit al Clujului; tatăl fizicianului Ion I. Agârbiceanu.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (411, "René Marill Albérès", "<p>(10 martie 1921 - 25 mai 1982), scriitor și critic literar francez. A publicat studii de carte despre Antoine de Saint-Exupéry, André Gide, Gérard de Nerval, Jean Giraudoux, Jean-Paul Sartre, Michel Butor, Franz Kafka, precum și studii despre romanul din literatura europeană a secolului XX.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (412, "Vasile Alexandri", "<p>(21 iulie/2 august 1821, Bacău, Moldova – 22 august/3 septembrie 1890, Mircești, Iași, România) poet, dramaturg, folclorist, om politic, ministru, diplomat, membru fondator al Academiei Române, creator al teatrului românesc și al literaturii dramatice în România, personalitate marcantă a Moldovei și apoi a României de-a lungul întregului secol al XIX-lea.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (413, "Grigore Alexandrescu", "<p>(22 februarie 1810, Târgoviște, Țara Românească – 25 noiembrie 1885, București, România) poet și fabulist român.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (414, "Amado Alonso", "<p>(13 septembrie 1896, Lerín Navarre, Spania - 26 mai 1952, Arlington, Massachusetts) filolog, lingvist și critic literar spaniol, naturalizat cetățean argentinian și unul dintre fondatorii stilisticii.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (415, "Theodor Aman", "<p>(20 martie 1831, Câmpulung, Muscel, România – 19 august 1891, București, România) pictor și grafician, pedagog și academician român, întemeietor al primelor școli românești de arte frumoase de la Iași și București.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (416, "Petre Andrei", "<p>(29 iunie 1891, Brăila – 4 octombrie 1940, Iași) savant, sociolog, filosof, profesor și om politic, membru post-mortem al Academiei Române; voluntar în Primul Război Mondial, profesor la Catedra de Sociologie a Universității din Iași, deputat din partea Partidului Național-Țărănesc, subsecretar de stat și apoi ministru în mai multe guverne din România interbelică.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (417, "Guillaume Apollinaire", "<p><strong>Wilhelm Albert Vladimir Apollinaris de Kostrowitzky</strong>, (26 august 1880, Roma - 9 noiembrie 1918, Paris) poet, prozator și critic de artă, participant activ și precursor al unora din importantele mișcări artistice ale secolului XX, așa cum sunt dadaismul, modernismul și suprarealismul.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (418, "Louis Aragon", "<p><strong>Louis-Marie Andrieux </strong>(3 octombrie 1897, Paris - 24 decembrie 1982, Paris) poet și scriitor francez.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (419, "Hans (Jean) Arp", "<p>(16 septembrie 1886, Strasbourg, Germania, azi în Franța - 7 iunie 1966, Basel, Elveția) artist plastic complex, care a lucrat ca artist grafic, pictor, sculptor și scriitor.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (420, "Antonin Artaud", "<p><strong>Antoine Marie Joseph Artaud</strong> (4 septembrie 1896, Marsilia, Franța – 4 martie 1948, Paris, Franța) poet, dramaturg, eseist, actor și regizor francez, asociat cu suprarealismul.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (421, "Miguel Ángel Asturias", "<p>(19 octombrie 1899 - 9 iunie 1974, Madrid) cel mai important scriitor din Guatemala, creația sa literară fiind răsplătită cu un Premiul Nobel pentru Literatură în anul 1967.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (422, "W. H. Auden", "<p><strong>Wystan Hugh Auden </strong>(21 februarie 1907 - 29 septembrie 1973) poet britanic-american. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (423, "Mihály Babits", "<p>(26 noiembrie 1883, Szekszárd – 4 august 1941, Budapesta) poet, eseist, romancier și traducător maghiar, din prima generație de la revista Nyugat.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (424, "Anatol Emilian Baconsky", "<p>(16 iunie 1925, Cofa, Hotin, Basarabia, România – 4 martie 1977, București, România) eseist, poet, prozator, publicist, teoretician literar și traducător român de orientare modernistă, asociat și cu mișcarea suprarealistă din România.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (425, "Jules Barbey d'Aurevilly", "<p><strong>Jules-Amédée Barbey d'Aurevilly</strong> (2 noiembrie 1808 - 23 aprilie 1889) prozator francez.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (426, "George Bariț", "<p><strong>Gheorghe Barițiu</strong> (4 iunie 1812, Jucu de Jos, comitatul Cluj – 2 mai 1893, Sibiu) istoric și publicist român transilvănean, întemeietorul presei românești din Transilvania.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (427, "Maurice Barrès", "<p>(19 august 1862 - 5 decembrie 1923) scriitor și om politic francez. În 1906, Maurice Barrès a fost ales membru în Academia Franceză.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (428, "Roland Barthes", "<p>(12 noiembrie 1915, Cherbourg, Normandia de Jos, Franța – 26 martie 1980, Paris, Île-de-France, Franța) eseist, critic, filosof și teoretician al literaturii, semiotician francez. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (429, "Salvatore Battaglia", "<p>(4 iunie 1904, Catania - 14 august 1971, Napoli) filolog, lingvist, gramatician și critic literar italian.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (430, "Charles Baudelaire", "<p><strong>Charles-Pierre Baudelaire</strong> (9 aprilie 1821, Paris, Franța – 31 august 1867, Paris, Franța) poet francez care a produs, de asemenea, lucrări notabile ca eseist, critic de artă și unul dintre primii traducători ai lui Edgar Allan Poe.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (431, "Jean Bayet", "<p>(12 noiembrie 1892 - 5 decembrie 1969) filozof și istoric francez, profesor la Sorbona, membru al mai multe institute și Academii.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (432, "August Bebel", "<p>(22 februarie 1840, Deutz (Cologne), Rhine Province, Kingdom of Prussia - 13 august 1913, Passug, Churwalden, Switzerland) politician, scriitor și orator socialist german. Este cel mai bine amintit ca fiind unul dintre fondatorii Partidului Social-Democrat al Muncitorilor din Germania (SDAP) în 1869.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (433, "Jacinto Benavente", "<p>(12 august 1866, Madrid - 14 iulie 1954), dramaturg spaniol, laureat al Premiului Nobel pentru Literatură în 1922.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (434, "Nicolas Boileau-Despréaux", "<p>(1 noiembrie 1636, Paris, Regatul Franței – 13 martie 1711, Paris, Regatul Franței) scriitor francez, considerat cel mai mare teoretician al literaturii franceze.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (435, "Savin Bratu", "<p><strong>Raul Baraș</strong> (15 mai 1925, Roman – 4 martie 1977, București) editor, critic și istoric literar român de origine evreiască.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (436, "Jean Cassou", "<p>(9 iulie 1897 - 19 ianuarie 1986) scriitor, critic de artă francez și luptător în Rezistența franceză.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (437, "Panait Cerna", "<p>pe numele său de naștere <strong>Stanciof</strong>, de origine bulgară (25 septembrie 1881, Cerna, județul Tulcea – 26 martie 1913, Leipzig) poet intimist, romantic minor sau tradiționalist român, un epigon al poetului Mihai Eminescu, precum și filosof, critic literar și traducător. Vorbitor nativ al limbii bulgare, Cerna a scris cu toate acestea în română și a dezvoltat un stil tradiționalist, care a fost legat de clasicism și neoclasicism.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (438, "Jean Chapelain", "<p>(4 decembrie 1595 - d. 22 februarie 1674) poet și critic literar francez, unul dintre principalii teoreticieni ai clasicismului. A fost unul dintre fondatorii Academiei Franceze.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (439, "Ilarie Chendi", "<p>(14 noiembrie, 1871, Dârlos, comitatul Târnava Mică — 23 iunie 1913, București) critic literar român, aparținând curentului sămănătorist.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (440, "Marie-Joseph Chénier", "<p>Marie-Joseph Blaise de Chénier (11 februarie 1764 - 10 ianuarie 1811) poet, dramaturg și om politic francez de origine franceză și greacă.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (441, "Ion Chinezu", "<p>(15 august 1894, Sântana de Mureș – 10 decembrie 1966, București) critic literar și traducător român.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (442, "Paul Claudel", "<p>(6 august 1868, Villeneuve-sur-Fère-en-Tardenois, Aisne — 23 februarie 1955, Paris) poet, dramaturg și eseist francez.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (443, "Samuel Taylor Coleridge", "<p>(21 octombrie 1772 - 25 iulie 1834) poet, critic literar și filozof englez, care a pus bazele, alături de prietenul său William Wordsworth, mișcării romantice în Anglia, fiind de asemenea unul dintre „Lake Poets” („Poeții lacului”). </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (444, "Petru Comarnescu", "<p><strong>Anton Coman</strong>, (23 noiembrie 1905, Iași – 27 noiembrie 1970, București), eseist, critic de artă, critic literar, memorialist, jurnalist cultural și cunoscut anglist & americanist român, unul dintre fondatorii revistei interbelice Criterion (1934), alături de Mircea Vulcănescu și Alexandru Christian Tell.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (445, "Salvador Dalí", "<p><strong>Salvador Domingo Felipe Jacinto Dalí Domenech</strong>, (11 mai 1904, Figueras, Spania - 23 ianuarie 1989, Figueras) pictor spaniol, originar din provincia Catalonia, reprezentant de seamă al curentului suprarealist în artă. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (446, "Wilhelm Dilthey", "<p>(19 noiembrie 1833 - 1 octombrie 1911) istoric, psiholog, sociolog și filozof hermeneutic german.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (447, "Nikolay Alexandrovich Dobrolyubov", "<p>(24 ianuarie 1836 - 29 noiembrie 1861) critic literar, filozof materialist și om politic rus.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (448, "Maxime Du Camp", "<p>(8 februarie 1822 - 9 februarie 1894) scriitor și fotograf francez.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (449, "Agustín Durán", "<p>(14 octombrie 1789, Madrid, Spania - 1 decembrie 1862, Madrid, Spania) savant și folclorist spaniol.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (450, "Albrecht Dürer", "<p>(21 mai 1471, Nürnberg, Ducatul de Bavaria – 6 aprilie 1528, Nürnberg, Ducatul de Bavaria) pictor german, creator de gravuri și teoretician al artei, una din personalitățile de seamă ale istoriei universale a artei.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (451, "Johann Peter  Eckermann", "<p>(21 septembrie 1792 - 3 decembrie 1854) poet și memorialist german, secretarul particular al lui Goethe.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (452, "T. S. Eliot", "<p><strong>Thomas Stearns Eliot</strong> (26 septembrie 1888 – 4 ianuarie 1965) poet, dramaturg și teoretician literar britanic de origine americană, laureat al Premiului Nobel pentru Literatură (1948). Prin arta sa remarcabilă, T.S. Eliot a revoluționat întreaga poezie modernă engleză. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (453, "Paul Éluard", "<p><strong>Eugène Emile Paul Grindel </strong>(14 decembrie 1895, Saint-Denis - 18 noiembrie 1952, Charenton-le-Pont) poet francez. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (454, "Odysseas Elytis", "<p>pseudonimul literar al lui <strong>Odysséas Alepoudhélis</strong> (2 noiembrie 1911, Heraklion, Creta - 18 martie 1996, Atena) scriitor, laureat al Premiului Nobel pentru Literatură în anul 1979.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (455, "Edgar Faure", "<p><b>Edgar Jean Faure </b>(18 august 1908, Béziers, Franța - 30 martie 1988, Arondismentul 7, Paris, Franța) politician, avocat, eseist, istoric și memorialist francez, prim-ministru al Franței în 1952 și din nou între 1955 și 1956.  În 1978, a fost ales membru al Academiei Franceze.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (456, "Konstantin Fedin", "<p>(24 februarie 1892, Saratov, Imperiul Rus – 15 iulie 1977, Moscova, RSFS Rusă, URSS) romancier și poet rus.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (457, "Edward Morgan Forster", "<p>(1 ianuarie 1879, Londra, Regatul Unit al Marii Britanii și Irlandei – 7 iunie 1970, Coventry, Anglia, Regatul Unit) scriitor și critic literar englez. Este considerat un maestru al romanului englezesc contemporan. Analist de mare finețe, lucid și sceptic, opera sa este inspirată din mediile intelectuale ale Angliei victoriene, fiind influențată și de călătoriile întreprinse de autor în Italia, India și Egipt.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (458, "Laurențiu Fulga", "<p>Laurențiu Ionescu (2 noiembrie 1916, Fulga de Jos județul antebelic Prahova – 16 noiembrie 1984, București) autor, dramaturg, eseist, jurnalist, publicist prozator, scriitor și traducător român.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (459, "Mihai Gafița", "<p>(21 octombrie 1923, comuna Baia, Județul Baia – 4 martie 1977, București) critic și istoric literar român.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (460, "Ion Ghica", "<p>(12 august 1816, București, Țara Românească – 7 mai 1897, Ghergani, Dâmbovița, România) personalitate marcantă a celei de-a doua jumătăți a secolului al XIX-lea. Economist, matematician, scriitor, pedagog, diplomat și om politic, Ion Ghica a fost cel de-al cincilea prim-ministru de cinci ori: de trei ori al guvernului României (în 1866, în 1866-1867 și în 1870-1871) și de două ori între 1859 și 1860, la Iași și la București, în perioada în care țările române se uniseră într-un stat, însă păstrau încă două guverne separate.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (461, "Vincent Van Gogh", "<p><strong>Vincent Willem van Gogh</strong> (30 martie 1853, Zundert, Brabantul de Nord, Țările de Jos – 29 iulie 1890, Auvers-sur-Oise⁠, Seine-et-Oise, Franța) pictor neerlandez post-impresionist ale cărui lucrări au avut o influență profundă asupra artei secolului al XIX-lea, prin culorile lor vii și impactul emoțional. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (462, "Lucien Goldmann", "<p>(20 iulie 1913, București, România – 8 octombrie 1970, Paris, Franța) filozof, sociolog și critic literar francez de etnie evreiască, născut în România.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (463, "Edmond de Goncourt", "<p>(26 mai 1822, Nancy, Restaurația franceză – 16 iulie 1896, Maison d'Alphonse Daudet⁠, Seine-et-Oise, Franța) scriitor, critic literar, critic de artă și publicist francez, precum și fondatorul Academiei Goncourt.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (464, "Antonio Gramsci", "<p>(Ales, Szardínia, 1891. január 22. – Róma, 1937. április 27.) olasz marxista filozófus, nyelvész, újságíró, író és teoretikus, az Olasz Kommunista Párt egyik alapítója, parlamenti képviselő, az Ordine Nuovo című hetilap alapítója és szerkesztője.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (465, "Wilhelm Grimm", "<p><strong>Wilhelm Carl Grimm</strong> (24 februarie 1786, Hanau — 16 decembrie 1859, Berlin) filolog, folclorist și scriitor romantic german, co-autor (împreună cu fratele său Jacob Grimm) al Dicționarului limbii germane precum și al unor celebre culegeri de povești, inspirate din motive populare germane.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (466, "Robert Grosseteste", "<p>(c. 1168 - 8 sau 9 octombrie 1253) cunoscut și sub numele de Robert Greathead sau Robert de Lincoln, a fost un om de stat englez, filosof scolastic, teolog, om de știință și episcop de Lincoln.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (467, "Jean-Marie Guyau", "<p>(28 octombrie 1854, Laval, Pays de la Loire, Franța – 31 martie 1888, Menton, Alpes-Maritimes, Franța) filosof și poet francez.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (468, "Nicolai Hartmann", "<p>Nikolajs Hartmanis (20 februarie 1882, Riga, Letonia – 9 octombrie 1950, Göttingen) filosof german, principal reprezentant al filosofiei idealiste, unul dintre cei mai sistematici și mai productivi filosofi din secolul al XX-lea, întemeietor al 'ontologiei critice' - formă specifică în orientarea ontologică a cugetării contemporane. Preocupat de o nouă formă a sistematicii filosofice, el s-a impus totodată ca un reputat istoric al filosofiei și al culturii în genere.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (469, "Ion Heliade-Rădulescu", "<p>(6 ianuarie 1802, Târgoviște, Țara Românească – 27 aprilie 1872, București, România) scriitor, filolog și om politic român, membru fondator al Academiei Române și primul său președinte, considerat cea mai importantă personalitate din cultura română prepașoptistă, prin aportul său cultural și estetic la dezvoltarea literaturii române, fiind apreciat și ca un precursor al poeziei moderne.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (470, "Emile Hennequin", "<p>Autor, editor, scriitor și filozof francez.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (471, "Joris-Karl Huysmans", "<p>(5 februarie 1848, Paris, Franța – 12 mai 1907, Paris, Franța) scriitor francez.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (472, "René Huyghe", "<p>(3 mai 1906 - 5 februarie 1997) scriitor francez de istorie, psihologie și filozofie a artei. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (473, "Radu Ionescu", "<p>Ivancescu Raducanu (1834, Bucuresti - 24 dec. 1872, Bucuresti) poet si critic literar.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (474, "Magda Isanos", "<p>(17 aprilie 1916, Iași – 17 noiembrie 1944, București) avocată, poetă, prozatoare și publicistă română.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (475, "Panait Istrati", "<p><strong>Gherasim Istrate</strong> (10 august 1884, Brăila, România – 16 aprilie 1935, București, România) scriitor român de limbă română și franceză.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (476, "Juan Ramón Jiménez", "<p>(24 decembrie 1881 – 29 mai 1958) poet și eseist spaniol, laureat al Premiului Nobel pentru Literatură în 1956.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (477, "James Joyce", "<p>(2 februarie 1882, Rathgar, Irlanda, Regatul Unit al Marii Britanii și Irlandei – 13 ianuarie 1941, Zürich, cantonul Zürich, Elveția) prozator și poet irlandez, considerat unul dintre cei mai importanți scriitori ai secolului al XX-lea. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (478, "Joseph Joubert", "<p>(6 mai 1754 Montignac, Périgord - 4 mai 1824 Paris) moralist și eseist francez.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (479, "Vasili Jukovski", "<p><strong>Vasili Andreevici Jukovski </strong>(9 februarie 1783 - 24 aprilie 1852) poet rus, personalitate marcantă a literaturii ruse de la jumătatea secolului al XIX-lea.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (480, "Paul Klee", "<p>(18 decembrie 1879, Münchenbuchsee, Elveția – 29 iunie 1940, Muralto, Elveția) pictor, desenator și teoretician al artei elvețiene, de naționalitate germană.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (481, "Jean Lahor", "<p><strong>Henri Cazalis</strong>, medic francez, poet simbolist și om de litere, care a scris sub pseudonimele<b> Jean Caselli </b>și <b>Jean Lahor.</b></p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (482, "Charles Lamb", "<p>(10 februarie 1775, Londra, Regatul Marii Britanii – 27 decembrie 1834, Londra Mare, Anglia, Regatul Unit al Marii Britanii și Irlandei) eseist englez, unul dintre creatorii tradiției eseului spiritual și umoristic asupra lucrurilor aparent banale.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (483, "Valery Larbaud", "<p>(29 august 1881, Vichy, Auvergne, Franța – 2 februarie 1957, Vichy, Auvergne, Franța) scriitor francez, cunoscut și sub pseudonimele: <strong>A.-O. Barnabooth</strong>,<strong> L. Hagiosy</strong>, <strong>X. M. Tourmier de Zamble</strong>.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (484, "Mariano José de Larra", "<p>Mariano José de Larra y Sánchez de Castro (24 martie 1809, Madrid - Madrid 13 februarie 1837) poet și narator romantic spaniol.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (485, "Victor Lazarev", "<p><strong>Viktor Nikitich Lazarev </strong>(3 September 1897 – 1 February 1976) Russian art critic and historian who specialized in medieval Byzantine, Russian, and Armenian religious art.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (486, "Leconte de Lisle", "<p>(22 octombrie 1818 - 17 iulie 1894) a fost un poet francez din mișcarea parnasiană.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (487, "Vladimir Ilici Lenin", "<p><strong>Vladimir Ilici Ulianov</strong> (22 aprilie 1870, Ulianovsk, Imperiul Rus – 21 ianuarie 1924, Bolshiye Gorki⁠, RSFS Rusă, URSS) revoluționar rus care a condus partidul bolșevic, primul premier al Uniunii Sovietice și fondatorul ideologiei cunoscute sub numele de leninism.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (488, "Leonid Leonov", "<p>(31 mai 1899 - 8 august 1994) important prozator și dramaturg sovietic.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (489, "Salvador de Madariaga", "<p><strong>Salvador de Madariaga y Rojo</strong> (23 iulie 1886, La Coruña, Galicia, Spania – 14 decembrie 1978, Locarno, Cantonul Ticino, Elveția) diplomat, scriitor, istoric și pacifist spaniol.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (490, "Stéphane Mallarmé", "<p><strong>Étienne Mallarmé</strong> (18 martie 1842 - 9 septembrie 1898), poet și critic francez, o figură de seamă a curentului simbolist european.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (491, "Adrian Maniu", "<p>(6 februarie 1891, București, România – 20 aprilie 1968, București, România) scriitor român, poet tradiționalist al epocii interbelice, membru corespondent al Academiei Române.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (492, "Alessandro Manzoni", "<p><strong>Alessandro Francesco Tommaso </strong><strong>Antonio </strong><strong>Manzoni</strong> (7 martie 1785, Milano, Ducatul Milanului – 22 mai 1873, Milano, Regatul Italiei) scriitor și critic literar italian. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (493, "W. Somerset Maugham", "<p><strong>William Somerset Maugham</strong> (25 ianuarie 1874 - 16 decembrie 1965) Scriitor englez, cunoscut pentru piesele de teatru, romanele și nuvelele sale.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (494, "Ion Minulescu", "<p>(6 ianuarie 1881, București – 11 aprilie 1944, București) poet și prozator român, reprezentant important al simbolismului românesc, numit director general al artelor în 1922.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (495, "Charles Morgan", "<p>(22 ianuarie 1894, Bromley, Regatul Unit al Marii Britanii și Irlandei – 6 februarie 1958, Metropolitan Borough of Kensington, Anglia, Regatul Unit) scriitor englez: romane impresionist-psihologice pe tema confllictelor spirituale, dragostei, morții, creației artistice într-un stil de eleganță clasică.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (496, "Modest Petrovici Musorgski", "<p>(21 martie 1839, Karevo, regiunea Pskov - 28 martie 1881, Sankt Petersburg) compozitor rus, care a devenit cunoscut prin operele sale muzicale pentru pian, fiind considerat unul dintre marii compozitori ruși din secolul al XIX-lea.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (497, "Ștefan I. Nenițescu", "<p>(8 octombrie 1897, București — octombrie 1979, București) critic literar și de artă, jurnalist literar și de artă, publicist, poet, scriitor, traducător și estetician român, consilier economic al României la Haga.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (498, "Pablo Neruda", "<p><strong>Neftalí Ricardo Reyes Basoalto</strong> (12 iulie 1904 – 23 septembrie 1973) poet și om politic chilian, consul în Spania, Japonia, China; ambasador la Paris, premiul 'Cununa de aur' a Reuniunii Internaționale a Poeților din orașul iugoslav Struga; Premiul Internațional Lenin 'Pentru întărirea păcii între popoare', 1953; laureat al Premiului Nobel pentru Literatură în anul 1971.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (499, "José Ortega y Gasset", "<p>(9 mai 1883 – 18 octombrie 1955) filosof spaniol.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (500, "Petre Pandrea", "<p><strong>Petre Marcu</strong> (26 iunie 1904, Balș‐Oltenia - 8 iulie 1968, București), avocat, scriitor, publicist și eseist român.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (501, "Ion Pascadi", "<p>(3 august 1932, Timişoara - 21 iulie 1979, Bucureşti) estetician şi traducător, licențiat în filosofie.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (502, "Walter Pater", "<p><strong>Walter Horatio Pater</strong> (4 august 1839, Londra Mare, Anglia, Regatul Unit al Marii Britanii și Irlandei – 30 iulie 1894, Oxford, Anglia, Regatul Unit al Marii Britanii și Irlandei) un proeminent estetician și critic din Anglia victoriană, susținător fervent al tezei artă pentru artă. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (503, "Sándor Petőfi", "<p>(1 ianuarie 1823, Kiskőrös, Regatul Ungariei, Imperiul Austriac – 31 iulie 1849, Albești, Principatul Transilvaniei, Imperiul Austriac) poet romantic maghiar, provenit dintr-o familie de origine sârbă, erou al Revoluției de la 1848 din Ungaria și Transilvania.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (504, "Ion Petrovici", "<p>(14 iunie 1882, Tecuci – 17 februarie 1972, București) filozof român, eseist, memorialist, scriitor, orator și om politic, profesor la Universitatea din Iași, membru titular al Academiei Române, fost Ministru al Educației Naționale.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (505, "Pablo Picasso", "<p><strong>Pablo Ruiz y Picasso</strong> (25 octombrie 1881, Málaga, Andaluzia, Spania – 8 aprilie 1973, Mas Notre-Dame-de-Vie de Mougins⁠, Provența-Alpi-Coasta de Azur, Franța) artist plastic spaniol.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (506, "Ion Pillat", "<p>(31 martie 1891, București, România – 17 aprilie 1945, București, România) eseist, poet tradiționalist, antologist, editor și publicist român, membru corespondent al Academiei Române.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (507, "Luigi Pirandello", "<p>(28 iunie 1867, Agrigento, Sicilia, Italia – 10 decembrie 1936, Roma, Regatul Italiei) dramaturg, romancier, poet și eseist italian, laureat al Premiului Nobel pentru Literatură pe anul 1934.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (508, "Victor Ion Popa", "<p>(29 iulie 1895, Bârlad, Tutova, România – 30 martie 1946, București, România) om de teatru și literat polivalent care a adus, prin opera sa dramatică, regizorală și pedagogică, o contribuție însemnată la evoluția teatrului românesc dintre cele două războaie mondiale.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (509, "Pierre-Joseph Proudhon", "<p>(15 ianuarie 1809, Besançon - 19 ianuarie 1865, Passy, Paris), economist, sociolog francez, teoretician al socialismului, considerat părintele anarhismului.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (510, "Raymond Queneau", "<p>(21 februarie 1903, Le Havre, Franța – 25 octombrie 1976, Paris, Île-de-France, Franța) scriitor francez modern.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (511, "Ion Rașcu", "<p><strong>I. M. Rașcu</strong> (31 martie 1890 – 7 decembrie 1971) poet simbolist român, promotor cultural, comparatist și profesor.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (512, "Marcel Raymond", "<p>(20 decembrie 1897, Geneva - 28 noiembrie 1981, Geneva) critic literar elvețian, specializat în literatura franceză. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (513, "Pierre Reverdy", "<p>(13 septembrie 1889 - 17 iunie 1960) poet francez a cărui operă a fost inspirată de mișcările artistice provocatoare ale vremii, suprarealismul, dadaismul și cubismul, pe care le-a influențat ulterior. Singurătatea și aprehensiunea spirituală care îi străbat poezia au făcut apel la crezul suprarealist. Cu toate acestea, el a rămas independent de '-ismele' predominante, căutând ceva dincolo de definițiile acestora. Scrisul său s-a maturizat într-o misiune mistică, căutând, așa cum scria el însuși: 'simplitatea sublimă a realității'.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (514, "Jean-Pierre Richard", "<p>(15 iulie 1922 - 15 martie 2019) scriitor și critic literar francez.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (515, "Rainer Maria Rilke", "<p><strong>René Karl Wilhelm Johann Josef Maria Rilke</strong> (4 decembrie 1875 - 29 decembrie 1926) poet și romancier austriac. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (516, "Stephan Roll", "<p><strong>Gheorghe Dinu</strong>, <strong>Gheorghi Dinev</strong> (5 iunie 1903, Florina, Macedonia – 13 mai 1974, București) poet și prozator român, de origine macedoromână-bulgară, reprezentant al literaturii românești de avangardă. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (517, "Max Scheler", "<p>(22 august 1874, Oberbayern, Bavaria, Imperiul German – 24 mai 1927, Frankfurt pe Main, Republica de la Weimar) filozof german, cunoscut în primul rând pentru contribuțiile sale în domeniul fenomenologiei, filozofiei valorilor, sociologiei cunoașterii și antropologiei filozofice, pe lângă calitatea sa de clasic al filozofiei religiei.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (518, "Pius Servien", "<p><strong>Piu-Șerban Coculescu </strong>(18 martie 1902, București - 28 ianuarie 1959, Paris) scriitor și lingvist român. Prin studiile sale de teorie a ritmurilor a pus bazele lingvisticii matematice.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (519, "Percy Bysshe Shelley", "<p>(4 august 1792, Horsham, Anglia, Regatul Unit – 8 iulie 1822, La Spezia, Liguria, Italia) unul dintre cei mai importanți poeți ai romantismului englez, criticii considerându-l unul dintre cei mai de seamă lirici ai literaturii de limbă engleză.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (520, "Jean Sibelius", "<p>(8 decembrie 1865, Hämeenlinna, Kanta-Hämeen maakunta, Finlanda – 20 septembrie 1957, Ainola, Uusimaa Province, Finlanda) compozitor finlandez din perioada Romantismului târziu, a cărui muzică a avut un rol esențial în formarea identității naționale finlandeze.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (521, "Philip Sidney", "<p>(30 noiembrie 1554, Kent, Regatul Angliei – 17 octombrie 1586, Arnhem, Gelderland, Țările de Jos) poet englez.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (522, "Zaharia Stancu", "<p>(5 octombrie 1902, Salcia, județul Teleorman – 5 decembrie 1974, București) scriitor român, poet, romancier, prozator, director de teatru, jurnalist și publicist, academician, laureat al Premiului Herder.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (523, "Strabon", "<p>în varianta latinizată <strong>Strabo </strong>(63 î.Hr., Amasia⁠, Grecia - 23 d. Hr., Amasia⁠, Grecia) era de fapt o poreclă, însemnând Sașiul, purtată de un vestit istoric și geograf antic grec.</p>
INSERT INTO Author_Name (id, name, des, language_id) VALUES (524, "Vladimir Streinu", "<p>(23 mai 1902, Teiu din Deal, Județul Argeș, România – 26 noiembrie 1970, București, România) autor, critic literar, deputat, estetician, istoric literar, pedagog, scriitor român.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (525, "Suetonius", "<p><strong>Gaius Suetonius Tranquillus</strong> (c. 69 d.Hr. - după 122 d.Hr.), istoric roman care a scris în timpul epocii imperiale timpurii a Imperiului Roman.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (526, "Sully Prudhomme", "<p><strong>René Armand François Prudhomme </strong>(16 martie 1839, Paris – 6 septembrie 1907, Châtenay-Malabry), primul laureat al Premiului Nobel pentru Literatură la 10 decembrie 1901.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (527, "Ieronim Șerbu", "<p>(pseudonimul lui Afon Herz Erick, 2 decembrie 1911, Botoșani, România – 8 decembrie 1972, București, România) prozator român.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (528, "Aleksei Nikolaevici Tolstoi", "<p>(29 decembrie 1882, Pugaciov - 23 februarie 1945, Moscova) scriitor rus care a abordat multe genuri, dar care s-a specializat în literatură SF și romane istorice.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (529, "Miguel de Unamuno", "<p>(29 septembrie 1864, Bilbao - 31 decembrie 1936, Salamanca) prozator spaniol, filozof, profesor, eseist, poet și dramaturg.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (530, "Pedro Henríquez Ureña", "<p>(29 iunie 1884 - 11 mai 1946) eseist, filosof, umanist, filolog și critic literar dominican.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (531, "Lionello Venturi", "<p>(25 aprilie 1885, Modena - 14 august 1961, Roma) istoric și critic de artă italian.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (532, "Jean Bruller", "<p><strong>Jean Marcel Adolphe Bruller</strong>, <strong>Vercors </strong>(26 February 1902 – 10 June 1991) French writer and illustrator. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (533, "Paul Verlaine", "<p>(30 martie 1844 — 8 ianuarie 1896) poet francez. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (534, "Ilarie Voronca", "<p>(născut Eduard Marcus), (31 decembrie 1903, Brăila – 8 aprilie 1946, Paris) poet evreu român de avangardă, promotorul revistelor 75 HP și Integral și al mișcării integraliste. A mai semnat sub pseudonimele Alex Cernat și Roneiro Valcia.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (535, "Wilhelm Heinrich Wackenroder", "<p>(13 iulie 1773 - 13 februarie 1798) jurist și scriitor german. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (536, "Richard Wagner", "<p>(22 mai 1813, Leipzig, Confederația Rinului – 13 februarie 1883, Veneția, Regatul Italiei) compozitor, dramaturg și teoretician al artei germane, unul din cei mai de seamă reprezentanți ai romantismului muzical.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (537, "Johann Joachim Winckelmann", "<p>(9 decembrie 1717, Stendal, Saxonia-Anhalt, Germania – 8 iunie 1768, Trieste, Imperiul Habsburgic) istoric de artă și arheolog german, considerat „părintele arheologiei moderne” sau chiar „profetul și eroul fondator al arheologiei moderne'.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (538, "William Wordsworth", "<p>(7 aprilie 1770 - 23 aprilie 1850) poet romantic englez care, împreună cu Samuel Taylor Coleridge, a contribuit la lansarea epocii romantice în literatura engleză prin publicarea în comun a Baladelor lirice (1798).</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (539, "Wilhelm Worringer", "<p>(13 ianuarie 1881, Aachen - 29 martie 1965, München) istoric de artă german, cunoscut pentru teoriile sale despre arta abstractă și relația acesteia cu mișcările de avangardă, cum ar fi expresionismul german.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (540, "Paul Zarifopol", "<p>(30 noiembrie 1874, Iași, România – 1 mai 1934, București, România) istoric, critic literar care a desfășurat și o amplă activitate de publicist, moralist, eseist politic și istoric al civilizației românești. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (541, "José Zorrilla", "<p>José Zorrilla y Moral - poet și dramaturg spaniol.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (542, "Hervey Allen", "<p><b>William Hervey Allen Jr.</b> (8 decembrie 1889 - 28 decembrie 1949) educator, poet și scriitor american. Este cunoscut mai ales pentru opera sa Anthony Adverse (transformată în filmul cu același nume în 1936), considerată de mulți critici 'modelul și precursorul romanului istoric american contemporan'.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (543, "Ion Barbu", "<p><strong>Dan Barbilian</strong> (19 martie 1895, Câmpulung, Muscel, România – 11 august 1961, București, România) poet și matematician român.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (544, "Friedrich Christian Baumeister", "<p>(17 iulie 1709 - 8 octombrie 1785) filosof german. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (545, "Jean-Eugène Bersier", "<p>Pictor și gravor francez.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (546, "Michel Foucault", "<p>(15 octombrie 1926, Poitiers – 25 iunie 1984, Paris) filosof și istoric francez a cărui operă, catalogată drept postmodernistă sau poststructuralistă, a avut puternice ecouri în filosofia și istoria științei, psihanaliză, sociologie.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (547, "Ionel Gherea", "<p>numele la naștere, Ioan Dobrogeanu-Gherea ori Ion D. Gherea (uneori cunoscut sub forma de J. D. Ghéréa - n. 1895, Ploiești, România – d. 5 noiembrie 1978, București, România), filozof, eseist, scriitor și pianist de concert român.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (548, "Nikos Kazantzakis", "<p>(2 martie 1883, Heraklion, Imperiul Otoman – 26 octombrie 1957, Freiburg im Breisgau, RFG) autor grec care a scris de-a lungul vieții sale poeme, romane, eseuri, și cărți de călătorii.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (549, "Amīr Khusrau", "<p>(1253-1325 d.Hr.) cântăreț, muzician, poet și cărturar sufi indo-persan care a trăit sub sultanatul din Delhi.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (550, "Selma Lagerlöf", "<p>(20 noiembrie 1858, Östra Emterwik, Värmland - 16 martie 1940) scriitoare suedeză, prima femeie care a primit Premiul Nobel pentru Literatură (1909) și prima femeie membră a Academiei Suedeze (1914).</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (551, "Sinclair Lewis", "<p><strong>Harry Sinclair Lewis</strong> (7 februarie, 1885, Sauk Centre, Minnesota – 10 ianuarie, 1951, Roma, Italia) prozator american, primul american care a luat Premiul Nobel pentru Literatură în anul 1930.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (552, "Charles Nodier", "<p><strong>Jean Charles Emmanuel Nodier </strong>(29 aprilie 1780, Besançon, Franche-Comté, Franța – 27 ianuarie 1844, Paris, Franța)  scriitor și bibliotecar francez care a introdus o generație tânără de romantici în lumea fantastică, în literatura gotică și în poveștile cu vampiri. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (553, "Thomas Paine", "<p>(29 ianuarie 1737, Thetford, Anglia - 8 iunie 1809, New York, Statele Unite) pamfletar, revoluționar, radicalist și intelectual.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (554, "Vasile Pârvan", "<p>(28 septembrie 1882, Huruiești, Bacău, România – 26 iunie 1927, București, România) istoric, arheolog, epigrafist și eseist român, membru titular (din 1913) al Academiei Române.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (555, "Jean Piaget", "<p>(9 august 1896, Neuchâtel, Cantonul Neuchâtel, Elveția – 16 septembrie 1980, Geneva, Geneva, Elveția) psiholog, biolog, logician și filozof elvețian, cunoscut pentru contribuțiile sale din domeniul epistemologiei și psihologiei dezvoltării.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (556, "Jules Romains", "<p>(26 august 1885 - 14 august 1972) poet și scriitor francez, fondator al mișcării literare unanimiste. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (557, "Edmond About", "<p>(14 februarie 1828, Dieuze⁠, Lorena, Franța – 16 ianuarie 1885, Quartier Saint-Georges⁠, Île-de-France, Franța) scriitor și academician francez.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (558, "Felix Aderca", "<p><strong>Froim Zelig Adercu </strong>(26 martie 1891, Puiești⁠, județul Tutova, România – 12 decembrie 1962, București⁠, Republica Populară Română) prozator, romancier, poet, dramaturg, estetician, eseist și critic literar român, de origine evreiască, considerat ca un reprezentant al modernismului literar în cadrul literaturii române.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (559, "Hans-Christian Andersen", "<p>(2 aprilie 1805, Odense, Danemarca – 4 august 1875, Copenhaga, Danemarca), scriitor danez, celebru mai ales pentru basmele sale. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (560, "Maxwell Anderson", "<p>(15 decembrie 1888 - 28 februarie 1959) dramaturg, autor, poet, jurnalist și textier american.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (561, "Ivo Andrić", "<p>(10 octombrie 1892, Dolac⁠, Travnik, Condominiul Bosniei și Herțegovinei, Austro-Ungaria – 13 martie 1975, Belgrad, RS Serbia, RSF Iugoslavia) scriitor sârb, laureat al Premiului Nobel pentru Literatură în anul 1961.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (562, "Antiphon", "<p>(480 î. Hr. – 411 î.Hr.) filozof sofist și orator grec antic din Atena. Concepțiile sale se opun democrației de atunci și se bucură de un prestigiu deosebit în rândul oligarhilor atenieni.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (563, "Constantin Antoniade ", "<p>(16 august 1880, București, România – 19 iulie 1954, Madrid, New Castile⁠, Spania) jurist, scriitor, istoric, filosof și diplomat român de origine etnică greacă.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (564, "Attius", "<p><strong>Lucius Accius</strong> (170 î.Hr., Pisaurum, Marchia Anconitana, Statele Papale – 84 î.Hr., Roma, Republica Romană), poet dramatic latin. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (565, "Henry Adams", "<p><strong>Henry Brooks Adams</strong> (16 februarie 1838 - 27 martie 1918) istoric, filozof și romancier american, membru al familiei politice Adams, descendent a doi președinți americani.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (566, "Alfred Adler", "<p>(7 februarie 1870 – 28 mai 1937) medic, psiholog, pedagog și publicist austriac, fondator al școlii de psihologie individuală, a doua școală vieneză de psihoterapie, în ordine cronologică (după psihanaliza lui <strong>Sigmund Freud</strong> și înainte de logoterapia lui <strong>Viktor Frankl</strong>).</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (567, "Endre Ady", "<p>(22 noiembrie 1877, Eriu-Mețenț, Austro-Ungaria – 27 ianuarie 1919, Budapesta, Republica Democrată Ungară) poet maghiar, considerat a fi unul dintre cei mai importanți poeți ai secolului XX, și ai literaturii maghiare în special.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (568, "Rudolf Agricola", "<p>(28 august 1443 sau 17 februarie 1444 - 27 octombrie 1485) umanist olandez din Țările de Jos de Nord, renumit pentru cunoștințele sale de latină și greacă. A fost educator, muzician, constructor de orgi bisericești, poet în latină și în limba vernaculară, diplomat, boxer și erudit în ebraică spre sfârșitul vieții sale.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (569, "Leon Battista Alberti", "<p>(14 februarie 1404, Genova, Republica Genova – 25 aprilie 1472, Roma, Statele Papale) scriitor, arhitect, pictor, sculptor, filozof italian și umanist al Renașterii. În cadrul preocupărilor sale multiple se pot adăuga și lingvistica, muzica, arheologia.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (570, "Vasile Alecsandri", "<p>(21 iulie 1821, Bacău, Moldova – 22 august 1890, Mircești, Iași, România) poet, dramaturg, folclorist, om politic, ministru, diplomat, membru fondator al Academiei Române, creator al teatrului românesc și al literaturii dramatice în România, personalitate marcantă a Moldovei și apoi a României de-a lungul întregului secol al XIX-lea.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (571, "Mateo Alemán", "<p><strong>Mateo Alemán y de Enero</strong> (septembrie 1547, Sevilia - d. 1614?) prozator spaniol.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (572, "Jean le Rond D'Alembert", "<p>(16 noiembrie 1717, Paris, - 29 octombrie 1783, Paris) matematician, fizician, filozof și enciclopedist francez. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (573, "Dámaso Alonso", "<p><strong>Dámaso Alonso y Fernández de las Redondas </strong>(22 octombrie 1899, Madrid, New Castile, Spania – 25 ianuarie 1990, Madrid, Madrid, Spania) poet, critic literar și filolog spaniol. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (574, "Levinus Ammonius", "<p>Levinus van der Maude (1485 - 1556) umanist olanez.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (575, "Jacques Amyot", "<p>(30 octombrie 1513 - 6 februarie 1593) episcop renascentist francez, cărturar, scriitor și traducător.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (576, "Anaxagora", "<p>Anaxagoras (c. 500 î.Hr.– 428 î.Hr.) filozof grec presocratic, membru a ceea ce astăzi numim Școala Ioniană de filozofie.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (577, "Anaximandru", "<p><strong>Anaximandros din Milet</strong>, redat uneori în limba română și ca <strong>Anaximandru </strong>sau <strong>Anaximander </strong>(610 î.Hr., Milet, Ionia, Grecia Antică – 547 î.Hr., Milet, Ionia, Turcia) filozof grec, reprezentant al Școlii ioniene.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (578, "Angelus Silesius", "<p><strong>Johannes Scheffler</strong> (25 decembrie 1624 - 9 iulie 1677) poet german.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (579, "Pietro Aretino", "<p>(19 aprilie 1492, Arezzo, Republica Florentină – 21 octombrie 1556, Veneția, Republica Veneția) poet și prozator italian.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (580, "Achim von Arnim", "<p><strong>Carl Joachim Friedrich Ludwig von Arnim</strong> (26 ianuarie 1781, Berlin - 21 ianuarie 1831, Wiepersdorf) prozator german, membru al cercului romanticilor din Heidelberg.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (581, "Gheorghe Asachi", "<p>(1 martie 1788, Herța, Moldova – 12 noiembrie 1869, Iași, România) poet, prozator și dramaturg român. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (582, "Naukratiszi Athénaiosz", "<p>(Naukratisz, 170 körül – ?, 223 után) ókori görög nyelvész és szónoklattan tanár, polihisztori műveltségű tudós.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (583, "Berthold Auerbach", "<p>Moyses Baruch (28 februarie 1812 - 8 februarie 1882) prozator și dramaturg german de etnie ebraică.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (584, "Petre S. Aurelian", "<p>(13 decembrie 1833, Slatina, Olt, România – 24 ianuarie 1909, București, România) economist, agronom, om politic român, membru titular din 1871 al Academiei Române și președinte al acesteia între anii 1901 - 1904.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (585, "Ausonius", "<p>Decimus Magnus Ausonius (circa 310 - 395) retor, demnitar și poet roman, prefect al Galiei, Iliriei și Africii. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (586, "Jane Austen", "<p>(16 decembrie 1775, Steventon⁠, Regatul Marii Britanii – 18 iulie 1817, Winchester, Anglia, Regatul Unit al Marii Britanii și Irlandei) romancieră engleză din perioada romantică pre-victoriană.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (587, "John Lubbock, baron Avebury ", "<p>(30 April 1834 – 28 May 1913), om politic și naturalist englez.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (588, "Victor Babeș", "<p>(28 iulie 1854, Viena, Imperiul Austriac – 19 octombrie 1926, București, România) bacteriolog și morfopatolog român, membru al Academiei Române din 1893.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (589, "François Noël Babeuf", "<p><strong>Gracchus Babeuf </strong>(23 noiembrie 1760 - 27 mai 1797) revoluționar francez, teoretician al comunismului utopic, fondator al ziarului Le Tribun du peuple și conducător al Conspirației egalilor împotriva guvernului reacționar al Directoratulului.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (590, "Bachilide", "<p><strong>Bacchylides </strong>poet antic grec din secolul al V-lea î.Hr, originar din Keos.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (591, "George Bacovia", "<p><strong>George Andone Vasiliu</strong> (17 septembrie 1881, Bacău, România – 22 mai 1957, București, România) scriitor român format la școala simbolismului literar francez.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (592, "Jacques Bainville", "<p>(9 februarie 1879 - 9 februarie 1936) istoric, jurnalist și publicist francez.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (593, "W. N. P. Barbellion", "<p><strong>Wilhelm Nero Pilate Barbellion</strong>, <strong>Bruce Frederick Cummings</strong> (7 septembrie 1889 - 22 octombrie 1919) naturalist și biolog englez, autor al unui „jurnal” început la 13 ani.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (594, "Henri Barbusse", "<p><strong>Adrien Gustave Henri Barbusse</strong> (17 mai 1873, Asnières-sur-Seine, Île-de-France, Franța – 30 august 1935, Moscova, RSFS Rusă, URSS) prozator francez, militant comunist și pacifist. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (595, "John Barrow", "<p>(19 iunie 1764 - 23 noiembrie 1848) geograf, lingvist, scriitor și funcționar public englez. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (596, "Jean Bart", "<p><strong>Eugeniu P. Botez</strong> (28 noiembrie 1874, Burdujeni, Botoșani, România – 12 mai 1933, București, România), prozator român, membru corespondent (din 1922) al Academiei Române.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (597, "Derek Barton", "<p><strong>Derek Harold Richard Barton</strong> (8 septembrie 1918, Gravesend, Anglia, Regatul Unit al Marii Britanii și Irlandei – 16 martie 1998, College Station, Texas, SUA) chimist britanic, laureat al Premiului Nobel pentru chimie (1969).</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (598, "Georges Bataille", "<p><strong>Georges Albert Maurice Victor Bataille</strong> (10 septembrie 1897, Billom, Auvergne, Franța – 9 iulie 1962, Orléans, Franța) antropolog, bibliotecar, critic literar, eseist, filozof și scriitor francez.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (599, "Konstantin Batiușkov", "<p>Konstantin Nikolaevici Batiușkov (29 mai 1787 - 19 iulie 1855) poet, eseist și traducător rus din epoca romantică. </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (600, "Ludwig Baur ", "<p>(9 aprilie 1871 - 14 ianuarie 1943) filozof german.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (601, "Albert Bayet", "<p><strong>Albert Pierre Jules Joseph Bayet</strong> (1 februarie 1880, Lyon - 26 iunie 1961, Paris) sociolog francez, profesor atât la Sorbona, cât și la École pratique des hautes études.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (602, "Pierre Bayle", "<p>(18 noiembrie 1647, Carla-Bayle, Midi-Pirinei, Franța – 28 decembrie 1706, Rotterdam, Țările de Jos) filozof și scriitor francez.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (603, "Nicolae Bălcescu", "<p>(29 iunie 1819, București – 29 noiembrie 1852, Palermo) istoric, scriitor și revoluționar român.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (604, "Simion Bărnuțiu", "<p>(21 iulie 1808, Bocșa, Sălaj, Imperiul Austriac – 28 mai 1864, Sânmihaiu Almașului, Sălaj, Imperiul Austriac) preot greco-catolic, om politic, istoric, filozof, și profesor universitar, unul dintre principalii organizatori ai Revoluției de la 1848 în Transilvania.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (605, "Test", "<p>Test</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (606, "Zaharia Bârsan", "<p>(11 ianuarie 1878, Sânpetru, Brașov, România – 13 decembrie 1948, Cluj, România) scriitor, poet, dramaturg și director de teatru român, fondatorul Teatrului Național din Cluj și primul său director.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (607, "Charles Austin Beard", "<p>(27 noiembrie 1874, Knightstown, Indiana, US – 1 septembrie 1948, New Haven, Connecticut, US) istoric și profesor american ale cărui scrieri sunt, în principal, din prima jumătate a secolului XX.</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (608, "Simone de Beauvoir", "<p>(9 ianuarie 1908, Paris, Franța – 14 aprilie 1986, Paris, Franța) filozof existențialist, scriitoare, teoretician social și activistă feministă de origine franceză.  </p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (609, "Cesare Beccaria", "<p>(15 martie 1738, Milan, Duchy of Milan - 28 noiembrie 1794, Milan, Duchy of Milan) gânditor iluminist, jurist și publicist italian, autor al tratatului de drept penal „Despre infracțiuni și pedepse” (1764), „Cercetări privind natura stilului” (1770).</p>", 77);
INSERT INTO Author_Name (id, name, des, language_id) VALUES (610, "Samuel Barclay Beckett", "<p>(3 aprilie 1906, Foxrock, Dublin, Irlanda - 22 decembrie 1989, Paris, Franța) romancier, dramaturg, povestitor, regizor de teatru, poet și traducător literar irlandez.</p>", 77);
