INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181808, 'Cesta Tao, jestli má být Cestou Tao, není nikdy neměně trvalou Cestou, pojmenovatelné, jestli má být pojmenovatelným, není nikdy neměně trvale pojmenovatelným! Už na počátku, když ještě Nebe a Země nebyly pojmenovatelné, byla Matkou – rodičkou všech desetitisíc pojmenovatelných věcí! Proto: V čem ještě netrvá – přeje si vyjevovat svou tajuplnost. V čem už trvá – přeje si vyjevovat svou obratnost. To oboje vzniká současně a odlišuje se jen v pojmenováních. A to společné nechť je nazývané: Prvopočátek. Prvopočátek všech prvopočátků, výchozí brána všech tajuplností!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 1, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181809, 'V Podnebesí se poznáváním krásna jako krásna vymezuje i ošklivé, poznáváním dobra jako dobra se vymezuje i nedobré. A tak i bytí a nebytí se navzájem rodí, těžké a lehké se navzájem tvoří, dlouhé a krátké se navzájem porovnává, vysoké a hluboké se navzájem sklání, souzvuk a rámus navzájem ladí, přední a zadní se navzájem spojuje. Proto: Dokonalý Člověk koná nezasahováním a je příkladný beze slov a desettisíc věcí se udělá bez návodů, zrodí se bez přináležení, vykonají se bez donucování a úspěšně se dovrší bez dalšího setrvávání v tom. A jen proto, že v ničem nesetrvává, nic mu neuniká!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 2, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181810, 'Nevyzdvihuje nikoho jako úctyhodnějšího a tím mezi lidmi nevytváří rozbroje, nepřikládá nijakou větší cenu těžko získatelnému zboží a tím v lidech nepodněcuje snahy okrádat, nevystavuje na odiv nic žádostivého a tím v srdcích lidí nevytváří zmatek. A právě proto za vlády Dokonalého Člověka se mysli lidu vyprazdňují a  naplňují se jeho žaludky, jeho svéhlavosti se oslabují a posilňují se jeho kosti a svaly.  Tím se obvykle dosahuje toho, že lid nemá v hlavě mudrování ani v srdci skryté tužby. A od moudrých je nejmoudřejší nedovolovat si nijaké zasahování. Vždyť právě v konání nezasahováním je podstata uspořádané vlády!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 3, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181811, 'Cesta Tao, nekonečně plynoucí, je ve všem potřebná, ale nikde nepřetéká přes okraj. Ach, jak propastná je, jako by byla prarodičkou desetitisícům věcí! Potlačuje svojí ostrost, přemáhá svou neuspořádanost, zmírňuje svůj žár, ztotožňuje se se svým “prašným světem”. Ach, jak naskrz je prosáknutá, jako by byla opravdu existencí čehosi! Nevím, koho je potomkem, ale zdá se, že tu byla už před Nebeským Císařem!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 4, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181812, 'Nebe a Země netvoří z principu lidskosti, a tak s desetitisíci věcmi zacházejí jako se slaměnými psy! Dokonalý Člověk netvoří pro princip lidskosti, a tak se stovkami rodů zachází jako se slaměnými psy! Hle, to, co je mezi Nebem a Zemí, jako by bylo rozdmychávané měchem, prázdným a tedy nevzdorujícím, plným té největší síly a vydávajícím ze sebe stále víc a víc. Další slova jsou zbytečná. Stačí: “Není nad přidržení se Středu”!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 5, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181813, 'Duchovní Hlubina nikde nezaniká a to je právě to, čemu říkám “lůno prvopočátku”! A jeho otvor je právě to, čemu říkám “kořen přediva Nebe a Země”! To předivo jako by skutečně existovalo. Vždy potřebné a ničím se nerozrušující!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 6, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181814, 'Nebe – rozprostraněnost. Země – trvání. Nebe a Země jsou společně schopné rozprostraněnosti a trvání. Nezrodili se jen pro sebe, a proto jsou schopné dlouhé plodotvornosti. Dokonaly Člověk staví do pozadí svou osobu a tím ji dostává do popředí, staví stranou svou osobu a právě tím tu ona je! A protože netrvá na svých žádostivostech, právě proto je schopný tvorby své osobnosti!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 7, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181815, 'Nejvyšší dobro je jako voda. Voda je dobrá a potřebná pro desetitisíce věcí, i když nijak o to nezápasí. Nachází se i tam, kde je to podle mnohých lidí zlé a je teda v mnohém jako Cesta Tao.  Pro dobro obydlí – dobrý pozemek, pro dobro srdce – jeho hloubka, pro dobré lidské vztahy – princip lidskosti, pro správnou řeč – důvěryhodnost, pro dobré partnery – uspořádaná vláda, pro správnou činnost – správné schopnosti, pro každou dobrou aktivitu – správná včasnost. A jen proto, že o nic nezápasí, vším je!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 8, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181816, 'Přehnat něco, až to přeteče přes okraj – cožpak není lepší nechat to tak? Vypočítavě něco přeceňovat – cožpak není lepší nevěnovat tomu pozornost? Přeplňovat paláce zlatem a drahokamy, které neuhlídáš, setrvávat v přepychu a urozenosti a dospět tím k vlastní škodě?! Úspěšně završit a jít od toho – to je věčná Cesta Tao!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 9, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181817, 'Jednotná plnost ducha v jeho správných mezích umožní nerozrušenost! Projevování životní energie až do té nejjemnější měkkosti umožní zůstat dítětem! Očištění a odstranění toho, co brání nazírání do té nejbytostnější prapodstaty, umožní nedopouštět se chyb! Milováním lidu správně vládnout umožní nenamáhat se zbytečným přemýšlením! Ponechat, ať se brány nebes přirozeně otevírají a zavírají, umožní nebýt ve střehu jako samička při hnízdění! Na všechny čtyři strany jasné osvětlení umožní setrvávat v nezasahování! Narodící se, zkultivující se, má potom žít a nic nevlastnit, stále konat a neulpívat, prožít dospělost a nebýt “panováníchtivý”! A to je to, čemu říkám podstata energie Te!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 10, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181818, 'Třicet tyček obepnutých loukotěmi, to je nevyhnutelný předpoklad vozu, ale jeho upotřebitelnost vytváří i to co jeho “jest” i to co jeho “není jest”. Hrnčířské práce jsou nevyhnutelným předpokladem nádoby, ale jejich upotřebitelnost vytváří i to co její “jest” i to co její “není jest”. Práce s tesařskými nástroji, kterými se vyrábějí okna a dveře, jsou nevyhnutelným předpokladem domu, ale jeho upotřebitelnost vytváří i to co jeho “jest” i to co jeho “není jest”. Proto: To ono co “jest” tvoří užitečnost věcí, to ono co “není jest” vytváří jejich použitelnost!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 11, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181819, 'Pět barev současně oslepuje oko, pět zvuků současně ohlušuje ucho, pět chutí současně mate ústa, ohnivé koně a lov rozdivočí srdce a náhlé uzření vzácného zboží vzbuzuje v člověku chuť přivlastnit si to! Dokonalý Člověk tvoří pro žaludky a ne pro vidiny a proto se přidržuje tohoto a odmítá tamto.', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 12, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181820, '"V stavu milosti či nemilosti, co když se ozve najednou strach? V blahobytu či utýranosti, co když se ozve najednou mé tělo? Proč pravím: “V stavu milosti či nemilosti, co když se ozve najednou strach?” Stav milosti přináší i obavu pádu. A tedy co když se po dosáhnutí vysokého postavení ozve najednou strach? co když se po ztrátě vysokého postavení ozve najednou strach?! Proto je možné se opravdu obávat: “V stavu milosti či nemilosti, co když se ozve najednou strach?"" Proč pravím: “V blahobytu či utýranosti, co když se ozve najednou mé tělo?” Protože, jestliže jsem naopak schopný stavu odtělesnění, jakáže utýranost?! Proto: kdo žije ve váženosti, tělem učiní si Podnebesí, bude moci v Podnebesí přebývat, kdo žije v lásce, tak jako když si tělo zamiluje Podnebesí, bude mu moci být Podnebesí svěřené!"', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 13, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181821, '"To, na co se dívám a nevidím, toho pojmenovatelnost nazývám to “nejrozptýlenější”, to, co poslouchám a neslyším, toho pojmenovatelnost nazývám to ""nejnezřetelnější”, to, na co sahám a nedotýkám se, toho pojmenovatelnost nazývám to “nejminimálnější”. Ale tuto trojitost je nemožné nijak konkrétně pochopit! Protože její různost tvoří univerzálnost, její nejvyšší výška neoslepuje svojí září, její nejhlubší hloubka nezatemňuje svojí temnotou. Je to spletitost, která se nedá pojmenovat! Vždy znovu a znovu se navrací do nebytí. Do toho, čemu se říká “beztvará tvářnost nebytí”, do těch podob věcí, o kterých se dá říci, že jsou absolutně rozplynuté. Kráčíš tomu naproti a nevidíš toho hlavu, sleduješ to zezadu a nedohlédneš toho ukončení. Přidrž se Cesty minulosti, aby si se vyznal v přítomnosti! A schopnost poznávání počátků minulosti, to nazývám “pamětí Cesty Tao”!"', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 14, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181822, 'Ani učenci, usilující poznat klady minulosti, ačkoli pronikají až do těch nejjemnějších tajemstvích jejích počátků, nejsou schopní rozpoznat to do hloubky. Vždyť ani se to nedá rozpoznat! Proto: Je třeba se stále utužovat jako před přebroděním ledové řeky! A jak ostražitým je třeba při tom být! Jako kdyby hrozilo ze všech stran nebezpečí. A jak rozvážným je třeba přitom být! Jako ve chvílích, kdy člověk vykonává přivítací obřad. A jak všepronikajícím je třeba přitom být! Jako voda z roztávajícího se ledu. A jak je třeba považovat všechno za velmi důležité! Jako kdybys stál před sebou jako před neopracovaným dřevem. A jak vyprázdněným je třeba přitom být! Jako kdybys byl bezednou hlubinou. A jak stále nevyjasněným je třeba při tom být! Jako kdybys si sám sobě připadal jako hlupák. Vždyť jak jinak bys dokázal ze své původní nevědomosti dopracovat se do stavu ničím nenarušitelné vyrovnanosti?! Vždyť jak jinak bys dokázal ze své původní ustrnulosti dopracovat se do stavu té nejpřenesmírnější tvořivosti?! Ti, co usilují o takovou Cestu poznání, nikdy v ničem “nepřetečou přes okraj”! A jen proto, že v ničem “nepřetečou přes okraj”, jsou schopní nabírat do sebe stále víc a víc a konce v tom nikdy nedojdou!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 15, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181823, '"Dosahováním těžiště vyprázdněnosti dosahujeme stav jednoty. V desetitisících věcech, které se neustále uskutečňují, pozorujeme jev jako by stálého navracení se. Nespočetná množství myslících bytostí i nemyslících věcí jako by se znovu a znovu krouživě navracela ke svým kořenům. Navracení se ke kořenům nazývám “stavem utišení” a to je to, co je možné nezvat “znovuobjevením určení”. Znovuobjevením určení se vymezí i moje bytí a pochopení svého bytí, to nazývám ""pochopením svého správného smyslu""! Nepochopení svého správného bytí bývá sebeklam, který plodí strašná nebezpečenství! Z pochopení svého správného bytí vychází následné formy chování. Formy chování-- a následná veřejněprospěšnost. Veřejněprospěšnost – následná schopnost vládnutí. Schopnost vládnutí – následná účast Nebes, účast Nebes – následná účast na Cestě Tao, účast na Cestě Tao – a z toho následná dlouhověkost. A potom už ztráta těla není žádnou smrtelnou pohromou!"', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 16, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181824, '"“Za těch nejdokonalejších Vládců, níže postavení vědí sami od sebe, jak má co být, za dalších vládne strýčkování a podlézání, za dalších ustrašenost a za dalších už jen všeobecná nenávist! A kde není důvěry, tam zavládne nedůvěra!” Ó, jak dalekosáhlá jsou tato vzácná slova. Jen tam, kde se bude moci plodně pracovat, tak jak třeba, jen tam se budou moci lidé ve všech stovkách rodů upokojovat: ""Je to s námi přirozené!”"', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 17, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181825, 'Jak se začne velkolepá Cesta Tao narušovat, obvykle bývá snaha o princip lidskosti a konání v mezích správnosti a spravedlnosti! Jak začnou narůstat spekulace a podvody, obvykle vzniká obrovské úsilí o umělou nápravu! Jak vymizí soudržnost hlavních kmenů, obvykle vzniká snaha o princip synovské poslušnosti a bratrské lásky! A jak v zemích a rodech zavládne rozvrat a anarchie, obvykle začíná úsilí o princip věrnosti a občanské poddajnosti!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 18, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181826, '"Skoncujme s “dokonalostí” a odmítněme “mudrlanství” a lid tím stonásobně získá! Skoncujme s “principem lidskosti” a odmítněme “princip konání v mezích správnosti a spravedlnosti"" a lid se navrátí k synovské poslušnosti a bratrské lásce! Skoncujme se ""šikovností” a odmítněme “osobní prospěch” a nebude žádných zlodějů a lupičů! Vždyť všechny tyto tři druhy chování jsou projevy nekulturnosti! Proto: Nechť k vám od této chvíle přináležímít za vzor prostotu,   přidržet se přirozenosti, potlačovat sobeckost a snižovat žádostivost!"', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 19, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181827, 'Skoncujme s učeností a bude po starostech! Jedinečné a mnohé navzájem odstraňovat – na co? Dobré a zlé navzájem zahánět – na co? Vždyť čeho se jednou člověk bojí, toho se už nedokáže nebát! Ach, kolik neuvážeností a konce jim není! Davy se valí, vlna za vlnou, jako kdyby chodili s požitkem se zúčastňovat krvavých obřadů v obětních ohradách, jako kdyby chodili s požitkem se zúčastňovat slavnostních jarních obětí na obřadných terasách! Ó, jen já jediný jsem lhostejný a nikam nepospíchám, jako novorozeně co ještě není ani dítětem! Jsem lhostejný, jako kdybych už netoužil se nikam navracet! Davy se pachtí po nadbytku, jen já jediný bychse všeho nejraděj zbavil! Moje myslje jako mysl prosťáčka. Ó, jak je neupřená na nic! Ostatní lidé jsou přemoudřelí, jen já jediný jsem jako zaslepený! Ostatní lidé se pachtí po vědomostech a věcem se chtějí dostat na kloub, jen já jediný jsem zasněný a od všeho odpoutaný! Ó, jak nekonečně klidný jsem, jako oceán, ó, jak vítr na vrcholcích hor, kterého už nic nezastaví! Davy lidí touží mít vše kvůli něčemu, jen já jediný jsem tvrdohlavý, jako kdybych šel hlavou proti zdi! Já jediný jsem jiný než ostatní?! Protože si vážím Matky – Živitelky!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 20, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181828, 'Projevující se energie Te je ve skutečnosti jen sled Cesty Tao. Avšak to, jak se Cestou Tao věci uskutečňují, je nevypočitatelné jako dravý živel. Ó, jaká nevypočitatelná spontánnosta šílená dravost! Jako kdyby tato spontánnost a dravost byly v jádře Její základní podobou! Ó, jaká promíšenost, jako kdyby byli v Jejím jádře ukryté všechny věci! Ó, jaká nerozeznatelnost, jako kdyby byla v jádře kvintescencí všech věcí a jako kdyby tato kvintescence byla v jádře tou Její nejpravdivější pravdou! Jako kdybybyla v jádře Její pravou věrohodností! Od minulosti až do přítomnosti se nic, co bylo pojmenované na Cestě Tao, neztrácí a vše by se dalo ověřit. Jak vím, že to bylo tak, jako by se to právě nyní stalo?! Jednoduše proto, protože to tak bylo!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 21, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181829, '"Droboulinké – později úplné, křivé – později rovné, navlhnuté – později přetékající téměř přes okraj, prastaré – později nové, málo – později mnoho, mnoho a vzápětí zklamání! A tak tedy, aby Dokonalý Člověk skutečně obsáhl univerzálnost a byl vzorem v Podnebesí, nehledí na sebe a proto je jasnozřivý, nedává sebe za pravdu a proto je obdivuhodný, není průbojný a proto je úspěšný, není namyšlený a proto je v popředí a jedině proto, že s nikým nezápasí, není v Podnebesí nikoho, kdo by si dovolil zápasit s ním! Vždyť už odedávna říkali: ""Nejprve malilinké a nakonec celé!"" Nuže, cožpak by to byla bývala jen prázdná slova?! A právě dožadováním se úplnosti dosahuje se návratnost!"', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 22, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181830, 'Hovořit co nejméně je přirozené. Vždyť ani tajfun nebouří až do rána a ani průtrž mračen netrvá celý den. Co je způsobuje? Nebe a Země. A když ani Nebe a Země nedokáží nic způsobovat dlouhodobě, tak co potom člověk! Proto: Postupujte činorodě na cestě Tao! A tím se s Ní ztotožníte, A tím se ztotožníte i s Její tvořivou energií Te! Avšak kdo se stane součástí Její ztráty, bude i ztotožněním se s Její ztrátou. Kdo se ztotožní s Cestou Tao, pociťuje radost z Cesty Tao! Kdo se ztotožní s Její tvořivou energií Te, pociťuje radost z Její tvořivé energie Te! Avšak kdo se ztotožní s Její ztrátou, bude mít radost jen z Její ztráty! A kde zanikne důvěra, tam vznikne jen nedůvěra!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 23, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181831, 'Kdo se staví na špičky, přestává stát pevně, kdo ustrne v půli kroku, přestane kráčet, kdo hledí jen na sebe, přestává být jasnozřivý, kdo dává jen sebe za pravdu, přestává být hodný obdivu, kdo odporuje, přestane být úspěšný a kdo se jen sám sebou pyšní, nebude dlouho v popředí! Kdo však postupuje Cestou Tao, ten si řekne: “Ach, jak je malicherné se přejídat a pachtit se za věcmi!” Proto: Kdo ztělesňuje Cestu Tao, nikdy v tom nesetrvává!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 24, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181832, 'Existovalo cosi jako rozvířený proud ještě před zrodem Nebe a Země. Ach, jak osamělé a pusté ve své samojedinosti trvalo beze změn! Krouživě plynulo ničím neohrožované. Možná, že proto se stalo Matkou Podnebesí! Nevím toho pojmenování a proto to označuji znakem: Cesta. A když jsem přeci nucen pojmenovat ji názvem: Obrovská! Co je obrovské, o tom je možné říci, že je rozpínavé. Co je rozpínavé, o tom je možné říci, že je nekonečné. Co je nekonečné, o tom je možné říci, že je navracející se. Proto: Cesta Tao je Obrovská, Nebe je Obrovské, Země je Obrovská, Člověk je obrovsky Veliký! Jádrem tohoto vymezení jsou tedy čtyři velikosti a Člověk je jednou z nich! Zákony Člověka podmiňuje Země, zákony Země podmiňuje Nebe, zákony Nebe podmiňuje Cesta Tao a Cesta Tao je samasebou!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 25, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181833, 'Těžké je kořenem lehkého, pokojné je pánem upachtěného! A tak tedy Dokonalý Člověk se ve svém každodenním konání nikdy neodtahuje od břemene povinností a kdyby měl před sebou jakékoliv vidiny blahobytu, zůstává nad tím povznesený jako vlaštovka ve výšinách. Vždyť má snad vládce desetitisícové armády právo, kvůli své osobě, brát na lehkou váhu celé Podnebesí?! A jakmile ho bere na lehkou váhu, velmi rychle v něm ztratí oporu. A kdo se takto poblázní, ztratí i panovnickou moc!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 26, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181834, 'Kdo správně postupuje, nevybočí z kolejí, kdo se správně vyjadřuje, neudělá chyby v argumentech, kdo správně počítá, nepotřebuje si dělat zářezy na bambusu, kdo správné dveře zavírá, nepotřebuje zámky ani závory, když by je někdo chtěl otevřít a kdo správně váže, nepotřebuje přediva ani zauzleniny, když by to někdo chtěl rozvázat. Proto: Dokonalý Člověk trvale koná správně a v zájmu lidí, a proto  lidi nepodceňuje, trvale koná správně v zájmu věcí, a proto ani věci nepodceňuje.  O něm se skutečně tedy dá říci, že všechno jasnozřivě předvídá a všemu dokonale porozumí! Proto: Dobře a správně se chovající k lidem nechť jsou učiteli těch, co se k lidem chovají zle, zle a nesprávně se chovající k lidem nechť jsou materiálem pro ty, kteří se k lidem chovají dobře! A kdo by si nevážil svého učitele a necenil svůj materiál, nechť by byl jakkoliv moudrý, dopustí se tím hrozného omylu! A potom se skutečně už jen dá říci: “To chce zázrak!”', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 27, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181835, 'V aktivním pochopení svojí mužskosti a přidržení se své ženskosti tvoří se řečiště Podnebesí a v tvořícím se řečišti Podnebesí se trvale a neporušitelně tvořivá energie Te znovu a znovu navrací do stavů novorozeňat. V aktivním pochopení svojí “běloby” a v přidržení se své “černé” se tvoří kontury Podnebesí a v tvořících se konturách Podnebesí se trvale a neporušitelně tvořivá energie Te znovu a znovu navrací do stavů nulovosti. V aktivním pochopení svojí odvahy a v přidržení se svého studu se tvoří škála hloubek Podnebesí. a v tvořivé se škále hloubek Podnebesí se trvale a neporušitelně tvořivá energie Te navrací do stavů základní přirozené prostoty. A jak se působení této prostoty rozšíří na všechno, stane se to i nástrojem vlády. A jak si nástroj takové vlády osvojí Dokonalý Člověk, potom se takovými stanou i všichni jeho hodnostáři a starosvatové rodů. A tak potom “zákony přestanou stínat hlavy!”', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 28, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181836, 'Vůdce armády se rozhodne zmocnit se Podnebesí a podle toho koná. Avšak já už vidím, že nedosáhne vůbec ničeho! Vždyť Podnebesí je velmi subtilní nádoba, s kterou je nemožné zacházet jakkoli! Takovým zacházením ji jen zničíte a násilným zmocněním se jí ji ztratíte! Protože: věci buď postupují anebo sestupují,  buď se vstřebávají anebo vytryskávají, buď sílí anebo slábnou, buď bují anebo zanikají!  A tak tedy Dokonalý Člověk odmítá krajnosti, odmítá výstřednosti, odmítá přehnanosti!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 29, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181837, 'Na Cestě Tao být prospěšným vůdcem lidí, to neznamená vojskem Podnebesí násilně k něčemu donucovat. Protože takové konání se rádo zvykne vracet! Vždyť, kde se rozloží armáda, tam zůstane jen trní a bodláčí a po vpádech obrovských vojsk nastanou nevyhnutelné roky utrpení! Plodem dobrého úsilí rozhodněnení uzurpování,  není zpupná pýcha, není dělání si protivenství, není bezohlednost, není podmaňování si věcí a není násilí!  Až když věci neustále plodně rozkvétají, až tehdy se stáváte úctyhodným člověkem! Doopravdy vám radím: Nescházejte z Cesty Tao! Kdo sejde z Cesty Tao, velmi rychle je sním konec!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 30, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181838, 'I kdyby vypadaly jakkoliv slibně, vojenské zbraně nejsou šťastné prostředky a věcem jsou jen na škodu! Proto v tom Cesta Tao nespočívá! Když knížata setrvávají v pokoji, potom převahu získává směřování doleva, když knížata začnou zacházet se zbraněmi, potom převahu získává směřování doprava. Vojenské zbraně tedy opravdu nejsou šťastné prostředky a nemají být nástroji ušlechtilého člověka! Proto on po nich nikdy nesahá a jediné, co klade nadevše, to jsou mírová vyjednávání. Vždyť vítězství pomocí ohavností, to ve skutečnosti znamená mít zálibu ve vraždění lidí a zálibou ve vraždění lidí se nedá dosáhnout prosazení své vůle v Podnebesí! Šťastnými životními skutky se směřuje doleva, nešťastnými životními skutky se ustupuje doprava! Ale poražený generál mívá obřadné místo vlevo,  vítězný generál mívá obřadné místo vpravo.  No povězte, zdalipak to není jako rozmístnění na pohřbu? a či množství pozabíjených lidí není důvodem pro nářky?! Tak tedy vojenské vítězství opravdu není nic jiného než důvod pro pohřební ceremoniál!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 31, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181839, 'Cesta Tao ve své prapůvodní jednoduchosti trvala už před vznikem pojmenovatelností a v Podnebesí nepotřebovala ani v tom nejmenším nikdy žádného pomocníka! Kdyby jen lénní páni a králové byli schopní se Jí přidržet, všechny desetitisíce věcí by se samy od sebe přidržely svých určení a Nebe a Země by byly v takové vzájemné harmonii, že na všechno jako kdyby “padala sladká rosa”. A lid by byl potom sám od sebe takový, jaký má být! No, ale i vydávání příkazů a nařízení jsou pojmenovatelnosti a při pojmenovatelnostech má moudrý člověk vždy vědět, kdy přestat! Vždyť když ví, kdy přestat, potom je naděje, že to neskončí pohromou! A jestliže bych byl nucený použít ještě jedno přirovnání, tak tedy: Cesta Tao je v Podnebesí jako koryto a hlubina pro řeky a moře!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 32, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181840, 'Poznat jiné – to je poznání, poznat sebe – to je osvícení! Zvítězit nad jinými – to je síla, zvítězit nad sebou – to je mohutnost! Umět se spokojit – to je pravé bohatství, být neochabující ve svém konání – to je pravá vůle! Nevzdávat se toho, jakým mám být – to je vytrvalost! A potom zemřít a nepominout – to je dlouhověkost!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 33, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181841, 'Ó, velkolepá je Cesta Tao, schopná plynout doleva i doprava! Od tebe závisí zrody desetitisíci věcí i to, že ony bez rozkazu spějí ke svým plodným výsledkům! Neděláš slávu z toho, že šatíš a živíš desetitisíce věcí veškerenstva a ani se neděláš jejich vládkyní! Trvale a navždy si ani v nejmenším nepřeješ honosit se tím! V koloběhu navracejících se desetitisících věcí veškerenstva nikdy si nehraješ ani na jejich vládkyni, ani na jejich velitelku! A právě proto je Tě možné pojmenovat Velikou, protože sama se opravdu nikdy Velikou neděláš! Proto: Jsi doopravdy schopná Velikosti!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 34, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181842, 'Pevně se přidržujíc Její Velikosti, Podnebesí se začne ubírat Jejím směrem! A ubírající se Jejím směrem, nebude ohrožená ani jeho spokojenost, ani jeho vyváženost! Lahodná hudba a výborné pokrmy jsou schopné zastavit putujícího. Ale to, co vyzařuje z Cesty Tao,  vychutnáváš a nedovychutnáš se toho, díváš se na to a nevynadíváš se na to, posloucháš to a nedoposloucháš se toho, užíváš to a nikdy nejsi schopný to vyčerpat!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 35, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181843, 'Jestliže je přání něco stáhnout, muselo to být původně dlouhé, jestliže je přání něco zeslabit, muselo to být původně silné, jestliže je přání něco odstranit, muselo to nejdříve někde povstat, jestliže je přání něco vyjmout, muselo to nejdříve k něčemu přináležet! A tím se dá opravdu vysvětlit a až do těch nejmenších podrobností objasnit i to, proč měkké a slabé nakonec vítězí nad tvrdým a silným! Avšak tak jako ryby není možné vyhnat z jejich vodních hlubin, tak ani nástrojem prospěšnosti země nemůže být: obětovat lid!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 36, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181844, 'Cesta Tao se vždy uskutečňuje nezasahováním! Kdyby si knížata a králové přidrželi této zásady, desetitisíce věcí by se samy od sebe přetvářely ve své přeměny. Ale kdyby chtěl někdo do procesu přeměn přece zasahovat, musí mít na paměti zásadu nevypověditelné prostoty! A k zásadě takovéto prostoty patří i stav bezžádostivosti! A kde není žádostivost, tam je klid a Podnebesí se usměrňuje samo od sebe!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 37, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181845, 'V nejvyšším stupni se energie Te jako taková neprojevuje, a přeci takovou je, v nejnižším stupni energie Te sama sebe nešetří a přeci tvořivou už není. V nejvyšším stupni energie Te nic nevykonává, a přeci není ničeho, co by bylo třeba ještě vykonat, v nejnižším stupni energie Te koná a koná, a přeci všechno je ještě třeba vykonat! V nejvyšším stupni principu lidskosti netřeba do ničeho zasahovat, v nejvyšším stupni principu konání a v mezích správnosti a spravedlnosti je třeba jak do čeho zasahovat a v nejvyšším stupni dodržování pravidel obřadnosti není už ničeho, co by nebylo vhodné, a tak už ani nahá ramena nejsou pohoršující! Proto: Po ztrátě Cesty Tao zůstává vám přeci ještě energie Te, po ztrátě energie Te zůstává vám ještě princip lidskosti, po ztrátě principu lidskosti zůstává vám ještě alespoň princip konání v mezích správnosti a spravedlnosti a po ztrátě principu konání v mezích správnosti a spravedlnosti zůstává vám ještě alespoň obřadné chování! Avšak kdo ztratí už i obřadnost, jeho důvěryhodnost a věrnost se stanou tak chatrné, že mu už zůstane jen zmatek v hlavě! A když se všechny jeho předcházející znalosti přemění jen v květnatou okázalost jeho Cesty Tao, tak to už bude počátek skutečné hlouposti! A tak tedy Veliký a kultivovaný vzdělanec setrvává jen v Její nejvyšší tvořivosti a ne v Její ochablosti, jen v Její skutečné opravdovosti a ne v Její falešné květnatosti! Proto: Odmítněte tamto a držte se tohoto!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 38, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181846, '"Už od pradávna platí: je třeba dosahovat jednotnost! Nebesa dosahují jednotnost svojí průzračností, Země dosahuje jednotnost svojí zhuštěností, duchovno dosahuje jednotnost svojí nadpřirozeností, hlubina dosahuje jednotnost svojí naplnitelností, desetitisíce věcí dosahují jednotnost svými zrozeními a další a další vládcové dosáhnou jednotnost tím, že budou přivádět Podnebesí k šťastnému osudu! Kdyby Nebesa nebyly průzračné, hrozilo by jim zřícení, kdyby Země nebyla zhuštěná, hrozilo by jí vykypění, kdyby duchovno nebylo nadpřirozené, hrozila by mu smrtelnost, kdyby hlubina nebyla naplněná, hrozilo by jí vyčerpání, kdyby desetitisíce věcí nebyly neustále zrozovány, hrozilo by jim vyhubení a kdyby další a další vládcové přestali být úctyhodní, hrozilo by jim svrhnutí! Proto: Vznešenost ať si klade za podklad zuboženost, vysoce vážená úctyhodnost ať si klade za podklad poníženost! “Ale vždyť naši nejnovější vládcové často o sobě hovoří jako o ubohých opuštěných sirotách! Či snad to není pravý podklad zuboženosti?!” ""Ne!"" Protože jestli je někde hodně vozů, tam přeci vůz nechybí! A nemusí si přát stát se drahokamem to, co už je nefritem, ani ozdobou to, co už je ozdobným kamenem!"', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 39, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181847, 'Ve zvratech je pohyb Cesty Tao, v poddajnosti je upotřebitelnost Cesty Tao! A tím se desetitisíce věcí neustále rodí do svého bytí a z bytí do nebytí!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 40, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181848, 'Když vysoce postavený vzdělanec uslyší o Cestě Tao, vzchopí se a začne v ní postupovat, když uslyší průměrný vzdělanec o Cestě Tao, zaváhá, jestli něco takového vůbec je a když uslyší podprůměrný vzdělanec o Cestě Tao, pustí se do hrozitánského smíchu. Ale vždyť ani by to nebyla Cesta Tao, kdyby se jí takový člověk nesmál! Obvykle se přeci říkává: Osvětlováním Cesty Tao, jako kdyby se naopak zatemňovala, vstupováním na Cestu Tao, jako kdyby se z Ní naopak ustupovalo a jednoduchostí Cesta Tao, jako kdyby se naopak komplikovala! V nejvyšší míře je energie Te jako by hlubinou, v nejčistější míře je energie Te jako by hanebností, v nejsoustředěnější míře je energie Te jako by neschopností, v nejspolehlivějším stavu je energie Te jako by nespolehlivostí, v nejopravdovějším stavu je energie Te jako by nestálostí. Její největší čtvercovitost je jako by bez rohů, Její největší zvuk je jako by nezvučný, Její největší tvar je jako by beztvarý! Cesta Tao je tajuplnost a nepojmenovatelnost. Ale jedině Cesta Tao neustále všechno dokonale vytváří!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 41, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181849, 'Cesta Tao tvoří Jedno, Jedno vytváří Dvě, Dvě vytváří Tři, Tři vytvářejí desetitisíce věcí, desetitisíce věcí neustále odnáší proud “jin” a přináší proud “jang”, a v tom plynoucí živoucí energie čchi je na to, aby všechno zharmonizovala. Nejnezáviděníhodnější je být opuštěnou sirotou. A přeci se knížata a králové rádi tak titulují! Protože věci jsou buď rozrušované a odstraňované, aby opět povstávaly, a nebo jsou na svůj vrchol vynášené, aby vzápětí zanikaly! To je velmi poučné pro lidi a proto je tu učení, které i já hlásám! Silnému nosníku zlom nehrozí a tak ani mně, otcovi tohoto učení!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 42, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181850, 'V Podnebesí z té nejútlejší útlosti jako by cvalem dospíváme do stavu nejstuhnutější strnulosti. A nebytím vkročíme do stavu neaktivity. Z toho jsem nejlépe pochopil prospěšnost nezasahování! A právě příkladnost beze slov a prospěšnost nezasahování, to je to nejcennější, co se dá v Podnebesí dosáhnout!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 43, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181851, 'Pověst či tělo – co z toho je ti bližší? Tělo či majetek – co z toho je ti více vhodné? Dosáhnutí slávy či riziko záhuby – co z toho je škodlivější? A to je ten pravý důvod, proč se za přehnané oddávání lásce tak draze platí, proč se velkým hromaděním bohatství dospívá k pohromě! Jestliže se víš uspokojit – nebudeš zostuzený, jestliže víš, kdy přestat – nebudeš ohrožený. A tak se dožiješ dlouhého trvání!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 44, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181852, 'Největší dovršení jako by bylo mrháním, ale jeho potřebnost není závratí, největší přeplněnost jako by byla rozlitím, ale její potřebnost je zbytečností, největší přímočarost jako by byla ohnutím, největší zručnost jako by byla nešikovností, největší výřečnost jako by byla jen bídným hlesnutím! Rychlou chůzí přemůžeš chlad a upokojením přemůžeš rozpálenost. Je tady jasné, že vyrovnáváním se vytváří správný stav Podnebesí!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 45, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181853, 'Když se v Podnebesí uskutečňuje Cesta Tao, koně ořou a hnojí pole, Když se v Podnebesí neuskutečňuje Cesta Tao, váleční koně se prohánějí zemí. A není věru větší pohromy, než nevědět kdy se uspokojit a není věru osudnější chyby, než stát se chamtivým! Proto: Až ve schopnosti umět se spokojit, je trvalé uspokojení!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 46, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181854, 'I bez vykročení z dveří se dá poznat Podnebesí i bez vyhlédnutí z okna se dá odpozorovat věčná Cesta Tao! Vždyť čím dále zacházíš, tím méně je tvoje mysl schopná obsáhnout! A tak tedy Dokonalý Člověk poznává bez toho, aby za tím chodil,  pojmenovává bez toho, aby to viděl a završuje bez toho, aby do toho zasahoval!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 47, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181855, 'Věnovat se studiu, tomu se říká: “úspěšně vzestupovat”, věnovat se Cestě Tao, tomu se říká: “způsobovat si škodu!” Ale vždyť stále dalším “způsobováním si škody” dospívám do stavu nezasahování! A v důsledku nezasahování není potom nic, co by se nevykonalo! Udržet si Podnebesí, to chce trvale do ničeho nezasahovat! Protože, jak do toho zasahujete, nedokážete si Podnebesí udržet!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 48, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181856, 'Dokonalý Člověk i bez toho, aby se staral o starosti stovek rodů, stará se o ně! Vůči dobrým a schopným jsem dobrý, vůči zlým a neschopným jsem taktéž dobrý, - to je tvořivá energie Dokonalého Dobra! Vůči důvěryhodným jsem důvěryhodný, ale i vůči nedůvěryhodným jsem důvěryhodný, - to je tvořivá energie Důvěryhodnosti! A Dokonalý člověk, i kdyby byl v Podnebesí úplně nesmělý a uzavřený, stejně má stále na mysli celé Podnebesí. Vždyť pro Dokonalého Člověka jako by Ono bylo jeho dítětem!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 49, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181857, 'Příchod – narození, odchod – smrt. Životní dospívání – jedna třetina, životní odumírání – jedna třetina, aktivní život mezi dospíváním a odumíráním – též jedna třetina! Proč?! Jednoduše proto, aby život dosáhl svého naplnění! Kdo poslechne, jak správně žít, tomu ani v nejhlubší divočině nepřetne cestu tygr či jednorožec, toho ani v nejzuřivějším boji nezasáhne žádná zbraň, protože nebude toho jednorožce, který by do něho vrazil,   nebude toho tygra, který by ho drapnul, nebude toho vojáka, který by na něho vytasil meč!   Proč?! Jednoduše proto, že toto není ještě místo jeho smrti!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 50, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181858, 'Cesta Tao je rodí, energie Te je živí, věci je formují a fyzická síla je dovršuje! A tak tedy pro desetitisíce věcí veškerenstva není opravdu nic důležitější než respektovat Cestu Tao a vážit si Její tvořivé energie Te! Respektovat Cestu Tao a vážit si Její tvořivé energie Te žádný příkaz nepřikazuje a je to teda spontánně přirozené! Proto: Cestou Tao zrozený a energií Te živený, dospějící, zesilnící, zesouladící se, zformující se, zeživotaschopnící se a rozmnožící se, má potom žít a nevlastnit, stále konat, ale neulpívat, žít v dospělosti avšak “nebýt panovačný!” A to je skutečně to, čemu se říká: Pravá energie Te!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 51, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181859, 'Jestliže má Podnebesí počátek, má tedy i Matku. A kdo dospěje k tomu, že má Matku, pochopí, že je dítětem. A kdo dospěje k tomu, že je dítětem, dospěje k opětovné přítulnosti ke svojí Matce. A tehdy už ztráta těla není žádným neštěstím! Zataras vstupy svého nitra a uzavři jejich brány a tvoje tělo bude bez námahy! Rozevři vstupy svého nitra a uvolni jejich průchodnost a neubráníš si tělo od vyčerpání! Co nejméně očima lačnit po věcech, tomu říkám: moudrost! A přidržet se přitom původní jemnosti, tomu říkám: síla! Využívajíc Její zářivou osvětlenost, navracíte se k jasnozřivé moudrosti! A potom zřeknutí se tělesnosti nebude žádnou pohromou. A to je to, čím se dá obnovovat trvalost!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 52, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181860, 'Jestliže já, který mám omezené vědomosti, hodlám kráčet velkolepou Cesto Tao, jen z jednoho jediného mám strach! Avšak velkolepá Cesta Tao je ve skutečnosti velmi prostá a přeci si Ji lidé ještě rádi zkracují! Vladařský dvůr si žije v odtrženosti, dokud vydrancované pole jsou zpustlé a veřejné sýpky zejí prázdnotou. Avšak oděvy vrchnosti jsou vyparáděné, z pod opasků jim ostré meče vytrčují, jídlem a pitím se bezostyšně přežírají, zbožím a bohatstvím se přecpávají. A to už je třeba opravdu nazvat zlodějstvím! Takové “hrdinství” věru není Cesta Tao!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 53, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181861, 'Co je dobře zakořeněné, to se nedá vytrhnout, co je dobře přichycené, to je nemožné odcizit! A tak ani tradici rituálních obětí předkům, vykonávaných syny a vnuky, je nemožné přerušit! Uvést se do správného pořádku, stává se moje energie skutečně tvořivou, uvést svoji rodinu v kultivovaný pořádek, stane se její tvořivá energie mnohonásobnou, uvést svoji rodnou občinu v kultivovaný pořádek, stane se její tvořivá energie skutečně dalekosáhlou, uvést svojí zem v kultivovaný pořádek, stane se její tvořivá energie skutečně vrcholnou a uvést celé Podnebesí v kultivovaný pořádek, stane se jeho tvořivá energie opravdu univerzální! Proto: Co se týká svého těla, pozorně na něj dohlížejte! Co se týká rodiny, pozorně na ní dohlížejte! Co se týká rodné občiny, též na ní pozorně dohlížejte! Co se týká země, i na ní pozorně dohlížejte! A co se týká celého Podnebesí, i na to je třeba pozorně dohlížet! Jenže, jak bych mohl rozumět celému Podnebesí?! Jednoduše takto!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 54, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181862, 'Ten, kdo má hojnost tvořivé energie Te, je přirovnatelný k novorozeněti: ani píchnutí hmyzem mu nehrozí, ani uštknutí hadem mu nehrozí, ani roztrhání dravou zvěří mu nehrozí, ani uchvácení dravým ptákem mu nehrozí! I když má slabé kosti a měkké svaly, pevně se drží Matky – Živitelky! A i když přitom nemá pomyšlení na spojení muže a ženy, je schopný dovést svůj plodivý akt k vrcholnému dovršení! Vědět jak tvořit součinně, to nazývám: trvalost! Vědět jak tvořit trvale, to nazývám: moudrost! A tím rozhojňovat život, to nazývám: šťastný osud! Srdcem a myslí správně působit na svojí životní energii čchi, to nazývám: silou! Avšak když se věci jen fyzicky posilňují, rychle stárnou a tomu říkám nesledovat Cestu Tao! A kdo nesleduje Cestu Tao, s tím je velmi rychle konec!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 55, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181863, 'Být moudrý neznamená hodně hovořit, hodně hovořit neznamená být moudrý! Zatarasovat vstupy svého nitra, uzavírat jejich brány, obrušovat svou ostrost, uvolňovat svou vyhraněnost, zmírňovat svou zářivost a vědět se rozumně ztotožnit s naším prašným světem, to je to, co nazývám tím prapůvodním ztotožňováním se! Proto: je nemožné o nic usilovat jen proto, že mi je to drahé, je nemožné o nic usilovat z vlastní rozhazovačnosti, je nemožné o nic usilovat ze ziskuchtivosti, je nemožné o nic usilovat ze zlomyslnosti , je nemožné o nic usilovat z hrabivosti, je nemožné o nic usilovat z ochoty zbídačení se! Proto: Konejte jen ve snaze tvořivého obohacování Podnebesí!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 56, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181864, 'Ať jsou země spravovány správně, ať je používání vojsk co nejzřídkavější a ať se v Podnebesí nezasahuje do přirozeného chodu věcí! Proč jsem dospěl k takovémuto závěru?! Pro toto: Dost už bylo v Podnebesí zastrašování a vyhrožování, ale osudem lidí zůstává neustále zbídačování! Lidé mají stále víc vražedných nástrojů a v zemích i v rodinách to vyvolává stále větší hnilobu a lidskou tupost! A čím jsou lidé vynalézavější, tím víc je věci popuzují k buřičství,  tím více jsou zákony jen květnatými řečmi a zlodějů a sdíračů je stále víc a víc!  Proto: Dokonalý Člověk si řekne: Nebudu zasahovat, nechť se lid sám přirozenou cestou mění! Budu dobrý a mírumilovný, nechť je lid sám od sebe správný! Nebudu nic činit násilně, nechť lid sám od sebe bohatství zvelebuje! A nebudu vyvolávat ani žádostivosti, nechť lid sám od sebe žije ve své přirozenosti!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 57, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181865, '"Když bude jeho vláda umírněná, lid bude vůči němu upřímný, když bude jeho vláda neúprosná, lid bude ve vztahu k němu bez zábran! “Ó, vždyť od štěstí k neštěstí je tak náramně blízko! Ó, vždyť neštěstí je častým přemožitelem štěstí! Tak jak rozpoznám, kde je čeho krajní hranice a co je kvůli čemu nesprávné?!"" Když se správné naopak stane zřídkavým, když se dobré převrátí v téměř výstřední, tak potom lidská pobloudilost, říkám vám, bude vládnout navěky! A tak tedy Dokonalý Člověk musí býtzásadový, ale neohrožující, důsledný, ale neubližující, přesný, ale neohraničující, zářivý, ale neoslepující!"', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 58, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181866, 'Pro správné spravování lidu i službu Nebesům není nic důležitější, než skromnost a sebeukázněnost! Vždyť jen skromnost a sebeukázněnost jsou skutečně tím, co se dá nazvat předvídavostí! A předvídavost je pro vrcholnou míru tvořivé energie Te, říkám vám, to nejdůležitější! Vždyť ve vrcholné míře tvořivé energie Te neexistuje, aby jste byli ve svém konání nepřiměření. A tomu, kdo ve svém konání není v ničem nepřiměřený, se nestává, aby nepoznal těžiště své pravé osy! A komu se nestane, aby nepoznal těžiště své pravé osy, ten je hoden vlastnit zemi. A když se stane Matkou – živitelkou své země, je hoden nadlouho v tom setrvávat! A tak potom skutečně je možné o něm říci, že má hluboké kořeny a pevné základy! A to je Cesta dlouhé a plodné životaschopnosti!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 59, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181867, 'Skutečně správně spravovat obrovskou zemi je podobné umění smažit křehkou rybičku! A jestliže se Podnebesí řídí Cestou Tao, duše zesnulých se po smrti nestanou démony. A jestliže se duše zesnulých nestanou po smrti démony, jejích démonství nebude ubližovat lidem. A jestliže jejich démonství nebude ubližovat lidem, potom ani Dokonalý Člověk nebude nucený nikoho poškodit. A tak si tyto dva světy nebudou vzájemně ubližovat! Protože tvořivá energie Te v nich bude proudit nepřerušeně!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 60, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181868, 'Když se velká země stane jako by dolním tokem Podnebesí, stává se jeho spojnicí, stává se jako by jeho “samičkou”. A samička nad samečkem obvykle vítězí “uspokojením se”, a to tím, že s uspokojením přijme svoje místo “dole”. Proto: Velká země právě tím, že vůči malé zemi přijme místo “dole”, získá si ji! A malá země tím, že vůči velké zemi přijme místo “dole”, též získá! A tak jedna přijímá místo “dole”, aby získala,   druhá přijímá místo “dole”, aby tím získala!   Velké země si musí neustále přát sjednocovat a shromažďovat lid, malé krajiny si mají neustále přát být ve službách lidstva. Ale každá svého přání dosáhne až tehdy, když bude klást svojí “velikost” opravdu úplně dospod!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 61, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181869, 'Cesta Tao ve svém trvání je pro desetitisíce věcí tajemstvím, je pro správné lidi drahocenností, je pro nesprávné lidi záchranou! Ale i pěknými řečičkami je možné všelicos vyhandlovat,  i honosným chováním je možné získat lid  a tak jak se vyvarovat lidských nesprávností?! Právě proto byl ustanoven statut Syna Nebes, byly vytvořené tři stupně veřejných hodností! Avšak ať by vás poctili jakkoli drahými kočáry, vpředu s bujným čtyřspřežím, nikdy se to věru nevyrovná usazení se a nastoupení do toho, co je Cesta Tao! Vždyť právě proto si už v dávnověku nejvíce vážili toho, čím je Cesta Tao! Neříkali přeci:  “Usiluj se a určitě Ji dosáhneš a budeš Ji mít! Jen chyb se přitom vyvaruj!?”  Proto: Už kvůli Podnebesí si Ji važte!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 62, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181870, 'Vykonává nezasahováním, uskutečňuje nečinností, ochutnává nevychutnáváním, tvoří velké z malého, mnohonásobné z nemnohého, na zlo odpovídá činorodou a tvořivou energií Te, dopředu vyřeší obtížné pomocí toho, co je prosté, vytvoří velké z toho, co je malé a nepatrné. Všechny těžko řešitelné věci v Podnebesí nevyhnutně začíná od jednoduchostí, všechny velké činy v Podnebesí nevyhnutně začíná od drobností! A proto Dokonalý Člověk se nikdy nedělá Velikým! A právě proto je schopný svoji Velikost vytvářet! Lehkovážností se nevyhnutně připravíte o důvěru, zjednodušováním nevyhnutně mnohé zkomplikujete! A tak tedy Dokonalému Člověku právě proto, že všechno považuje za rovnocenně obtížné, nic není nakonec skutečně obtížné!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 63, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181871, 'Co je spokojené, lehko se dá spravovat, co ještě není ve věštební rýhy vypálené, lehko se dá předznamenat, co je křehké, lehko se dá rozemlít, co je droboulinké, lehko se dá rozprášit. Co tedy ještě není uskutečněné, to se dá vykonat, co není rozvrácené, to se dá uspořádaně spravovat! Strom, který sotva obejmeš, se zrodil z maličkého klíčku, devítiposchoďová pagoda vyrostla z drobných zrnek zeminy, tisíc li dlouhá cesta začala prvním vykročením. Ale zásahem do toho je možné všechno zničit, uchvacováním toho můžeme to ztratit! A tak tedy vpravdě Dokonalý Člověk právě tím, že nezasahuje, nic neničí, právě tím, že neuchvacuje, nic neztratí! Mnohé lidské skutky bývají už skoro vykonané a právě tehdy se všechno najednou zmaří! Je potřebné, abyste byli stejně opatrní na konci jako na začátku, jen tehdy vaše námaha nebude marná! A tak tedy snahou Dokonalého Člověka není pachtit se po těžko získatelném zboží, vzdělávání Dokonalého Člověka je v dalším a dalším odnaučování se lidských chyb! Tím napomáhá desetitisícům věcí v jejich přirozeném dění a proto nikdy nepotřebuje do ničeho zasahovat!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 64, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181872, 'V dávných dobách ti, co správně postupovali Cestou Tao, neusilovali o to, aby byl lid osvícený, ale ponechávali ho v jeho přirozené prostotě. Vždyť těžké je spravovat lid, který moc mudruje! Protože mudrováním se při spravování země dospívá k jejímu vydrancování. Jedině zbytečným nemudrováním je možné dospět k jejímu šťastnému osudu! Kdo pochopí tyto dva rozdíly, jedině ten dospěje k pochopení správné míry věcí. A jen při trvalém pochopení správné míry věcí je možné hovořit o pravé energii Te! Takováto pravá energie Te mívá potom velmi hluboké kořeny a velmi dalekosáhlé výsledky! Opakem toho je zaměřování se na věci! A až když pochopíte tento rozdíl, až potom dosáhnete souladu ve všem!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 65, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181873, 'Jak je to možné, že řeky a moře mají moc ovládat stovky hlubin a údolí?! Protože jejich mistrovskou dokonalostí je směřovat “dolů”! Proto: Kdo má možnost vládnout stovkám údolí, jestliže si přeje “být nad lidmi”, má svými slovy směřovat k nim dolů, jestliže si přeje “být před lidmi”, má svou osobu klást až za ně! Jedině proto Dokonalý Člověk, dostající se nad lid nebude pro něho břemenem, dostající se před lid nebude jeho neštěstím! A Podnebesí, nadšeně si ho vyvolící za svého vládce, nebude si ho muset znenávidět, ba ani se proti němu vzbouřit! A tak potom se v Podnebesí nenajde nikdo, kdo by s ním bojoval o moc!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 66, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181874, 'Všichni v Podnebesí tvrdí, že ta moje Cesta Tao je sice Veliká, ale jako by byla pro život nepoužitelná. Vždyť už jen proto, že je Veliká, je jako by nepoužitelná! Jestliže přeci je něco dlouho oblíbené, tak to bude určitě nějaká hloupost! Tři klenoty v sobě máme, které je třeba uchovávat a ochraňovat: první je soucit, druhý je skromnost a třetí je nedovolovat si stavět se v Podnebesí před jiných! Soucit podmiňuje schopnost být statečným, skromnost podmiňuje schopnost být štědrým a nedovolovat si stavět se v Podnebesí do popředí, to podmiňuje smysl pro zodpovědnost! Dnes je však soucit zavrhovaný a tím je zavrhovaná i opravdová statečnost. I skromnost je zavrhovaná a tím je zavrhovaná i štědrost. Ba zavrhovaná je i ústupnost a tím jsou tedy zavrhovaní i ti, co se dokáží postavit tváří v tvář smrti! Kdo bude mít soucit, ten v boji zvítězí a v obraně bude neporazitelný. A aby bylo po všechny dny i v budoucnosti bezpečně, na to je potřebná obrana soucitem!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 67, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181875, 'Mistrovský velitel není bojechtivý, mistrovský zápasník není vznětlivý, mistrovský přemožitel není rýpavý, mistrovský vládce lidí umí se podřídit! A to je právě to, čemu říkám energie neútočení, to je to, co nazývám: silou ovládání lidí, to je to, co je tou nejhodnotnější osou Nebes i lidských dějin!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 68, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181876, 'Kdo používá vojsko, mívá zásadu: Kde se neodvážím být pánem, budu alespoň hostem, kde se neodvážím postoupit o palec, ustoupím o stopu! Ale to je to čemu říkám: postupovat bez postupu, vyhrnout si rukáv bez odhalení ramene, uhýbat bez útočníka, útočit beze zbraní! A není věru většího neštěstí, než brát na lehkou váhu protivníka! Když berete na lehkou váhu protivníka, dožijete se většího zklamání, než při kvílení nad ztrátou svého pokladu! Proto: Když vzájemně zkřížíte zbraně, nechť zvítězí ten, kdo se slituje!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 69, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181877, 'Moje slova jsou velmi lehko pochopitelná a lehko uskutečnitelná, ale naproti tomu není v Podnebesí nikoho, kdo je umí pochopit a kdo je umí uskutečnit. Moje slova však mají svého prapůvodce a jejich uskutečňování má svého pána! A jen proto, že u lidí není pro ně porozumění, není u nich porozumění ani pro mne. Ale jen co mi někdo přeci porozumí, okamžitě mě respektuje! Tak tedy přeci je pravdou, že Dokonalému Člověku i z pod pytloviny září v hrudi drahokam!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 70, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181878, 'Poznáváním nepoznaného se dostávám na vrchol, nepoznáváním poznaného upadnu do nemoci! Avšak jen ten, kdo si v nemoci přizná, že je nemocný, je schopný nemoci se zbavit! Dokonalý Člověk právě proto nebývá nemocný, protože si v nemoci dokáže přiznat, že je nemocný. A tak se z ní nakonec opravdu dostane!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 71, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181879, 'Když náhle lid přestává mít strach, potom je to už doopravdy vyhrocené! A potom nebude té neuctivosti, v které by nesetrvával. nebude té nenávisti, kterou by nezplodil! Ale jen ten, kdo neumí nenávidět, může nebýt nenáviděný! A tak tedy Dokonalý Člověk z vlastního sebepoznání neholduje sebeobdivování a z lásky k sobě neholduje sebeuctívání! Proto: Odmítejte tamto a držte se tohoto!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 72, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181880, 'Spojením odvahy s bezohledností se dospívá k vraždění, spojením odvahy s ukázněností se dospívá k životu. Tyto dva druhy lidského chování mají tedy za následek buď prospěch, nebo škodu! A jestliže se tedy Nebesa rozhněvají, každý ví, proč! Jenže tím se i Dokonalý Člověk může dostat do těžkostí. Nebeská Cesta Tao však i bez boje vždy zvítězí, i bez slov dokonale odpoví, i bez zavolání sama od sebe přijde a všechno vždy dokonale vyřeší! Vždyť síťovina Nebes je tak rozsáhlá, že se z ní nic nevytratí!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 73, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181881, 'Jestliže se lid už přestal bát smrti, jaký bude mít význam smrtí ho zastrašovat?! Zapříčiní to jedině to, že lid, který bude žít trvale v hrůze ze smrti, začne být nevypočitatelný a těžko ho potom udržíte v poslušnosti popravováním. Ach, vždyť kdo by si už tehdy něco takového troufal?! Jestliže někdo začne popravovat namísto kata, o tom tvrdím, že fušuje do řemesla mistrovi-tesařovi. A když někdo fušuje do řemesla mistrovi-tesařovi, není možné, aby si neporanil ruku!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 74, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181882, 'Jestliže lid začne trpět hladem, protože je stále více sdíraný nadměrnými daněmi, tak to už potom bude velice hrozný hladomor! Jestliže už bude lid velmi těžko spravovatelný, protože do jeho věcí vrchnost neustále zasahuje, tak to už bude potom opravdu těžko spravovatelný! A jakmile už lid začne brát i smrt na lehkou váhu, protože vrchnost začíná mít na jeho život stále větší požadavky, tak to už potom opravdu nebude ani smrtí zastrašitelný! Jen ten, kdo odmítá zasahovat do života, jen ten je skutečně schopný vážit si života!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 75, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181883, 'Při narození bývá člověk měkoučký a slaboučký, při smrti bývá už tvrdý a ošlehaný. I rostliny a stromy jsou při zrodu slaboučké a křehoučké, při úhynu shnilé a zdřevěnělé. Proto: Tvrdost a síla jsou průvodními znaky umírání, slabost a měkkost jsou průvodními znaky zrození! A tak tedy síla zbraní není vítězstvím a síla stromu přivábí jen sekery! Silou a velikášstvím se dostáváte dolů, slabostí a měkkostí se dostáváte na vrchol!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 76, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181884, 'Nebeská Cesta Tao jako by byla napínáním obrovského luku.  Čím více se napíná, tím více se ohýbá, čím méně se napíná, tím více se nahoru zdvihá. Kde má přebytky, tam ubírá, kde má nedostatky, tam přidává.  Nebeská Cesta Tao tedy z přebytků ubírá a nedostatkům přidává! Lidská Cesta Tao, ta bývá jinačí: ubírá z nedostatků, aby posloužila přebytkům! Kdo z lidí je však schopný nakládat s přebytky tak, aby to posloužilo Podnebesí? Jen ten, kdo má Cestu Tao! A tak tedy Dokonalý Člověk koná bez toho, aby se na to upínal,  úspěšně dovršuje bez toho, aby v tom setrvával a nikdy si nepřeje vystavovat na odiv svoji výjimečnost!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 77, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181885, 'V Podnebesí nic není měkčí a poddajnější jako voda, a přeci, když voda zaútočí na tvrdé a silné, není ničeho, co by nad ní zvítězilo, co by ji bytostně změnilo! To, že zvítězí slabé nad silným, měkké nad tvrdým, o tom není v Podnebesí pochyb! U mnohých lidí však není schopností konat podle toho! Právě proto Dokonalý Člověk se neustále utvrzuje: “Když se o těch, kteří se podjímají odstraňovat odpadky a špínu, říká, že jsou strážmistry duchů půdy a úrody, tak ti, kteříse podjímají odstraňovat nešťastný osud země, ti musí být v Podnebesí skutečnými Vojevůdci!” Toto jsou opravdu důvěryhodná slova, i když se zdají být protiřečivá!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 78, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181886, 'I po utišení velkého rozhořčení jisté rozčílení zůstává. Tak jak se můžu považovat za dokonale dobrého?! Proto: Dokonalý Člověk věnuje pozornost jen zářezům na svojí levé straně úpisu a nevymáhá dluhy od jiných! Dokud má tvořivou energii Te, plní si všechny svoje zářezy na úpisu. Když ji nemá, hledá, kam mu uniká. A věčná Cesta Tao nepozná strýčkování. Vždy je na straně Dokonalého Člověka!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 79, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181887, 'Malé země s malými počty lidí, mají různé prostředky na zastrašování a tím, že je nepoužívají, mají lid, který z obav před smrtí nebude muset prchat do daleka, i když budou mít vojenské vozy a lodě, nebudou muset do nich nasedat, i když budou mít vojenská brnění a zbraně, nebudou muset jich používat. A právě tím dosáhnou, že lid si bude v pokoji uzlíčkovat čas a vychutnávat sladkosti života! Z jídla si bude dělat krásu, jeho oděvy budou uhlazené, jeho obydlí budou veselá a jeho obyčeji se stanou přátelská sousedská sezení! Tyto země si začnou být jako by na dohled a vzájemně budou slýchávat kritikaření kohoutů a štěkot psů. A jejich obyvatelé budou umírat až po dovršení staroby a bez toho, aby se museli beznadějně Podnebesím potulovat sem a tam!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 80, 0, 0, 6);
INSERT INTO Quote (id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id) VALUES (181888, 'Důvěryhodná slova nebývají líbivá, líbivé řeči nebývají důvěryhodné! Co je vynikající, nepotřebuje být obhajováno, co potřebuje být obhajováno, nebývá vynikající! Moudrost nejsou rozsáhlé vědomosti, rozsáhlé vědomosti nemusí být modrostí! Dokonalý Člověk nehromadí! Čím více koná pro druhé, tím více má, čím více lidem dává, tím více se mu dostává! Věčná Cesta Tao je tvorbou prospěchu bez zničení. Cesta Dokonalého Člověka je konáním bez boje!', 249, 5, 1, '2024-10-13_14:44:45', '2024-10-13_14:44:45', 81, 0, 0, 6);
