(id, quote, author_id, language_id, topic_id, created_at, updated_at, order_no, chapter_id, translation_of, book_id)
VALUES
(182699,"تائويي كه بتوان آن را بر زبان آورد تائوي جاودان نخواهد بود. نامي كه بتوان آن را ذكر كرد نامي ماندگار نخواهد بود. آن چه نمي توان برايش نامي نهاد حقيقت جاويد است. رها از آرزوها مي توان اسرار را درك كرد. در بند آرزو تنها مي توان جلوه ها را ديد. اسرار نهان و جلوه ها عيان هر دو از يك منبع اند. اين منبع را تاريكي ناميده اند. تاريكي در تاريكي؛ دروازه ي ورود به دنياي شناخت.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",1,0,0,21),
(182700,"وقتي مردم برخي چيزها را زيبا مي دانند چيزهاي ديگر زشت مي شوند. وقتي مردم بعضي چيزها را خوب مي دانند چيزهاي ديگر بد مي شوند. بودن و نبودن يك ديگر را مي آفرينند. سخت و ساده يك ديگر را پشتيبانند بلند و كوتاه يك ديگر را تعريف مي كنند. پستي و بلندي به يك ديگر وابسته اند. قبل و بعد به دنبال هم مي آيند.  بنابراين فرزانه بدون انجام دادن كاري عمل مي كند و بدون به زبان آوردن كلمه اي آموزش مي دهد. اتفاقات رخ مي دهند و او به آن ها اجازه ي روي دادن مي دهد؛ موارد مختلف ناپديد مي شوند و او به آن ها اجازه ي از بين رفتن مي دهد. او دارد، بدون آن كه مالك چيزي باشد، عمل مي كند، بدون آن كه انتظاري داشته باشد. وقتي كارش به اتمام مي رسد، آن را فراموش مي كند. به همين دليل براي هميشه جاويد باقي مي ماند.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",2,0,0,21),
(182701,"اگر مردانِ بزرگ را بيش از اندازه ارزشمند شماريد، مردم عادي كوچك شمرده مي شوند. اگر دارايي خود را بيش از اندازه عزيز داريد، دزدي ميان مردم رواج پيدا مي كند. فرزانه با خالي كردن ذهن مردم آن ها رهبري مي كند و با تضعيف جاه طلبي ها و تحكيم اراده شان درون آها را غني مي سازد. او به آن ها كمك مي كند تا از دانسته ها و آرزوهاي خود خالي شوند و در كساني كه تصور مي كنند مي دانند سردرگمي به وجود مي آورد.  بي عملي را بيازماييد، و هر چيز در جاي خود قرار خواهد گرفت.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",3,0,0,21),
(182702,"تائو به چاه مي ماند؛ مي توان از آن استفاده كرد ولي هرگز تمام نمي شود. همانند خلأ بي پايان است؛ پر از امكانات بي شمار. نهان است و هميشه حاضر. نمي دانم چه كسي آن را به وجود آورده؛ از خدا هم قديمي تر است.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",4,0,0,21),
(182703,"تائو از كسي جانب داري نمي كند؛ خوب و بد هر دو از آن به وجود آمده اند. فرزانه از كسي جانب داري نمي كند: او به پرهيزكاران و گناهكاران هر دو خوش آمد مي گويد. تائو به ني مي ماند؛ خالي است ولي قابليت هايش بي عدداند. هرچه بيشتر آن را بكار بريد، بيشتر قابليت هايش را مي شناسيد؛ هرچه بيشتر درباره اش صحبت كنيد، كمتر مي شناسيدش.  در مركز باقي بمانيد.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",5,0,0,21),
(182704,"تائو را مادرِ بزرگ مي نامند؛ خالي ولي تمام ناشدني. كلماتي بي شمار از آن متولد مي شود. هميشه درون شما حاضر است؛ هرگونه كه بخواهيد مي توانيد به كارش گيريد.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",6,0,0,21),
(182705,"تائو بي نهايت و جاويد است. چرا بي نهايت است؟ هرگز به دنيا نيامده است؛ بنابراين هرگز نمي ميرد. چرا جاويد است؟ آرزويي براي خود ندارد؛ بنابراين در دسترس همه ي موجودات است.  فرزانه هميشه در آخر مي ايستد؛ به همين دليل از همه جلو تر است. به هيچ چيز وابستگي ندارد؛ به همين دليل با همه چيز يگانه است. چون خودش را رها كرده، به تمامي راضي و خوشنود است.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",7,0,0,21),
(182706,"خوب همانند آب است، بدون تلاش همه چيز را سيراب مي كند و جمع شدن در پستي را هرگز حقارت نمي شمارد؛ بنابراين همانند تائو است.  نزديك به زمين زندگي كنيد. هميشه ساده بيانديشيد. در مشاجرات، عادل و بخشنده باشيد. در حكومت، سعي در فرمانروايي و تسلط نداشته باشيد. در كار، آن چيزي را انجام دهيد كه از آن لذت مي بريد. در زندگي خانوادگي، هميشه در دسترس و حاضر باشيد.  وقتي از اين كه خودتان هستيد خوشنوديد و از رقابت و مقايسه دست كشيده ايد، همگان به شما احترام مي گذارند.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",8,0,0,21),
(182707,"كاسه خود را بيش از اندازه پر كنيد؛ لبريز مي شود. چاقوي خود را بيش از حد تيز كنيد، كند مي شود. به دنبال پول و راحتي باشيد؛ دلتان هرگز آرام نمي گيرد. به دنبال تاييد ديگران باشيد؛ برده آن ها خواهيد بود.  كار خود ر انجام دهيد، سپس رها كنيد. اين تنها راه آرامش يافتن است.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",9,0,0,21),
(182708,"آيا مي توانيد ذهنتان را از پرسه زدن باز داريد و آن را به يگانگي ابتدايي - با هستي - باز گردانيد؟ آيا مي توانيد بدنتان را همانند نوزادان دوباره نرم و انعطاف پذير كنيد؟ آيا مي توانيد ديد دروني تان را پاك كنيد تا چيزي جز نور نبينيد؟  آيا مي توانيد ديگران را دوست بداريد و آن ها را بدون تحميل خواسته هاي خود راهنمايي كنيد؟ آيا مي توانيد در برخورد با مسائل مهم و حياتي زندگي هيچ دخالتي نكنيد و اجازه دهيد آن چه بايد، رخ بدهد؟ آيا مي توانيد از ذهن خود دست بكشيد و بدون دخالت ذهن درك كنيد؟  داشتن بدون احساس مالكيت، عمل كردن بدون انتظار داشتن و راهنمايي كردن بدون سعي در حكم راندن فضايل عالي محسوب مي شوند.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",10,0,0,21),
(182709,"براي ساختن چرخ محور ها را به هم وصل مي كنيم ولي اين فضاي تهي ميان چرخ است كه باعث چرخش آن مي شود. از گل كوزه اي مي سازيم، اين خالي درون كوزه است كه آب را در خود جاي مي دهد. از چوب خانه اي بنا مي كنيم، اين فضاي خالي درون خانه است كه براي زندگي سودمند است.  مشغول هستي ايم در حالي كه اين نيستي است كه به كار ما مي آيد.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",11,0,0,21),
(182710,"رنگ ها چشم را كور مي كنند. صداها گوش را كر مي كنند. مزه ها چشايي را كند مي كنند. افكار، ذهن را ضعيف مي كنند. آرزوها موجب پژمردن دل مي شوند.  فرزانه جهان را مشاهده مي كند و به ديد دروني خويش اعتماد مي كند. او اجازه مي دهد اتفاقات آن طور كه بايد رخ دهند. قلب او همچون آسمان باز و گسترده است.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",12,0,0,21),
(182711,"موفقيت به اندازه ي شكست خطرناك است. آرزو به اندازه ي ترس تو خالي است. اين كه موفقيت به اندازه ي شكست خطرناك است يعني چه؟ چه از پله هاي نردبان بالا رويد يا از آن پايين آييد، موقعيت شما متزلزل است. هنگامي كه با دو پاي خود روي زمين ايستاده ايد، مي توانيد هميشه تعادلتان را حفظ كنيد. اين كه آرزو به اندازه ترس توخالي است يعني چه؟ ترس و آرزو هر دو سايه اند؛ آن ها از افكار ما به وجود مي آيند. وقتي خود را به صورتِ خود نمي بينيم، چيزي براي ترسيدن و جود ندارد. دنيا را به صورت خودتان ببينيد. به چيزها همانطور كه هستند اعتماد كنيد. جهان را چون خودتان دوست بداريد سپس مي توانيد آن طور كه بايد مراقب همه چيز باشيد.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",13,0,0,21),
(182712,"نگاه كنيد و نمي توانيد آن را بيابيد. گوش كنيد و نمي توانيد آن را بشنويد. سعي كنيد و نمي توانيد به آن دست يابيد. بالا ؛ آن واضح نيست. پايين ؛ آن تاريك نيست. يكپارچه، بي نام، به مأواي هيچ باز مي گردد؛ جايي كه همه شكل ها را در بر مي گيرد، شكلِ بي شكل، ظريف، ماوراي درك. به آن نزديك شويد و آغازي ندارد. آن را دنبال كنيد و پاياني ندارد. نمي توانيد آن را بشناسيد ولي با سادگي در زندگي خود مي توانيد آن شويد. بدانيد از كجا آمده ايد؛ اين عصاره ي خرد است.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",14,0,0,21),
(182713,"فرزانگان كهن كامل و دقيق بودند. خرد آن ها دست نيافتني بود. كلمات از بيان آن عاجزند؛ آن چه مي توان بيان كرد تنها ظاهر آن هاست. آن ها مراقب بودند؛ درست مانند كسي كه از رودخانه اي يخ زده مي گذرد. هوشيار؛ همانند جنگجويي كه در قلمرو دشمن است. با ادب همچون يك ميهمان. جاري همچون يخ در حال آب شدن. شكل پذير چون تكه اي چوب. پذيرا همچون دره ها. زلال همچون آب. آيا براي انتظار كشيدن شكيباييد تا زماني كه گلتان ته نشين شود و تنها آب زلال باقي بماند؟ آيا مي توانيد بي حركت باقي بمانيد تا زماني كه عمل مناسب خود به خود رخ دهد؟  فرزانه در جستجوي رضايت خاطر نيست. نه در جستجوست، نه انتظار مي كشد. او حاضر است؛ آماده براي خوش آمد گويي به هرچيز.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",15,0,0,21),
(182714,"ذهن خود را از همه ي افكار خالي كنيد. اجازه دهيد دلتان آرامش يابد. نا آرامي موجودات را مشاهده كنيد و در بازگشت آن ها دقيق شويد. هر موجودي در اين جهان به مأواي مشترك باز مي گردد. بازگشت به اين مأوا آرامش يافتان است. اگر اين مأوا را نمي شناسيد، در اندوه و سردر گمي به اين طرف و آن طرف مي خوريد. اگر بدانيد از كجا امده ايد، ظرفيت تحملتان به طور طبيعي بالا مي رود. بي نياز، سرگرم، مهربان همچون مادرِ بزرگ و باشكوه همچون پادشاهان مي شويد. غرق در حيرتِ تائو از آن چه زندگي برايتان به ارمغان مي آورد خوشنوديد و چون مرگ فرا رسد، آماده.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",16,0,0,21),
(182715,"هنگامي كه فرزانه حكومت مي كند، مردم به ندرت متوجه وجودش مي شوند. پس از فرزانه بهترين حاكم، رهبري است كه دوستش دارند. پس از او فرمانروايي كه از او مي ترسند و بدترين، كسي است كه او را خوار مي شمارند. اگر به مردم اعتماد نكنيد، آن ها را غير قابل اعتماد مي سازيد.  فرزانه سخن نمي گويد، عمل مي كند. وقتي كارش به انجام مي رسد، مردم مي گويند:  چه جالب، ما خود آن را انجام داده ايم! ",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",17,0,0,21),
(182716,"وقتي تائو فراموش مي شود، خوبي و زهد پديدار مي شود. وقتي هوش طبيعي بدن از بين مي رود، دانش و زيركي پديد مي آيد. وقتي كشور دچار آشوب مي شود، ميهن پرستي رونق مي گيرد.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",18,0,0,21),
(182717,"تقدس و خرد را فراموش كنيد و مردم هزار بار شادتر خواهند بود. عدل و اخلاقيات را فراموش كنيد و مردم تنها آن چه درست است را انجام مي دهند. سود و صنعت را فراموش كنيد و ديگر دزدي وجود نخواهد داشت. اگر اين سه كافي نيستند، در مركز دايره باقي بمانيد و اجازه دهيد همه چير به دور خود بگردد.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",19,0,0,21),
(182718,"فكر كردن را متوقف كن و مشكلاتت پايان مي يابد. چه تفاوتي ميان بله و نه وجود دارد؟ چه تفاوتي ميان شكست و پيروزي وجود دارد؟ آيا تو هم بايد آن چه ديگران مهم مي دانند، مهم بداني و آن چه آن ها دوست ندارند، دوست نداشته باشي؟ چه مسخره! ديگران بسيار هيجان زده اند، گويي در حال رژه رفتن اند. فقط من اهميتي نمي دهم، فقط من نظري ندارم، همانند نوزادي كه هنوز نمي تواند لحظه اي لبخند بزند. ديگران آن چه نياز دارند را مالك اند، فقط من هستم كه چيزي ندارم، فقط من هستم كه آواره ام؛ همانند كسي كه خانه اي ندارد. من به يك احمق مي مانم كه ذهنش به تمامي خالي است. ديگران مي درخشند، فقط من نوري ندارم. ديگران باهوشند، فقط من گنگ هستم. ديگران هدفي دارند، فقط من نمي دانم. من همانند موجي در اقيانوس سرگردانم و در بي هدف به باد مي مانم. من با مردم عادي تفاوت دارم؛ من از سينه مادرِ بزرگ شير مي نوشم.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",20,0,0,21),
(182719,"فرزانه هميشه ذهنش را با تائو هماهنگ و يگانه مي سازد؛ اين چيزي است كه به او نور مي بخشد. تائو غير قابل درك است. او چگونه مي تواند ذهنش را با آن هماهنگ و يگانه سازد؟ با نچسبيدن به هيچ ايده اي. تائو تاريك و بي پايان است. چگونه مي توان از آن نور گرفت؟ با اجازه دادن به آن تا وارد شود. از آنجايي كه تائو قبل از زمان و فضا وجود داشت، ماوراي هستي و نيستي است. چگونه اين نكته را درك مي كنم؟ به درون خود مي نگرم و آن را مي بينم.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",21,0,0,21),
(182720,"اگر مي خواهيد يكپارچه باشيد، قطعه قطعه شويد. اگر مي خواهيد صاف باشيد، خميده شويد. اگر مي خواههيد پر باشيد، خالي شويد. اگر مي خواهيد رستاخيز يابيد، بميريد. اگر مي خواهيد همه چيز از آن شما باشد، هيچ نخواهيد. فرزانه با استقرار در تائو، به الگويي براي ديگران تبديل مي شود. چون خود را نمي نمايد، ديگران نورش را مي بينند. چون چيزي براي ثابت كردن ندارد، ديگران به گفتارش اعتماد مي كنند. چون نمي داند كيست، ديگران خود را در او مي شناسند. چون هدفي در ذهن ندارد، در هر چه مي كند موفق است. هنگامي كه فرزانگان كهن مي گفتند:  اگر مي خواهيد همه چيز از آن شما باشد، هيچ نخواهيد ، سخناني بي معنا بر زبان نمي راندند. تنها با زندگي در تائو مي توانيد حقيقتِ خود باشيد.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",22,0,0,21),
(182721,"خود را به طور كامل ابراز كنيد، سپس آرام و خاموش باقي بمانيد؛ همانند نيرو هاي طبيعي. هنگامي كه باد مي ورزد، فقط باد مي ورزد. هنگامي كه باران مي بارد، فقط باران مي بارد. هنگامي كه ابر ها كنار مي روند، خورشيد مي تابد. اگر خود را به روي تائو باز كنيد، با تائو يكي مي شويد و آن در وجودتان جاي مي گيرد. اگر خود را به روي بصيرت باز كنيد، با بصيرت يكي مي شويد و آن در وجودتان جاري مي شود. اگر خود را به روي شكست باز كنيد، با شكست يكي مي شويد و آن را مي پذيريد. خود را به روي تائو باز كنيد سپس به عكس العمل هاي طبيعي خود اعتماد كنيد و هر چيز در جاي خود قرار مي گيرد.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",23,0,0,21),
(182722,"كسي كه روي انگشتانش مي ايستد نمي تواند تعادلش را حفظ كند. كسي كه عجله مي كند نمي تواند خيلي پيش رود. كسي كه سعي در درخشيدن مي كند نور وجودش را خاموش مي كند. كسي كه خود را بزرگ مي داند نمي تواند بداند به واقع كيست. كسي كه بر ديگران فرمان مي راند نمي تواند بر وجود خويش فاتح شود. كسي كه به كارش مي چسبد چيزي كه ديري بپايد، خلق نمي كند. اگر مي خواهيد با تائو در همانگي قرار گيريد، كار خود را بكنيد و سپس همه چيز را رها كنيد.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",24,0,0,21),
(182723,"قبل از تولد جهان چيزي بي شكل و كامل وجود داشت. آن آرام است؛ خالي، تنها، تغيير ناپذير، بي نهايت، حاضرِ ابدي؛ مادرِ جهان است. چون نام بهتري برايش نمي يابم، آن را تائو مي نامم. در هر چيز جاري است؛ بيرون و درون و به منبع اصلي باز مي گردد.  تائو پهناور است. جهان پهناور است. زمين پهناور است. انسان پهناور است. اين چهار قدرت بزرگ هستي است. انسان از زمين پيروي مي كند. زمين از جهان پيروي مي كند. جهان از تائو پيروي مي كند. تائو از خود پيروي مي كند.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",25,0,0,21),
(182724,"سنگين ريشه سبك است. ثبات منبع حركت است.  فرزانه هر روز در سفر است بدون اينكه خانه اش را ترك كند. هر چه قدر مناظر مقابلش با شكوه باشند به آرامش او خدشه اي وارد نمي سازد. اگر در وجودتان تزلزلي ايجاد شود، ارتباط با ريشه ي خود را از دست مي دهيد. اگر اجازه دهيد ناآرامي به وجودتان رخنه كند، ارتباط با اصل خود را از دست مي دهيد.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",26,0,0,21),
(182725,"يك مسافر خوب هيچ نقشه ي از قبل طراحي شده اي ندارد و براي رسيدن به مقصد خاصي سفر نمي كند. يك هنرمند خوب به حس شهود خود اجازه مي دهد تا او را به آن جا كه مي خواهد راهنمايي كند. يك دانشمند خوب رها از نظريه هاي مختلف ذهن خود را به روي آن چه وجود دارد باز مي گذارد. فرزانه در دسترس همه ي مردم است و به هيچ كس نه نمي گويد. او آماده است تا از هر موقعيتي استفاده كند و هيچ چيز را به هدر نمي دهد. اين يعني تجسمِ نور بودن. انسان خوب كسي نيست جز آموزگاري براي انسان بد. انسان بد كسي نيست جز چالشي براي انسان خوب. اگر اين را درك نكنيد، به حتم راه را گم مي كنيد؛ اگر چه بسيار باهوش باشيد. اين رازِ بزرگ است.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",27,0,0,21),
(182726,"نيروي مذكر را بشناسيد ولي ارتباط خود را با انرژي مونث از دست ندهيد. جهان را با آغوش باز بپذيريد. اگر جهان را اين گونه بپذيريد، تائو هرگز شما را ترك نمي كند و شما چون كودكي كوچك خواهيد بود. سفيد را بشناسيد ولي سياه را هم ناديده نگيريد. الگويي براي جهانيان باشيد.  اگر الگويي براي همگان باشيد، تائو در وجودتان تقويت مي شود. و چيزي وجود نخواهد داشت كه ازعهده اش بر نياييد. حالات شخصي خود را بشناسيد و در عين حال از حالات اجتماعي غافل نشويد. جهان را آن گونه كه هست بپذيريد. اگر جهان را بپذيريد، تائو در وجودتان خواهد درخشيد و به خودِ اوليه يِ خويش باز مي گرديد.  هستي از نيستي به وجود آمده است. همانند ظرفي چوبي كه از قطعه اي چوب ساخته شده است. فرزانه ظرف چوبي را مي شناسد ولي در عين حال از قطعه اوليه چوب نيز غافل نيست؛ به اين ترتيب او مي تواند همه چيز را به كار گيرد.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",28,0,0,21),
(182727,"آيا مي خواهيد جهان را بهبود ببخشيد؟ فكر نمي كنم چنين كاري عملي باشد. جهان مقدس است. نمي توان آن را بهبود بخشيد. اگر سعي در اين كار داشته باشيد، آن را خراب خواهيد كرد. اگر با آن همانند يك شئ برخورد كنيد، آن را از دست خواهيد داد.  زماني جلوتر از ديگران هستيد، زماني عقب تر. زماني در حركتيد، زماني در حال استراحت. زماني قوي و نيرومنديد، زماني خسته و بي حال. زماني از امنيت بر خورداريد و زماني در خطريد.  فرزانه هر چيز را آن طور كه هست مي بيند، بدون تلاش براي تسلط بر آن. او اجازه مي دهد هر چيز سير طبيعي اش را طي كند و در مركز دايره باقي مي ماند.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",29,0,0,21),
(182728,"هركه در حكومت به مردم، به تائو اعتماد كند سعي در تحميل چيزي نخواهد داشت. چنين كسي تلاش نمي كند دشمنان را با نيروي نظامي شكست دهد.  هر عملي، عكس العملي دارد. خشونت حتي با نيت خوب هميشه به كننده اش باز مي گردد. قرزانه كار خود را به پايان مي رساند و متوقف مي شود. او مي داند كه جهان هميشه از كنترل خارج است. سعي در تسلط بر اوضاع مخالف جريان تائو شنا كردن است.  فرزانه به خود ايمان دارد پس تلاش نمي كند تا خوشنودي ديگران را بدست آورد. چون از خود خوشنود است نيازي به تائيد ديگران ندارد. چون خود را پذيرفته است، تمام دنيا او را مي پذيرند.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",30,0,0,21),
(182729,"ابزار نظامي، ابزار خشونت است. فرزانه از خشونت پرهيز مي كند. ابزار نظامي ابزار ترس است؛ فرزانه هرگز آن را بكار نمي برد؛ مگر در زمان اضطراري و در صورت اجبار با كمال بي ميلي آن را به كار مي گيرد. صلح و دوستي والاترين ارزش است. اگر در صلح و دوستي خللي پيدا شود او چگونه مي تواند خوشنود باقي بماند؟ دشمنان او ديوها نيستند، بلكه انسان هايي هستند همانند او. او در آرزوي نابود شدن آن ها نيست و هنگام پيروزي جشن نمي گيرد. او چگونه مي تواند هنگام پيروزي و كشتن انسان ها به جشن و شادي بپردازد؟ با شجاعت به ميدان نبرد وارد شويد؛ شجاعتي همراه با غم و شفقت؛ گويي در مراسم تشييع جنازه شركت مي كند.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",31,0,0,21),
(182730,"تائو را نتوان فهميد؛ از الكترون هم كوچكتر است، ولي كهكشان هاي بي شمار در آن جاي دارند.  اگر مردان و زنان قدرتمند مي توانستند در تائو متمركز شوند، همه چيز در هماهنگي كامل به سر مي برد. در اين صورت دنيا به بهشت تبديل مي شد، همگي در صلح و آرامش به سر مي بردند و قوانين در قلبشان حك مي شد.  وقتي نام و شكل داريد، بدانيد كه موقتي اند. وقتي رسم و سنتي داريد بدانيد عمل كردن به آن ها را كجا پايان دهيد. با دانستن اين كه كجا بايد متوقف شويد مي توانيد از هر خطري جلوگيري كنيد. همه چيز در تائو پايان مي پذيرد، همان طور كه رودخانه ها به دريا منتهي مي شوند.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",32,0,0,21),
(182731,"شناخت ديگران هوش و آگاهي است، شناخت خوب خرد ناب است. تسلط بر ديگران قدرت است، تسلط بر خود قدرت حقيقي است. اگر اعتقاد داشته باشيد كه به اندازه كافي داريد، ثروتمند واقعي هستيد.  اگر در مركز باقي بمانيد و با تمام وجو مرگ را به ياد داشته باشيد، بقاي جاودان مي يابيد.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",33,0,0,21),
(182732,"تائو همه جا جاري است. همه چيز از تائو به وجود مي آيد، با اين ها تائو آن ها را خلق نمي كند. تائو خود را به آن چه انجام مي دهد مي ريزد، ولي هيچ ادعايي ندارد؛ دنياهاي بي شمار را زندگي مي بخشد، ولي وابستگي به آن ها ندارد. از آن جايي كه به همه چيز آميخته و در دل هر چيز نهان است، مي توان آن را فروتن ناميد. از آن جايي كه همه چيز در تائو نابود و ناپديد مي شود و تنها تائو باقي مي ماند، مي توان آن را والا ناميد. تائو از بزرگي خود بي خبر است؛ پس در حقيقت بزرگ است.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",34,0,0,21),
(182733,"او كه در تائو تمركز يافته مي تواند هو كجا كه دوست دارد برود، بي آن كه خطري تهديدش كند. او هماهنگي كائنات را درك مي كند، حتي هنگام درد و اندوه زيرا آرامش راستين را در دلش يافته است.  لذت موسيقي يا بوي خوش غذا ممكن است شما را از آن چه مي كنيد باز دارد در حالي كه كلام تائو يكنواخت و بي مزه به نظر مي رسد.  هنگامي كه جستجو مي كنيد، نمي يابيدش. هنگامي كه گوش فرا مي دهيد، نمي شنيدش. وقي به كارش مي گيريد، تمام ناشدني است.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",35,0,0,21),
(182734,"اگر مي خواهيد چيزي كوچكتر شود، اجازه دهيد خوب گسترده شود. اگر مي خواهيد از چيزي رها شويد، ابتدا اجازه دهيد شكوفا شود. اگر مي خواهيد چيزي را بدست آوريد، ابتدا آن را ببخشيد. اين درك ظريفي از قانون هستي است. نرم بر سخت غلبه مي كند. آرام بر سريع پيروز مي شود.  اجازه دهيد آن چه مي كنيد پنهان باقي بماند و تنها نتيجه را به ديگران نمايش دهيد.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",36,0,0,21),
(182735,"تائو هرگز عملي انجام نمي دهد، ولي همه چيز از طريق آن انجام مي شود. اگر مردان و زنان قدرتمند مي توانستند خود را در آن متمركز كنند، دنيا خود به خود متحول مي شد و به آهنگِ طبيعي خود باز مي گشت، مردم به زندگي ساده و روزمره خود راضي مي شدند و از هر آرزويي آزاد و رها بودند. وقتي آرزويي نباشد، همه چيز در صلح و آرامش است.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",37,0,0,21),
(182736,"فرزانه تلاش نمي كند قدرتمند شود؛ بنابراين قدرتمند حقيقي است. انسان عادي در تلاش براي رسيدن به قدرت است؛ بنابراين هرگز قدرت كافي بدست نمي آورد. فرزانه هيچ نمي كند، با اين وجود چيزي ناتمام باقي نمي گذارد. انسان عادي هميشه مشغول كارهاي مختلف است، با اين وجود كارهاي ناتمام زيادي دارد. انسان مهربان عمل مي كند، و چيزي ناتمام باقي مي ماند. انسان عادل عمل مي كند و بسياري چيزهاي ناتمام باقي مي مانند. اخلاق گرا عمل مي كند و هنگامي كه كاري از پيش نمي برد آستين بالا مي زند و از زور استفاده مي كند. وقتي تائو گم مي شود، خوبي پديدار مي شود. وقتي خوبي گم مي شود، اخلاقيات پديد مي آيند. وقتي اخلاقيات گم مي شوند، آداب و رسوم جايگزين مي شود. آداب و رسوب مختلف پوسته ايمان است و شروع آشوب و بي نظمي.  فرزانه به عمق توجه مي كند نه به سطح، به ميوه نه به گل، او از خود كاري نمي كند، در واقعيت تمركز يافته و خيالات باطلش از ميان رفته است.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",38,0,0,21),
(182737,"در هماهنگي با تائو آسمان صاف و گسترده است، زمين محكم و غني است، همه موجودات در شكوفايي اند و خوشنود از آن چه هستند، به توليد مثل مي پردازند؛ در حالي كه لحظه به لحظه تازه مي شوند. هنگامي كه انسان در تائو دخالت مي كند، آسمان آلوده مي شود، زمين فقير مي شود، تعادل از ميان مي رود و نسل موجودات منقرض مي شود.  فرزانه با مهر و شفقت به اجزا مي نگرد زيرا از كل آگاه است. هميشه در حال تواضع است، از خودستايي پرهيز مي كند و اجازه مي دهد جريان تائو او را شكل دهد.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",39,0,0,21),
(182738,"حركت تائو به سوي بازگشت است. طريق تائو تسليم است.  همه چيز از هستي متولد مي شود و هستي از نيستي.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",40,0,0,21),
(182739,"وقتي مردي برتر درباره تائو مي شنود، بي درنگ جلوه گاه تائو مي شود. وقتي مردي متوسط درباره تائو مي شنود، نيمي از آن را باور مي كند و درباره نيم ديگر ترديد مي كند. وقتي ديوانه اي درباره تائو مي شنود، با صداي بلند مي خندد. اگر اين خنده نبود، تائويي وجود نداشت.  راهي كه به نور مي انجامد به نظر تاريك مي آيد. راهي كه به جلو مي رود به نظر مي رسد به عقب باز مي گردد. راه مستقيم طولاني به نظر مي آيد. قدرت حقيقي ضعف به نظر مي آيد. خلوص ناب كدر به نظر مي آيد. ثبات واقعي تغيير به نظر مي آيد. وضوح راستين گنگي به نظر مي آيد. والاترين هنر ساده به نظر مي آيد. عشق راستين بي تفاوتي به نظر مي آيد. خرد ناب كودكي به نظر مي آيد.  تائو را نمي توان جايي يافت، با اين وجود همه چيز در آن كامل مي شود و از آن قوت مي يابد.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",41,0,0,21),
(182740,"يك از تائو متولد مي شود. دو از يك به وجود مي آيد. سه از دو زاده مي شود و همه چيز از سه به وجود مي آيند.  آن چه وجود دارد پشت به نيروي مونث دارد و رو به نيروي مذكر. وقتي نيروي مونث و مذكر تركيب مي شوند، همه چيز در هماهنگي آرامش مي يابد.  انسان هاي عادي تنهايي را دوست ندارند در حالي كه فرزانه تنهايي را به كار مي گيرد. او در تنهايي درك مي كند كه با هستي يگانه است.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",42,0,0,21),
(182741,"ظريف ترين چيز در جهان بر سخت ترين غلبه مي كند. اين ارزش بي عملي را مي نمايد.  آموزش بدون كلام و انجام دادن بدون عمل، شيوه ي فرزانه است.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",43,0,0,21),
(182742,"شهرت يا صداقت؛ كدام يك مهم تر است؟ ثروت يا شادي؛ كدام يك ارزشمند تر است؟ موفقيت يا شكست؛ كدام يك مخرب تر است؟ اگر براي خوشنودي به ديگران وابسته باشيد، هرگز خوشنودِ حقيقي نخواهيد بود. اگر شادي شما به پول و ثروت وابسته باشد، هرگز با خود شاد نخواهيد بود. از آن چه داريد راضي باشيد و زندگي را همان گونه كه هست جشن بگيريد. وقتي درك كنيد هيچ كاستي و نقصي وجود ندارد، كل دنيا متعلق به شما خواهد بود.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",44,0,0,21),
(182743,"كمال واقعي به نظر نقص مي آيد، ولي در عين حال در خود كامل است. پُري واقعي خلأ به نظر مي آيد. ولي در عين حال پر است. صافي واقعي كج به نظر مي آيد. خرد ناب حماقت به نظر مي آيد. هنر راستين بي هنري به نظر مي آيد.  فرزانه اجازه مي دهد آن چه بايد رخ دهد. او به رخدادها آن طور كه مي آيند شكل مي دهد. او از راه قدم بيرون مي نهد و اجازه مي دهد تائو خود سخن بگويد.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",45,0,0,21),
(182744,"وقتي كشوري در هماهنگي با تائو است، كارخانه هاي آن ماشين هاي باروري و كشاورزي مي سازند. وقتي كشوري راه خلاف تائو مي پويد، ابزار و ادوات جنگي در حومه ي شهرها انبار مي شوند. توهمي بالاتر از ترس وجود ندارد و اشتباهي بزرگتر از آماده شدن براي دفاع ازخود بد شانسي اي بد تر از دشمن داشتن نيست. هر كه بتواند در ميان ترس هاي خود به مشاهده آن چه رخ مي دهد بپردازد، هميشه در امان خواهد بود.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",46,0,0,21),
(182745,"بدون باز كردن درب خانه تان مي توانيد دلتان را به روي جهان باز كنيد. بدون نگاه كردن از پنجره خانه تان مي توانيد جوهره تائو را بشناسيد. هرچه بيشتر بدانيد، كمتر درك مي كنيد. فرزانه بدون ترك كردن جايي به مقصد مي رسد، بدون نگاه كردن نور را مي بيند. و بدون انجام دادن كاري آن چه لازم است بدست مي آورد.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",47,0,0,21),
(182746,"در جستجوي دانش هر روز چيزي به شما اضافه مي شود. در تجربه ي تائو، هر روز چيزي از شما كم مي شود و كمتر و كمتر احتياج پيدا مي كنيد تا كارها را با زور به انجام برسانيد. و در نهايت به بي عملي مي رسيد، هنگامي كه هيچ نمي كنيد، چيزي ناتمام باقي نمي ماند. با اجازه دادن به رخدادها، همان گونه كه رخ مي دهند و دخالت نكردن در جريان روي دادنشان، فرزانگي پديدار مي شود.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",48,0,0,21),
(182747,"فرزانه ذهني از خود ندارد، او با ذهن مردم كار مي كند. او با كساني كه خوبند، خوب است و همچنين با كساني كه خوب نيستند نيز خوب است. اين خوبي واقعي است. او به كساني كه قابل اعتمادند، اعتماد مي كند و به كساني كه قابل اعتماد نيستند نيز اعتماد مي كند. اين اعتماد راستين است.  ذهن فرزانه چون فضا پهناور و گسترده است. ديگران او را درك نمي كنند. آن ها به او مي نگرند و انتظار مي كشند. او با ديگران چون فرزندانش برخورد مي كند.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",49,0,0,21),
(182748,"فرزانه تسليم آن چيزي است كه لحظه ي حال براي او به ارمغان مي آورد. او مي داند كه بالاخره خواهد مرد و به هيچ چيز وابستگي ندارد. توهمي در ذهنش وجود ندارد و مقاومتي در بدنش نيست. او درباره ي عملش فكر نمي كند؛ اعمال او از مركز وجودش جاري مي شوند. به گذشته اش نچسبيده، پس هر لحظه براي مرگ آماده است؛ همانطور كه ديگران پس از يك روز كاري سخت براي خواب آماده اند.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",50,0,0,21),
(182749,"هر موجودي در هستي جلوه اي از تائو است. موجودات در ناآگاهي ولي آزاد و كامل كالبدي مادي مي گيرند و به وجود مي آيند. به همين دليل هر موجودي ناخودآگاه تائو را بزرگ مي شمارد. موجودات از تائو تولد مي يابند. تائو آن ها را تغذيه و نگهداري مي كند، مراقب آن هاست و از آن ها حمايت مي كند و دوباره آن ها را به خود باز مي گرداند. آفريدن بدون احساس مالكيت، عمل كردن بدون انتظار و راهنمايي بدون دخالت از خصوصيات تائوست. به عمين دليل عشقِ تائو در طبيعتِ همه چيز است.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",51,0,0,21),
(182750,"در ابتدا فقط تائو بود. تمام چيزهاي ديگر از آن به وجود آمدند و دوباره به آن باز مي گردند. براي يافتن ريشه ها در جلوه ها جستجو كنيد. وقتي كودك را بشناسيد و مادرش را بيابيد از غم و اندوه رها مي شويد.  اگر ذهن خود را با قضاوت هاي مختلف بسته نگاه داريد و آن را از آرزوها پر كنيد دلي نا آرام خواهيد داشت. اگر ذهن خود را از قضاوت هاي مختلف آزاد نگاه داريد و از حواس خود پيروي نكنيد دلتان آرامش خواهد يافت. ديدن در تاريكي بصيرت است. دانستن اين كه چگونه تسليم باشيد، قدرت است. نور وجود خود را به كار گيريد و به منبع نور باز گرديد؛ اين تجربه ي جاودانگي است.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",52,0,0,21),
(182751,"راهِ بزرگ ساده و گسترده است، با اين وجود مردم راه هاي باريك را ترجيح مي دهند. آگاه باشيد چه زماني تعادلتان بر هم مي خورد. در تائو متمركز باقي بمانيد.  وقتي ثروتمندان در رفاه اند، در حالي كه كشاورزان زمين هاي خود را از دست مي دهند؛ وقتي دولتمردان بودجه هاي گزاف صرف ساز و كارهاي نظامي مي كنند؛ وقتي طبقه ي بالا مصرف گرا و بي مسئوليت است و وقتي فقرا جايي براي تنظم خواهي از اين نابساماني ندارند؛ اوضاع از هماهنگي با تائو به در آمده است.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",53,0,0,21),
(182752,"هركه در تائو ريشه دارد، هرگز ريشه كن نمي شود. هركه تائو را در بر كشيده است، هرگز رها نخواهد شد. نام چنين كسي نسل پس از نسل با احترام برده مي شود.  اجازه دهيد تائو در زندگي تان حضور يابد و به اين ترتيب اصالت يابيد. اجازه دهيد تائو در خانواده تان حضور يابد و خانواده تان شكوفا گردد. اجازه دهيد تائو در كشورتان رخ بنمايد و كشورتان نمونه اي خواهد بود براي كشور هاي ديگر جهان. اجازه دهيد تائو در جهان حاضر باشد و جهانيان در شادي و شور به پايكوبي مي پردازند. چگونه مي دانم اين حقيقت دارد؟ با نگريستن به درون خويش.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",54,0,0,21),
(182753,"او كه در هماهنگي با تائو است همچون كودكي نوزاده است؛ استخوان هايش نرم و ماهيچه هايش ضعيف اند ولي نيروهاي حيات در او شديد و قدرتمند اند؛ مي تواند تمام طول روز فرياد بكشد و گريه كند بدون اينكه صدايش خش دار و دورگه شود؛ هماهنگي و يكپارچگي كامل در او كامل است. قدرت فرزانه چنين است. او اجازه مي دهد همه چيز بيايند و بروند؛ بدون هرگونه تلاش و آرزويي. او هرگز در انتظار نتيجه نيست پس هرگز نااميد نمي شود. او هرگز نااميد نمي شود پس روحش هميشه جوان است.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",55,0,0,21),
(182754,"آنان كه مي دانند، خاموش باقي مي مانند. آنان كه نمي دانند، سخن مي گويند. دهانت را ببند، حواست را ناديده بگير، زندگي ات را فراموش كن، گره هايت را باز كن، نگاهت را نرم و لطيف كن، و گرد و خاكت را بتكان؛ اين هويت اصلي توست. چون تائو باش.  نمي توان به تائو نزديك شد يا از آن دوري كرد، از آن سود برد يا به آن آسيبي وارد كرد، آن را گرامي داشت يا آبرويش را برد. تائو تسليم كامل است و به همين دليل ابدي است.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",56,0,0,21),
(182755,"اگر مي خواهي رهبر بزرگي باشي، بايد ياد بگيري چگونه از تائو پيروي كني. تلاش براي تسلط را متوقف كن. نقشه هاي از قبل طراحي شده را به دور بريز. جهان، خود خويش را اداره مي كند. هرچه ممنوعيت ها بيشتر باشند پاكدامني و تقوا در جامعه كمتر خواهد بود. هرچه ابزار نظامي بيشتري انبار كنيد، كمتر در امنيت خواهيد بود.  فرزانه مي گويد:  قانون را رها مي كنم و مردم با صداقت مي شوند. اقتصاد را رها مي كنم و مردم ثروتمند مي شوند. تمامي نقشه ها براي رفاه مردم را رها مي كنم و رفاه عمومي فراگير مي شود.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",57,0,0,21),
(182756,"اگر كشوري با شكيبايي اداره شود، آرامش و شرافت بر آن حكم فرما مي شود. اگر كشوري با سركوب اداره شود، اندوه و نيرنگ بر آن حكم فرما مي شوند. وقتي ميل به قدرت بر رأس امور است، هرچه ايده آل ها والاتر باشند، نتيجه اندك تر است. سعي كن مردم را خوشحال كني و به اين ترتيب اساس غم و اندوه ناپديد مي شود.  فرزانه از اين كه الگوي خدمت است راضي و خوشنود است. در تحميل خواسته هاي خود تلاش نمي كند؛ صريح و واضح است ولي نمي رنجاند؛ رك و روراست و در عين حال انعطاف پذير؛ فروزان و تابناك است ولي نورش چشم را نمي زند.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",58,0,0,21),
(182757,"براي خوب اداره كردن يك كشور چيزي سودمند تر از ميانه روي نيست. نشانه انسان ميانه رو رهايي از ايده هاي شخصي است؛ شكيبا چون آسمان، همه گير چون تابش خورشيد، استوار چون كوه، انعطاف پذير چون درختي در باد، هدفي در نظر ندارد و همه چيز را به كار مي گيرد.  زندگي، خود راهش را مي يابد. براي فرزانه ناممكن وجود ندارد چون رها و آزاد است و به فكر رفاه ديگران؛ همچون مادري كه از فرزندش مراقبت مي كند.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",59,0,0,21),
(182758,"اداره ي كشوري بزرگ چون سرخ كردن ماهي اي كوچك است؛ با دستكاري بيش از حد، به حتم كار را خراب مي كني، كشورت را در تائو متمركز كن و بدي ديگر قدرتي نخواهد داشت؛ نه اين كه از ميان برود ولي به سادگي مي تواني از راهش به در آيي. بهانه اي به بدي براي مخالفت مده و بدي خود به خود از ميان مي رود.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",60,0,0,21),
(182759,"وقتي كشوري قدرت به دست مي آورد همانند دريا مي شود؛ همه ي جريان ها به سوي او سرازير مي شود. هر چه قدرت اين كشور بيشتر شود، بيشتر نيازمند تواضع و فروتني است فروتني يعني اعتماد به تائو و از ميان رفتن نياز به دفاع از خود.  ملت بزرگ مانند مردي بزرگ است؛ وقتي اشتباهي مرتكب مي شود، متوجه آن مي شود و آن را مي پذيرد. با پذيرش اشتباه، آن را اصلاح مي كند. كساني كه نقص هايشان را به او گوشزد مي كنند، آموزگاران مشفق خويش مي داند و دشمنانش را سايه ي خويش.  اگر حكومتي در تائو متمركز شود، ملت خود را تغذيه كند و در امور ديگران دخالت نكند؛ الگويي براي ديگر كشورها در سرتاسر دنيا خواهد بود.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",61,0,0,21),
(182760,"تائو مركز هستي است. تائو گنجِ انسان نيك است و پناه انسان بد. افتخار با سخنان نيكو خريدني است، احترام با اعمال نيكو ولي تائو وراي هر ارزشي است كه هيچ كس نمي تواند آن را تصاحب كند.  هنگامي كه رهبري جديد انتخاب مي شود، با ثروت و مهارت خود به او كمك نكنيد بلكه به او تائو را آموزش دهيد.  چرا فرزانگان كهن تائو را گرامي مي داشتند؟ زيرا در يگانگي با تائو با جستجو كردن، مي يابيد و هنگامي كه اشتباهي مرتكب مي شويد، بخشوده مي شويد؛ به همين دليل همه تائو را دوست دارند.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",62,0,0,21),
(182761,"در بي عملي عمل كنيد. بدون تلاش كار كنيد. كوچك را بزرگ شماريد و كم را زياد. با سخت هنگامي كه ساده است، رودررو شويد. كارهاي بزرگ را به قطعات كوچك تقسيم كنيد و سپس آن را به انجام برسانيد.  وقتي فرزانه به مشكلي بر مي خورد، متوقف مي شود و خود را وقف مشكل مي كند. او به رفاه خود دلبسته نيست پس مشكلات براي او مشكل نيستند.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",63,0,0,21),
(182762,"آن چه ريشه دارد آسان تغذيه مي شود، آن چه به تازگي رخ داده آسان اصلاح مي شود. آن چه خشك و سخت است آسان مي شكند. آن چه كوچك است آسان گسترده مي شود. از مشكلات قبل از روي دادنشان جلوگيري كنيد. به هر چيز قبل از به وجود آمدن نظم بدهيد. درخت كاج عظيم از بذري كوچك مي رويد و سفرِ هزار فرسنگي با يك گام آغاز مي شود. با عجله در عمل شكست مي خوريد. با سعي در فهميدن مسائل را از دست مي دهيد. با زور زدن براي تكميل كاري، آن چه به تقريب كامل بود را خراب مي كنيد.  فرزانه با جازه دادن به روند طبيعي رخدادها اقدام مي كند. او در پايان به همان آرامي است كه در ابتداي كار. او هيچ ندارد پس چيزي را براي از دست دادن برايش وجود ندارد. آرزوي او رهايي از آرزوهاست. آن چه فرا مي گيرد رهايي از آرزوهاست. او به مردم يادآوري مي كند كه چه كسي بوده اند. براي او هيچ چيز به جز تائو اهميت ندارد پس مي تواند مراقب همه چيز باشد.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",64,0,0,21),
(182763,"فرزانگان كهن سعي در آموزش مردم نداشتند كه با مهرباني به آن ها ندانستن را درس مي دادند. وقتي مردم فكر مي كنند كه جواب ها را مي دانند، راهنمايي كردنشان سخت است. وقتي مي دانند كه نمي دانند، مي توانند راهشان را بيابند.  اگر مي خواهيد ياد بگيريد چگونه امور مختلف را اداره كنيد، از ثروتمند شدن يا زرنگ بودن پرهيز كنيد. ساده ترين الگو واضح ترين است. اگر به زندگي ساده و عادي راضي باشيد، مي توانيد راه را به ديگران نشان دهيد تا به طبيعت حقيقي خود باز گردند.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",65,0,0,21),
(182764,"همه ي رودها به دريا مي ريزند زيرا دريا از آن ها فروتر است. فروتني به دريا قدرت مي بخشد. اگر مي خواهيد زندگي مردم را سامان ببخشيد، فروتر از آن ها قرار بگيريد. اگر مي خواهيد مردم را رهبري كنيد، ياد بگيريد چگونه از آن ها پيروي كنيد. فرزانه بالاتر از ديگران است در حالي كه كسي احساس كوچكي نمي كند. او جلوتر از ديگران است در حالي كه كسي احساس زير دست بودن نمي كند. دنيا از او سپاسگذار است. با كسي رقابت نمي كند و كسي نيز با او به رقابت نمي پردازد.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",66,0,0,21),
(182765,"برخي مي گويند آموزش هاي من بي معني است. برخي ديگر آن را رفيع ولي غير عملي مي دانند ولي براي كساني كه به درون نگريسته اند، همين آموزش هاي بي معني، پر معني است و براي كساني كه به آن عمل مي كنند همين بلندي در عمق جانشان ريشه مي گيرد. من تنها سه چيز را آموزش مي دهم؛ سادگي، شكيبايي و شفقت. اين سه گرانبهاترين گنج ها هستند. ساده در اعمال و افكار، به منبع وجود باز مي گرديد. شكيبا با دوستان و دشمنان هردو، با همه چيز هماهنگي مي يابيد. مهربان با خود به همه ي موجوداتِ جهان در صلح و آشتي خواهيد بود.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",67,0,0,21),
(182766,"بهترين ورزشكار دوست دارد حريف مقابلش در بهترين وضعيت باشد. بهترين سردار به ذهن دشمنش رخنه مي كند. بهترين تاجر در خدمت همه ي اجتماع است. بهترين رهبر پيرو خواست مردم است. همه اين ها نشان از برتريِ رقابت نكردن دارد. نه اين كه دوست ندارند رقابت كنند كه با روحيه بازيگوشي رقابت مي كنند. درست همانند كودكان؛ در هماهنگي با تائو",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",68,0,0,21),
(182767,"در ميان سرداران مثلثي هست مي گويد:  قبل از اولين حركت، بهتر است صبر كنيد و خوب اوضاع را برسي كنيد. قبل از يك متر پيشروي بهتر است هزار متر عقب نشيني كنيد.  اين يعني پيشروي بدون جلو رفتن و عقب راندن بدون استفاده از ابزار جنگي. هيچ شكستي بدتر از دست كم گرفتن دشمنان نيست. دست كم گرفتن دشمن يعني اين كه تصور كنيم او بد است؛ به اين ترتيب سه گنج خود را ناديده مي گيريد و به دشمن خويش تبديل مي شويد. وقتي دو نيروي قوي رودروري هم قرار مي گيرند، پيروزي از آن كسي است كه مي داند چگونه تسليم شود.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",69,0,0,21),
(182768,"آموزش هاي من ساده اند. عمل كردن به آن ها راحت است؛ با وجود زيركي تان از آن ها سر در نمي آورد و اگر سعي در عمل كردن به آن ها كنيد، شكست مي خوريد. آموزش هاي من از جهان هم قديمي تر است. چگونه مي توانيد معاني آن ها را در يابيد؟ اگر مي خواهيد مرا بشناسيد، به عمق دل خود بنگريد.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",70,0,0,21),
(182769,"ندانستن، دانش راستين است. تلاش براي انباشتنِ دانش، بيماري است. ابتدا بدانيد كه بيماريد، سپس براي سلامتي كوشش كنيد. فرزانه طبيب خود است. او خود را از تمام دانسته هايش رها نموده پس در حقيقت يكپارچه و كامل است.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",71,0,0,21),
(182770,"وقتي ديگر به خود اعتماد نداري به قدرت دل مي بندي. فرزانه به عقب گام مي نهد تا مردم دچار سردرگمي نشوند. او بدون آموزش دادن، آموزش مي دهد تا براي مردم چيزي براي يادگرفتن وجود نداشته باشد.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",72,0,0,21),
(182771,"تائو هميشه در آرامش است. بدون رقابت غلبه مي كند، بدون سخن گفتن پاسخ مي دهد، بدون فراخوانده شدن حضور مي يابد و بي نقشه، تكميل مي كند. توري كه گسترده جهان را در بر دارد. با وجود شكاف هاي بزرگ اين تور چيزي نمي تواند از آن فرار كند.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",73,0,0,21),
(182772,"اگر بدانيد همه چيز در تغيير است، به هيچ چيز دل نمي بنديد. اگر از مرگ نهراسيد، چيزي نيست كه نتوانيد به دست آريدش. سعي در تسلط بر آينده مانند اين است كه بخواهيد يك شَبه، استاد نجاري شويد. وقتي ابزار نجاري را در دست داريد، ممكن است حتي دستتان را قطع كنيد.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",74,0,0,21),
(182773,"وقتي ماليات ها زيادند، مردم گرسنه مي مانند. وقتي حكومت مختلف است، مردم روحيه خود را از دست مي دهند. به سود مردم عمل كنيد. به آن ها اعتماد كنيد و تنهايشان بگذاريد.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",75,0,0,21),
(182774,"انسان نرم و لطيف زاده مي شود و به هنگام مرگ خشك و سخت مي شود. گياهان هنگامي كه سر از خاك بيرون مي آورند نرم و انعطاف پذيرند و به هنگام مرگ خشك و شكننده. پس هركه سخت و خشك است مرگش نزديك شده و هركه نرم و انعطاف پذير سرشار از زندگي است. سخت و خشك مي شكند. نرم و انعطاف پذير باقي مي ماند.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",76,0,0,21),
(182775,"عملكرد تائو در جهان همانند كشيدن كمان است؛ دو سر كمان به پايين كشيده مي شود و ميانش بالا مي آيد؛ از زياد كم مي كند و به كم مي بخشد. آنان كه سعي در تسلط دارند و براي حفظ قدرت از زور استفاده مي كنند و خلاف جهت تائو حركت مي كنند، از كساني كه چيزي ندارند مي گيرند و به كساني كه بيش از اندازه دارند، مي دهند. فرزانه بي وقفه مي بخشد زيرا آن چه دارد بي پايان است. او بدون انتظار عمل مي كند و هرگز تصور نمي كند بهتر از كسي است.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",77,0,0,21),
(182776,"هيچ چيز در اين جهان چون آب، نرم و انعطاف پذير نيست. با اين حال براي حل كردن آن چه سخت است چيز ديگري ياراي مقابله با آب را ندارد. نرمي بر سختي غلبه مي كند و لطافت بر خشونت. همه اين را مي دانند ولي كمتر كسي به آن عمل مي كند.  فرزانه هنگام غم، آرام باقي مي ماند. بدي به دل او راهي ندارد. چون كمك كردن را ترك كرده بزرگترين كمك مردم است. كلام حقيقت متناقض به نظر مي رسد.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",78,0,0,21),
(182777,"شكست يك فرصت است. اگر ديگري را مقصر بداني، پاياني براي مقصر دانستن ديگران وجود نخواهد داشت. فرزانه به وظايفش عمل مي كند و اشتباهاتش را اصلاح مي نمايد. او آن چه ضروري است را به انجام مي رساند و از ديگران چيزي طلب نمي كند.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",79,0,0,21),
(182778,"اگر كشوري خردمندانه اداره شود، ساكنانش خوشنود خواهند بود. از دسترنج شان لذت مي برند و از آن جايي كه خانه هايشان را دوست دارند علاقه به سفر را از دست مي دهند. ممكن است در اين كشور چند وسيله ي نقليه و قايق وجود داشته باشد ولي به جايي نمي رود. ممكن است انبارهاي ادوات جنگي وجود داشته باشند ولي كسي از آن ها استفاده نمي كند. مردم از خوراكشان لذت مي برند و از زندگي با خانواده هايشان شادند. تعطيلات را در باغچه هايشان سپري مي كنند و از كمك به همسايگان خوشنود مي شوند. حتي اگر كشور همسايه به قدري به آن ها نزديك باشد كه صداي خروس ها يا پارس سگ هايشان را به سادگي بشنوند، از اين كه بدون سفر كردن به كشور همسايه در سن پيري بميرند، راضي اند.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",80,0,0,21),
(182779,"كلام حقيقت عاري از آراستگي و كلام آراسته از حقيقت خالي است. خردمندان نيازي نمي بينند تا منظورشان را اثبات كنند؛ آنان كه سعي در اثبات نظرات شان دارند از خرد بويي نبرده اند.  فرزانه دارايي اي ندارد. هرچه بيشتر به ديگران ياري مي رساند، شادتر مي شود. هرچه بيشتر به ديگران مي بخشد، غني تر مي شود.  تائو بي اصرار و پافشاري، قوت مي بخشد. فرزانه با رها كردن ميل به تسلط، رهبري مي كند.",249,19,1,"2024-10-18 00:03:58","2024-10-18 00:03:58",81,0,0,21);